     1→/**
     2→ * Facial Analysis Scoring Functions
     3→ * Uses correct landmark IDs from landmarks.ts
     4→ * Includes FaceIQ-style bell curve scoring and percentile ranking
     5→ */
     6→
     7→import {
     8→  LandmarkPoint,
     9→  ScoringConfig,
    10→  FACEIQ_SCORING_CONFIGS,
    11→  FACEIQ_IDEAL_VALUES,
    12→  PopulationStats,
    13→  POPULATION_STATS,
    14→} from './landmarks';
    15→
    16→// ============================================
    17→// UTILITY TYPES & FUNCTIONS
    18→// ============================================
    19→
    20→export interface Point {
    21→  x: number;
    22→  y: number;
    23→}
    24→
    25→export interface ScoreResult {
    26→  value: number;
    27→  score: number;
    28→  idealRange: { min: number; max: number };
    29→  rating: 'excellent' | 'good' | 'average' | 'below_average';
    30→}
    31→
    32→/**
    33→ * Calculate distance between two points
    34→ */
    35→export function distance(p1: Point, p2: Point): number {
    36→  return Math.sqrt(Math.pow(p2.x - p1.x, 2) + Math.pow(p2.y - p1.y, 2));
    37→}
    38→
    39→/**
    40→ * Calculate angle at vertex point (in degrees)
    41→ * Angle is formed by points: p1 -> vertex -> p2
    42→ */
    43→export function calculateAngle(p1: Point, vertex: Point, p2: Point): number {
    44→  const v1 = { x: p1.x - vertex.x, y: p1.y - vertex.y };
    45→  const v2 = { x: p2.x - vertex.x, y: p2.y - vertex.y };
    46→
    47→  const dot = v1.x * v2.x + v1.y * v2.y;
    48→  const cross = v1.x * v2.y - v1.y * v2.x;
    49→
    50→  const angle = Math.atan2(cross, dot) * (180 / Math.PI);
    51→  return Math.abs(angle);
    52→}
    53→
    54→/**
    55→ * Calculate perpendicular distance from point to line
    56→ * Positive = in front of line, Negative = behind line
    57→ */
    58→export function perpendicularDistance(
    59→  point: Point,
    60→  lineStart: Point,
    61→  lineEnd: Point
    62→): number {
    63→  const A = point.x - lineStart.x;
    64→  const B = point.y - lineStart.y;
    65→  const C = lineEnd.x - lineStart.x;
    66→  const D = lineEnd.y - lineStart.y;
    67→
    68→  const dot = A * C + B * D;
    69→  const lenSq = C * C + D * D;
    70→  const param = lenSq !== 0 ? dot / lenSq : -1;
    71→
    72→  let xx: number, yy: number;
    73→
    74→  if (param < 0) {
    75→    xx = lineStart.x;
    76→    yy = lineStart.y;
    77→  } else if (param > 1) {
    78→    xx = lineEnd.x;
    79→    yy = lineEnd.y;
    80→  } else {
    81→    xx = lineStart.x + param * C;
    82→    yy = lineStart.y + param * D;
    83→  }
    84→
    85→  const dx = point.x - xx;
    86→  const dy = point.y - yy;
    87→
    88→  // Determine sign (positive = in front of line, negative = behind)
    89→  const sign =
    90→    (lineEnd.y - lineStart.y) * point.x -
    91→      (lineEnd.x - lineStart.x) * point.y +
    92→      lineEnd.x * lineStart.y -
    93→      lineEnd.y * lineStart.x >=
    94→    0
    95→      ? 1
    96→      : -1;
    97→
    98→  return sign * Math.sqrt(dx * dx + dy * dy);
    99→}
   100→
   101→/**
   102→ * Get rating from score
   103→ */
   104→function getRating(score: number): ScoreResult['rating'] {
   105→  if (score >= 90) return 'excellent';
   106→  if (score >= 75) return 'good';
   107→  if (score >= 50) return 'average';
   108→  return 'below_average';
   109→}
   110→
   111→/**
   112→ * Helper to get landmark by ID from array
   113→ */
   114→function getLandmark(landmarks: LandmarkPoint[], id: string): Point | null {
   115→  const lm = landmarks.find((l) => l.id === id);
   116→  return lm ? { x: lm.x, y: lm.y } : null;
   117→}
   118→
   119→// ============================================
   120→// SIDE PROFILE SCORING (uses _side suffix landmarks)
   121→// ============================================
   122→
   123→/**
   124→ * Gonial Angle - Jaw angle measurement
   125→ * Uses: tragus, gonionBottom, menton
   126→ * Ideal: 120° - 130° (male), 125° - 135° (female)
   127→ */
   128→export function calculateGonialAngle(
   129→  sideLandmarks: LandmarkPoint[],
   130→  gender: 'male' | 'female' = 'male'
   131→): ScoreResult | null {
   132→  const tragion = getLandmark(sideLandmarks, 'tragus');
   133→  const gonion_inferior = getLandmark(sideLandmarks, 'gonionBottom');
   134→  const menton = getLandmark(sideLandmarks, 'menton');
   135→
   136→  if (!tragion || !gonion_inferior || !menton) return null;
   137→
   138→  const angle = calculateAngle(tragion, gonion_inferior, menton);
   139→
   140→  const idealMin = gender === 'male' ? 120 : 125;
   141→  const idealMax = gender === 'male' ? 130 : 135;
   142→
   143→  let score: number;
   144→  if (angle >= idealMin && angle <= idealMax) {
   145→    score = 100;
   146→  } else {
   147→    const deviation = angle < idealMin ? idealMin - angle : angle - idealMax;
   148→    score = Math.max(0, 100 - deviation * 5);
   149→  }
   150→
   151→  return {
   152→    value: angle,
   153→    score,
   154→    idealRange: { min: idealMin, max: idealMax },
   155→    rating: getRating(score),
   156→  };
   157→}
   158→
   159→/**
   160→ * Nasolabial Angle - Angle between columella and upper lip
   161→ * Uses: columella, subnasale, labraleSuperius
   162→ * Ideal: 90° - 105° (male), 95° - 115° (female)
   163→ */
   164→export function calculateNasolabialAngle(
   165→  sideLandmarks: LandmarkPoint[],
   166→  gender: 'male' | 'female' = 'male'
   167→): ScoreResult | null {
   168→  const columella = getLandmark(sideLandmarks, 'columella');
   169→  const subnasale = getLandmark(sideLandmarks, 'subnasale');
   170→  const labrale_superius = getLandmark(sideLandmarks, 'labraleSuperius');
   171→
   172→  if (!columella || !subnasale || !labrale_superius) return null;
   173→
   174→  const angle = calculateAngle(columella, subnasale, labrale_superius);
   175→
   176→  const idealMin = gender === 'male' ? 90 : 95;
   177→  const idealMax = gender === 'male' ? 105 : 115;
   178→
   179→  let score: number;
   180→  if (angle >= idealMin && angle <= idealMax) {
   181→    score = 100;
   182→  } else {
   183→    const deviation = angle < idealMin ? idealMin - angle : angle - idealMax;
   184→    score = Math.max(0, 100 - deviation * 4);
   185→  }
   186→
   187→  return {
   188→    value: angle,
   189→    score,
   190→    idealRange: { min: idealMin, max: idealMax },
   191→    rating: getRating(score),
   192→  };
   193→}
   194→
   195→/**
   196→ * E-Line (Ricketts' Esthetic Line) - Lip protrusion analysis
   197→ * Uses: pronasale, pogonion, labraleSuperius, labraleInferius
   198→ * Ideal: Upper lip 4mm behind (male) / 2mm behind (female)
   199→ *        Lower lip 2mm behind (male) / 0mm (female)
   200→ */
   201→export function calculateELine(
   202→  sideLandmarks: LandmarkPoint[],
   203→  gender: 'male' | 'female' = 'male'
   204→): { upperLip: ScoreResult; lowerLip: ScoreResult; combined: ScoreResult } | null {
   205→  const pronasale = getLandmark(sideLandmarks, 'pronasale');
   206→  const pogonion = getLandmark(sideLandmarks, 'pogonion');
   207→  const labrale_superius = getLandmark(sideLandmarks, 'labraleSuperius');
   208→  const labrale_inferius = getLandmark(sideLandmarks, 'labraleInferius');
   209→
   210→  if (!pronasale || !pogonion || !labrale_superius || !labrale_inferius) return null;
   211→
   212→  const upperLipDistance = perpendicularDistance(labrale_superius, pronasale, pogonion);
   213→  const lowerLipDistance = perpendicularDistance(labrale_inferius, pronasale, pogonion);
   214→
   215→  // Ideal values (negative = behind the line, which is desirable)
   216→  const idealUpperLip = gender === 'male' ? -4 : -2;
   217→  const idealLowerLip = gender === 'male' ? -2 : 0;
   218→  const tolerance = 2;
   219→
   220→  // Upper lip scoring
   221→  const upperDeviation = Math.abs(upperLipDistance - idealUpperLip);
   222→  const upperScore =
   223→    upperDeviation <= tolerance
   224→      ? 100
   225→      : Math.max(0, 100 - (upperDeviation - tolerance) * 10);
   226→
   227→  // Lower lip scoring
   228→  const lowerDeviation = Math.abs(lowerLipDistance - idealLowerLip);
   229→  const lowerScore =
   230→    lowerDeviation <= tolerance
   231→      ? 100
   232→      : Math.max(0, 100 - (lowerDeviation - tolerance) * 10);
   233→
   234→  const combinedScore = (upperScore + lowerScore) / 2;
   235→
   236→  return {
   237→    upperLip: {
   238→      value: upperLipDistance,
   239→      score: upperScore,
   240→      idealRange: { min: idealUpperLip - tolerance, max: idealUpperLip + tolerance },
   241→      rating: getRating(upperScore),
   242→    },
   243→    lowerLip: {
   244→      value: lowerLipDistance,
   245→      score: lowerScore,
   246→      idealRange: { min: idealLowerLip - tolerance, max: idealLowerLip + tolerance },
   247→      rating: getRating(lowerScore),
   248→    },
   249→    combined: {
   250→      value: (upperLipDistance + lowerLipDistance) / 2,
   251→      score: combinedScore,
   252→      idealRange: { min: -6, max: 2 },
   253→      rating: getRating(combinedScore),
   254→    },
   255→  };
   256→}
   257→
   258→/**
   259→ * Mentolabial Angle - Angle at the labiomental fold
   260→ * Uses: labraleInferius, sublabiale, pogonion
   261→ * Ideal: 120° - 140°
   262→ */
   263→export function calculateMentolabialAngle(
   264→  sideLandmarks: LandmarkPoint[]
   265→): ScoreResult | null {
   266→  const labrale_inferius = getLandmark(sideLandmarks, 'labraleInferius');
   267→  const sublabiale = getLandmark(sideLandmarks, 'sublabiale');
   268→  const pogonion = getLandmark(sideLandmarks, 'pogonion');
   269→
   270→  if (!labrale_inferius || !sublabiale || !pogonion) return null;
   271→
   272→  const angle = calculateAngle(labrale_inferius, sublabiale, pogonion);
   273→
   274→  const idealMin = 120;
   275→  const idealMax = 140;
   276→
   277→  let score: number;
   278→  if (angle >= idealMin && angle <= idealMax) {
   279→    score = 100;
   280→  } else {
   281→    const deviation = angle < idealMin ? idealMin - angle : angle - idealMax;
   282→    score = Math.max(0, 100 - deviation * 3);
   283→  }
   284→
   285→  return {
   286→    value: angle,
   287→    score,
   288→    idealRange: { min: idealMin, max: idealMax },
   289→    rating: getRating(score),
   290→  };
   291→}
   292→
   293→/**
   294→ * Nasofrontal Angle - Angle at the bridge of nose
   295→ * Uses: glabella, nasion, rhinion
   296→ * Ideal: 115° - 135°
   297→ */
   298→export function calculateNasofrontalAngle(
   299→  sideLandmarks: LandmarkPoint[]
   300→): ScoreResult | null {
   301→  const glabella = getLandmark(sideLandmarks, 'glabella');
   302→  const nasion = getLandmark(sideLandmarks, 'nasion');
   303→  const rhinion = getLandmark(sideLandmarks, 'rhinion');
   304→
   305→  if (!glabella || !nasion || !rhinion) return null;
   306→
   307→  const angle = calculateAngle(glabella, nasion, rhinion);
   308→
   309→  const idealMin = 115;
   310→  const idealMax = 135;
   311→
   312→  let score: number;
   313→  if (angle >= idealMin && angle <= idealMax) {
   314→    score = 100;
   315→  } else {
   316→    const deviation = angle < idealMin ? idealMin - angle : angle - idealMax;
   317→    score = Math.max(0, 100 - deviation * 3);
   318→  }
   319→
   320→  return {
   321→    value: angle,
   322→    score,
   323→    idealRange: { min: idealMin, max: idealMax },
   324→    rating: getRating(score),
   325→  };
   326→}
   327→
   328→// ============================================
   329→// FRONT PROFILE SCORING (uses front landmarks)
   330→// ============================================
   331→
   332→/**
   333→ * Facial Thirds - Vertical face proportions
   334→ * Uses: trichion, nasal_base, subnasale, menton
   335→ * Ideal: Each third ~33%
   336→ *
   337→ * Upper third: hairline (trichion) to nasal_base
   338→ * Middle third: nasal_base to subnasale (base of nose)
   339→ * Lower third: subnasale to menton (chin)
   340→ */
   341→export function calculateFacialThirds(
   342→  frontLandmarks: LandmarkPoint[]
   343→): { upper: ScoreResult; middle: ScoreResult; lower: ScoreResult; overall: ScoreResult } | null {
   344→  const trichion = getLandmark(frontLandmarks, 'trichion');
   345→  const nasal_base = getLandmark(frontLandmarks, 'nasal_base');
   346→  const subnasale = getLandmark(frontLandmarks, 'subnasale');
   347→  const menton = getLandmark(frontLandmarks, 'menton');
   348→
   349→  if (!trichion || !nasal_base || !subnasale || !menton) return null;
   350→
   351→  const totalHeight = distance(trichion, menton);
   352→  if (totalHeight === 0) return null;
   353→
   354→  const upperThird = (distance(trichion, nasal_base) / totalHeight) * 100;
   355→  const middleThird = (distance(nasal_base, subnasale) / totalHeight) * 100;
   356→  const lowerThird = (distance(subnasale, menton) / totalHeight) * 100;
   357→
   358→  const ideal = 33.33;
   359→  const tolerance = 3;
   360→
   361→  const scoreThird = (value: number): number => {
   362→    const deviation = Math.abs(value - ideal);
   363→    return deviation <= tolerance ? 100 : Math.max(0, 100 - (deviation - tolerance) * 8);
   364→  };
   365→
   366→  const upperScore = scoreThird(upperThird);
   367→  const middleScore = scoreThird(middleThird);
   368→  const lowerScore = scoreThird(lowerThird);
   369→  const overallScore = (upperScore + middleScore + lowerScore) / 3;
   370→
   371→  return {
   372→    upper: {
   373→      value: upperThird,
   374→      score: upperScore,
   375→      idealRange: { min: 30, max: 36 },
   376→      rating: getRating(upperScore),
   377→    },
   378→    middle: {
   379→      value: middleThird,
   380→      score: middleScore,
   381→      idealRange: { min: 30, max: 36 },
   382→      rating: getRating(middleScore),
   383→    },
   384→    lower: {
   385→      value: lowerThird,
   386→      score: lowerScore,
   387→      idealRange: { min: 30, max: 36 },
   388→      rating: getRating(lowerScore),
   389→    },
   390→    overall: {
   391→      value: Math.max(Math.abs(upperThird - ideal), Math.abs(middleThird - ideal), Math.abs(lowerThird - ideal)),
   392→      score: overallScore,
   393→      idealRange: { min: 0, max: 3 },
   394→      rating: getRating(overallScore),
   395→    },
   396→  };
   397→}
   398→
   399→/**
   400→ * Facial Width-to-Height Ratio (FWHR)
   401→ * Uses: left_zygion, right_zygion, nasal_base (as upper bound), labrale_superius
   402→ * Ideal: 1.8 - 2.0
   403→ *
   404→ * NOTE: This is the standard FWHR = bizygomatic_width / upper_face_height
   405→ * (Carré & McCormick, 2008). This differs from "Total Facial Width to Height Ratio"
   406→ * in some systems which uses total_height / cheek_width (inverted, ideal 1.33-1.38).
   407→ * Our implementation follows the widely-used FWHR convention.
   408→ */
   409→export function calculateFWHR(frontLandmarks: LandmarkPoint[]): ScoreResult | null {
   410→  const left_zygion = getLandmark(frontLandmarks, 'left_zygion');
   411→  const right_zygion = getLandmark(frontLandmarks, 'right_zygion');
   412→  const nasal_base = getLandmark(frontLandmarks, 'nasal_base');
   413→  const labrale_superius = getLandmark(frontLandmarks, 'labrale_superius');
   414→
   415→  if (!left_zygion || !right_zygion || !nasal_base || !labrale_superius) return null;
   416→
   417→  const bizygomatic_width = distance(left_zygion, right_zygion);
   418→  const upper_face_height = distance(nasal_base, labrale_superius);
   419→
   420→  if (upper_face_height === 0) return null;
   421→
   422→  const ratio = bizygomatic_width / upper_face_height;
   423→
   424→  const idealMin = 1.8;
   425→  const idealMax = 2.0;
   426→
   427→  let score: number;
   428→  if (ratio >= idealMin && ratio <= idealMax) {
   429→    score = 100;
   430→  } else {
   431→    const deviation = ratio < idealMin ? idealMin - ratio : ratio - idealMax;
   432→    score = Math.max(0, 100 - deviation * 50);
   433→  }
   434→
   435→  return {
   436→    value: ratio,
   437→    score,
   438→    idealRange: { min: idealMin, max: idealMax },
   439→    rating: getRating(score),
   440→  };
   441→}
   442→
   443→/**
   444→ * Interpupillary Distance Ratio
   445→ * Uses: left_pupila, right_pupila, left_zygion, right_zygion
   446→ * Ideal: IPD ~46% of bizygomatic width
   447→ */
   448→export function calculateIPDRatio(frontLandmarks: LandmarkPoint[]): ScoreResult | null {
   449→  const left_pupila = getLandmark(frontLandmarks, 'left_pupila');
   450→  const right_pupila = getLandmark(frontLandmarks, 'right_pupila');
   451→  const left_zygion = getLandmark(frontLandmarks, 'left_zygion');
   452→  const right_zygion = getLandmark(frontLandmarks, 'right_zygion');
   453→
   454→  if (!left_pupila || !right_pupila || !left_zygion || !right_zygion) return null;
   455→
   456→  const ipd = distance(left_pupila, right_pupila);
   457→  const bizygomatic_width = distance(left_zygion, right_zygion);
   458→
   459→  if (bizygomatic_width === 0) return null;
   460→
   461→  const ratio = (ipd / bizygomatic_width) * 100;
   462→
   463→  const ideal = 46;
   464→  const tolerance = 2;
   465→
   466→  const deviation = Math.abs(ratio - ideal);
   467→  const score = deviation <= tolerance ? 100 : Math.max(0, 100 - (deviation - tolerance) * 8);
   468→
   469→  return {
   470→    value: ratio,
   471→    score,
   472→    idealRange: { min: 44, max: 48 },
   473→    rating: getRating(score),
   474→  };
   475→}
   476→
   477→/**
   478→ * Nasal Index (Nose Width to Height)
   479→ * Uses: left_ala_nasi, right_ala_nasi, nasal_base, subnasale
   480→ * Classification varies by ethnicity
   481→ */
   482→export function calculateNasalIndex(frontLandmarks: LandmarkPoint[]): ScoreResult | null {
   483→  const left_ala_nasi = getLandmark(frontLandmarks, 'left_ala_nasi');
   484→  const right_ala_nasi = getLandmark(frontLandmarks, 'right_ala_nasi');
   485→  const nasal_base = getLandmark(frontLandmarks, 'nasal_base');
   486→  const subnasale = getLandmark(frontLandmarks, 'subnasale');
   487→
   488→  if (!left_ala_nasi || !right_ala_nasi || !nasal_base || !subnasale) return null;
   489→
   490→  const nasal_width = distance(left_ala_nasi, right_ala_nasi);
   491→  const nasal_height = distance(nasal_base, subnasale);
   492→
   493→  if (nasal_height === 0) return null;
   494→
   495→  const index = (nasal_width / nasal_height) * 100;
   496→
   497→  // Using mesorrhine (medium) as ideal: 70-85
   498→  const idealMin = 70;
   499→  const idealMax = 85;
   500→
   501→  let score: number;
   502→  if (index >= idealMin && index <= idealMax) {
   503→    score = 100;
   504→  } else {
   505→    const deviation = index < idealMin ? idealMin - index : index - idealMax;
   506→    score = Math.max(0, 100 - deviation * 3);
   507→  }
   508→
   509→  return {
   510→    value: index,
   511→    score,
   512→    idealRange: { min: idealMin, max: idealMax },
   513→    rating: getRating(score),
   514→  };
   515→}
   516→
   517→/**
   518→ * Eye Canthal Tilt
   519→ * Uses: left_canthus_medialis, left_canthus_lateralis (or right)
   520→ * Ideal: 4° - 8° positive tilt
   521→ */
   522→export function calculateCanthalTilt(
   523→  frontLandmarks: LandmarkPoint[],
   524→  side: 'left' | 'right' = 'left'
   525→): ScoreResult | null {
   526→  const medialId = side === 'left' ? 'left_canthus_medialis' : 'right_canthus_medialis';
   527→  const lateralId = side === 'left' ? 'left_canthus_lateralis' : 'right_canthus_lateralis';
   528→
   529→  const canthus_medialis = getLandmark(frontLandmarks, medialId);
   530→  const canthus_lateralis = getLandmark(frontLandmarks, lateralId);
   531→
   532→  if (!canthus_medialis || !canthus_lateralis) return null;
   533→
   534→  // Y is inverted in screen coordinates
   535→  const deltaY = canthus_medialis.y - canthus_lateralis.y;
   536→  const deltaX = canthus_lateralis.x - canthus_medialis.x;
   537→
   538→  const angle = Math.atan2(deltaY, deltaX) * (180 / Math.PI);
   539→
   540→  const idealMin = 4;
   541→  const idealMax = 8;
   542→
   543→  let score: number;
   544→  if (angle >= idealMin && angle <= idealMax) {
   545→    score = 100;
   546→  } else if (angle < 0) {
   547→    // Negative tilt is worse
   548→    score = Math.max(0, 50 + angle * 5);
   549→  } else {
   550→    const deviation = angle < idealMin ? idealMin - angle : angle - idealMax;
   551→    score = Math.max(0, 100 - deviation * 10);
   552→  }
   553→
   554→  return {
   555→    value: angle,
   556→    score,
   557→    idealRange: { min: idealMin, max: idealMax },
   558→    rating: getRating(score),
   559→  };
   560→}
   561→
   562→/**
   563→ * Mouth Width to Nose Width Ratio
   564→ * Uses: left_cheilion, right_cheilion, left_ala_nasi, right_ala_nasi
   565→ * Ideal: 1.5 - 1.6
   566→ */
   567→export function calculateMouthNoseRatio(frontLandmarks: LandmarkPoint[]): ScoreResult | null {
   568→  const left_cheilion = getLandmark(frontLandmarks, 'left_cheilion');
   569→  const right_cheilion = getLandmark(frontLandmarks, 'right_cheilion');
   570→  const left_ala_nasi = getLandmark(frontLandmarks, 'left_ala_nasi');
   571→  const right_ala_nasi = getLandmark(frontLandmarks, 'right_ala_nasi');
   572→
   573→  if (!left_cheilion || !right_cheilion || !left_ala_nasi || !right_ala_nasi) return null;
   574→
   575→  const mouth_width = distance(left_cheilion, right_cheilion);
   576→  const nose_width = distance(left_ala_nasi, right_ala_nasi);
   577→
   578→  if (nose_width === 0) return null;
   579→
   580→  const ratio = mouth_width / nose_width;
   581→
   582→  const idealMin = 1.5;
   583→  const idealMax = 1.6;
   584→
   585→  let score: number;
   586→  if (ratio >= idealMin && ratio <= idealMax) {
   587→    score = 100;
   588→  } else {
   589→    const deviation = ratio < idealMin ? idealMin - ratio : ratio - idealMax;
   590→    score = Math.max(0, 100 - deviation * 30);
   591→  }
   592→
   593→  return {
   594→    value: ratio,
   595→    score,
   596→    idealRange: { min: idealMin, max: idealMax },
   597→    rating: getRating(score),
   598→  };
   599→}
   600→
   601→/**
   602→ * Jaw Width to Face Width Ratio (Bigonial to Bizygomatic)
   603→ * Uses: left_gonion_inferior, right_gonion_inferior, left_zygion, right_zygion
   604→ * Ideal: 0.75 - 0.80 (male), 0.70 - 0.75 (female)
   605→ */
   606→export function calculateJawRatio(
   607→  frontLandmarks: LandmarkPoint[],
   608→  gender: 'male' | 'female' = 'male'
   609→): ScoreResult | null {
   610→  const left_gonion = getLandmark(frontLandmarks, 'left_gonion_inferior');
   611→  const right_gonion = getLandmark(frontLandmarks, 'right_gonion_inferior');
   612→  const left_zygion = getLandmark(frontLandmarks, 'left_zygion');
   613→  const right_zygion = getLandmark(frontLandmarks, 'right_zygion');
   614→
   615→  if (!left_gonion || !right_gonion || !left_zygion || !right_zygion) return null;
   616→
   617→  const bigonial_width = distance(left_gonion, right_gonion);
   618→  const bizygomatic_width = distance(left_zygion, right_zygion);
   619→
   620→  if (bizygomatic_width === 0) return null;
   621→
   622→  const ratio = bigonial_width / bizygomatic_width;
   623→
   624→  const idealMin = gender === 'male' ? 0.75 : 0.7;
   625→  const idealMax = gender === 'male' ? 0.8 : 0.75;
   626→
   627→  let score: number;
   628→  if (ratio >= idealMin && ratio <= idealMax) {
   629→    score = 100;
   630→  } else {
   631→    const deviation = ratio < idealMin ? idealMin - ratio : ratio - idealMax;
   632→    score = Math.max(0, 100 - deviation * 100);
   633→  }
   634→
   635→  return {
   636→    value: ratio,
   637→    score,
   638→    idealRange: { min: idealMin, max: idealMax },
   639→    rating: getRating(score),
   640→  };
   641→}
