   743→// ============================================
   744→// FACEIQ-STYLE BELL CURVE SCORING
   745→// ============================================
   746→
   747→/**
   748→ * Bell curve scoring function
   749→ * score = e^(-0.5 × ((value-ideal)/stdDev)²) × 100
   750→ */
   751→export function calculateBellCurveScore(
   752→  value: number,
   753→  config: ScoringConfig
   754→): number {
   755→  const z = (value - config.idealValue) / config.standardDeviation;
   756→  const bellCurve = Math.exp(-0.5 * z * z);
   757→  return config.minScore + (config.maxScore - config.minScore) * bellCurve;
   758→}
   759→
   760→/**
   761→ * Get bell curve score with gender-specific ideal values
   762→ */
   763→export function calculateGenderAwareBellCurveScore(
   764→  value: number,
   765→  measurementKey: string,
   766→  gender: 'male' | 'female'
   767→): number {
   768→  const idealValues = FACEIQ_IDEAL_VALUES[measurementKey];
   769→  const config = FACEIQ_SCORING_CONFIGS[measurementKey];
   770→
   771→  if (!idealValues || !config) {
   772→    return 0;
   773→  }
   774→
   775→  const genderIdeal = idealValues[gender].ideal;
   776→  const adjustedConfig = {
   777→    ...config,
   778→    idealValue: genderIdeal,
   779→  };
   780→
   781→  return calculateBellCurveScore(value, adjustedConfig);
   782→}
   783→
   784→/**
   785→ * Calculate overall harmony score (weighted average)
   786→ */
   787→export function calculateHarmonyScore(
   788→  scores: Record<string, number>
   789→): number {
   790→  let totalWeight = 0;
   791→  let weightedSum = 0;
   792→
   793→  for (const [key, score] of Object.entries(scores)) {
   794→    const config = FACEIQ_SCORING_CONFIGS[key];
   795→    if (config) {
   796→      weightedSum += score * config.weight;
   797→      totalWeight += config.weight;
   798→    }
   799→  }
   800→
   801→  return totalWeight > 0 ? weightedSum / totalWeight : 0;
   802→}
   803→
   804→// ============================================
   805→// PERCENTILE RANKING (Population Comparison)
   806→// ============================================
   807→
   808→/**
   809→ * Standard normal CDF approximation (Abramowitz and Stegun formula)
   810→ */
   811→export function normalCDF(z: number): number {
   812→  const a1 = 0.254829592;
   813→  const a2 = -0.284496736;
   814→  const a3 = 1.421413741;
   815→  const a4 = -1.453152027;
   816→  const a5 = 1.061405429;
   817→  const p = 0.3275911;
   818→
   819→  const sign = z < 0 ? -1 : 1;
   820→  const absZ = Math.abs(z) / Math.sqrt(2);
   821→
   822→  const t = 1.0 / (1.0 + p * absZ);
   823→  const y =
   824→    1.0 -
   825→    ((((a5 * t + a4) * t + a3) * t + a2) * t + a1) * t * Math.exp(-absZ * absZ);
   826→
   827→  return 0.5 * (1.0 + sign * y);
   828→}
   829→
   830→/**
   831→ * Calculate percentile (where you fall on the bell curve)
   832→ */
   833→export function calculatePercentile(
   834→  value: number,
   835→  stats: PopulationStats
   836→): number {
   837→  const z = (value - stats.mean) / stats.standardDeviation;
   838→  return normalCDF(z) * 100;
   839→}
   840→
   841→/**
   842→ * Calculate percentile for a specific measurement
   843→ */
   844→export function calculateMeasurementPercentile(
   845→  value: number,
   846→  measurementKey: string
   847→): number {
   848→  const stats = POPULATION_STATS[measurementKey];
   849→  if (!stats) {
   850→    return 50; // Default to median if no stats available
   851→  }
   852→  return calculatePercentile(value, stats);
   853→}
