   855→// ============================================
   856→// BELL CURVE DATA FOR VISUALIZATION
   857→// ============================================
   858→
   859→export interface BellCurvePoint {
   860→  x: number;
   861→  y: number;
   862→}
   863→
   864→export interface BellCurveData {
   865→  points: BellCurvePoint[];
   866→  userValue: number;
   867→  userPercentile: number;
   868→  mean: number;
   869→  standardDeviation: number;
   870→  idealValue?: number;
   871→}
   872→
   873→/**
   874→ * Generate bell curve data for visualization
   875→ */
   876→export function generateBellCurveData(
   877→  stats: PopulationStats,
   878→  userValue: number,
   879→  idealValue?: number
   880→): BellCurveData {
   881→  const points: BellCurvePoint[] = [];
   882→
   883→  // Generate points from -4 to +4 standard deviations
   884→  for (let i = -4; i <= 4; i += 0.1) {
   885→    const x = stats.mean + i * stats.standardDeviation;
   886→    const y =
   887→      (1 / (stats.standardDeviation * Math.sqrt(2 * Math.PI))) *
   888→      Math.exp(
   889→        -0.5 * Math.pow((x - stats.mean) / stats.standardDeviation, 2)
   890→      );
   891→    points.push({ x, y });
   892→  }
   893→
   894→  return {
   895→    points,
   896→    userValue,
   897→    userPercentile: calculatePercentile(userValue, stats),
   898→    mean: stats.mean,
   899→    standardDeviation: stats.standardDeviation,
   900→    idealValue,
   901→  };
   902→}
   903→
   904→/**
   905→ * Generate bell curve data for a specific measurement
   906→ */
   907→export function generateMeasurementBellCurve(
   908→  measurementKey: string,
   909→  userValue: number,
   910→  gender?: 'male' | 'female'
   911→): BellCurveData | null {
   912→  const stats = POPULATION_STATS[measurementKey];
   913→  if (!stats) {
   914→    return null;
   915→  }
   916→
   917→  const idealValues = FACEIQ_IDEAL_VALUES[measurementKey];
   918→  const idealValue = idealValues && gender ? idealValues[gender].ideal : undefined;
   919→
   920→  return generateBellCurveData(stats, userValue, idealValue);
   921→}
