   960→      ? { min: idealValues[gender].range[0], max: idealValues[gender].range[1] }
   961→      : { min: config.idealValue - config.standardDeviation, max: config.idealValue + config.standardDeviation },
   962→    rating: getRating(bellCurveScore),
   963→    bellCurveScore,
   964→    percentile,
   965→    idealValue,
   966→    deviation,
   967→  };
   968→}
