     2→
     3→import { useState } from 'react';
     4→import Link from 'next/link';
     5→import { Reply, Pencil, Trash2 } from 'lucide-react';
     6→import { Comment, VoteType } from '@/types/forum';
     7→import { formatDistanceToNow } from '@/lib/utils';
     8→import { VoteButtons } from './VoteButtons';
     9→import { KarmaBadge } from './KarmaBadge';
    10→import { MentionInput } from './MentionInput';
    11→import { MentionLink } from './MentionLink';
    12→
    13→interface CommentThreadProps {
    14→  comments: Comment[];
    15→  onVote: (commentId: string, voteType: VoteType) => void;
    16→  onReply: (commentId: string, content: string) => void;
    17→  onEdit?: (commentId: string, content: string) => void;
    18→  onDelete?: (commentId: string) => void;
    19→  currentUserId?: string;
    20→}
    21→
    22→export function CommentThread({
    23→  comments,
    24→  onVote,
    25→  onReply,
    26→  onEdit,
    27→  onDelete,
    28→  currentUserId,
    29→}: CommentThreadProps) {
    30→  return (
    31→    <div className="space-y-6">
    32→      {comments.map((comment) => (
    33→        <CommentItem
    34→          key={comment.id}
    35→          comment={comment}
    36→          onVote={onVote}
    37→          onReply={onReply}
    38→          onEdit={onEdit}
    39→          onDelete={onDelete}
    40→          currentUserId={currentUserId}
    41→        />
    42→      ))}
    43→    </div>
    44→  );
    45→}
    46→
    47→interface CommentItemProps {
    48→  comment: Comment;
    49→  onVote: (commentId: string, voteType: VoteType) => void;
    50→  onReply: (commentId: string, content: string) => void;
    51→  onEdit?: (commentId: string, content: string) => void;
    52→  onDelete?: (commentId: string) => void;
    53→  currentUserId?: string;
    54→}
    55→
    56→function CommentItem({
    57→  comment,
    58→  onVote,
    59→  onReply,
    60→  onEdit,
    61→  onDelete,
    62→  currentUserId,
    63→}: CommentItemProps) {
    64→  const [isReplying, setIsReplying] = useState(false);
    65→  const [isEditing, setIsEditing] = useState(false);
    66→  const [replyContent, setReplyContent] = useState('');
    67→  const [editContent, setEditContent] = useState(comment.content);
    68→
    69→  const isOwner = currentUserId === comment.author.id;
    70→
    71→  const handleSubmitReply = () => {
    72→    if (replyContent.trim()) {
    73→      onReply(comment.id, replyContent.trim());
    74→      setReplyContent('');
    75→      setIsReplying(false);
    76→    }
    77→  };
    78→
    79→  const handleSubmitEdit = () => {
    80→    if (editContent.trim() && editContent !== comment.content) {
    81→      onEdit?.(comment.id, editContent.trim());
    82→      setIsEditing(false);
    83→    } else {
    84→      setIsEditing(false);
    85→    }
    86→  };
    87→
    88→  return (
    89→    <div className={`${comment.depth > 0 ? 'ml-8 pl-6 border-l-2 border-white/5' : ''}`}>
    90→      <div className="flex gap-4">
    91→        <VoteButtons
    92→          voteCount={comment.voteCount}
    93→          userVote={comment.userVote}
    94→          onVote={(voteType) => onVote(comment.id, voteType)}
    95→          size="sm"
    96→        />
    97→
    98→        <div className="flex-1 min-w-0">
    99→          {/* Meta */}
   100→          <div className="flex items-center gap-2 mb-2">
   101→            <Link
   102→              href={`/forum/user/${comment.author.id}`}
   103→              className="text-[10px] font-black uppercase tracking-widest text-cyan-400 hover:text-cyan-300 transition-colors"
   104→            >
   105→              u/{comment.author.username}
   106→            </Link>
   107→            {comment.author.karma !== undefined && comment.author.karma > 0 && (
   108→              <KarmaBadge karma={comment.author.karma} size="xs" />
   109→            )}
   110→            <span className="text-neutral-700">•</span>
   111→            <span className="text-[10px] text-neutral-600">{formatDistanceToNow(comment.createdAt)}</span>
   112→            {comment.updatedAt !== comment.createdAt && (
   113→              <>
   114→                <span className="text-neutral-700">•</span>
   115→                <span className="text-[10px] text-neutral-600 italic">edited</span>
   116→              </>
   117→            )}
   118→          </div>
   119→
   120→          {/* Content */}
   121→          {isEditing ? (
   122→            <div className="space-y-3">
   123→              <MentionInput
   124→                value={editContent}
   125→                onChange={setEditContent}
   126→                rows={4}
   127→                className="resize-none"
   128→              />
   129→              <div className="flex gap-3">
   130→                <button
   131→                  onClick={handleSubmitEdit}
   132→                  className="px-4 py-2 bg-cyan-500 text-black text-[10px] font-black uppercase tracking-widest rounded-lg hover:bg-cyan-400 transition-all"
   133→                >
   134→                  Save
   135→                </button>
   136→                <button
   137→                  onClick={() => {
   138→                    setIsEditing(false);
   139→                    setEditContent(comment.content);
   140→                  }}
   141→                  className="px-4 py-2 text-neutral-500 text-[10px] font-black uppercase tracking-widest hover:text-white transition-colors"
   142→                >
   143→                  Cancel
   144→                </button>
   145→              </div>
   146→            </div>
   147→          ) : (
   148→            <p className="text-neutral-300 text-sm leading-relaxed whitespace-pre-wrap">
   149→              <MentionLink content={comment.content} />
   150→            </p>
   151→          )}
   152→
   153→          {/* Actions */}
   154→          {!isEditing && (
   155→            <div className="flex items-center gap-2 mt-3">
   156→              <button
   157→                onClick={() => setIsReplying(!isReplying)}
   158→                className="flex items-center gap-1.5 px-3 py-1.5 rounded-lg text-[10px] font-black uppercase tracking-wider text-neutral-600 hover:bg-white/5 hover:text-cyan-400 transition-all"
   159→              >
   160→                <Reply size={12} />
   161→                Reply
   162→              </button>
   163→              {isOwner && onEdit && (
   164→                <button
   165→                  onClick={() => setIsEditing(true)}
   166→                  className="flex items-center gap-1.5 px-3 py-1.5 rounded-lg text-[10px] font-black uppercase tracking-wider text-neutral-600 hover:bg-white/5 hover:text-white transition-all"
   167→                >
   168→                  <Pencil size={12} />
   169→                  Edit
   170→                </button>
   171→              )}
   172→              {isOwner && onDelete && (
   173→                <button
   174→                  onClick={() => {
   175→                    if (confirm('Are you sure you want to delete this comment?')) {
   176→                      onDelete(comment.id);
   177→                    }
   178→                  }}
   179→                  className="flex items-center gap-1.5 px-3 py-1.5 rounded-lg text-[10px] font-black uppercase tracking-wider text-neutral-600 hover:bg-red-500/10 hover:text-red-400 transition-all"
   180→                >
   181→                  <Trash2 size={12} />
   182→                  Delete
   183→                </button>
   184→              )}
   185→            </div>
   186→          )}
   187→
   188→          {/* Reply form */}
   189→          {isReplying && (
   190→            <div className="mt-4 space-y-3">
   191→              <MentionInput
   192→                value={replyContent}
   193→                onChange={setReplyContent}
   194→                placeholder="Write a reply... Use @username to mention others."
   195→                rows={3}
   196→                className="resize-none"
   197→              />
   198→              <div className="flex gap-3">
   199→                <button
   200→                  onClick={handleSubmitReply}
   201→                  disabled={!replyContent.trim()}
   202→                  className="px-4 py-2 bg-cyan-500 text-black text-[10px] font-black uppercase tracking-widest rounded-lg hover:bg-cyan-400 disabled:opacity-50 disabled:cursor-not-allowed transition-all"
   203→                >
   204→                  Reply
   205→                </button>
   206→                <button
   207→                  onClick={() => {
   208→                    setIsReplying(false);
   209→                    setReplyContent('');
   210→                  }}
   211→                  className="px-4 py-2 text-neutral-500 text-[10px] font-black uppercase tracking-widest hover:text-white transition-colors"
   212→                >
   213→                  Cancel
   214→                </button>
   215→              </div>
   216→            </div>
   217→          )}
   218→        </div>
   219→      </div>
   220→
   221→      {/* Nested replies */}
   222→      {comment.replies.length > 0 && (
   223→        <div className="mt-6 space-y-6">
   224→          {comment.replies.map((reply) => (
   225→            <CommentItem
   226→              key={reply.id}
   227→              comment={reply}
   228→              onVote={onVote}
   229→              onReply={onReply}
   230→              onEdit={onEdit}
   231→              onDelete={onDelete}
   232→              currentUserId={currentUserId}
   233→            />
   234→          ))}
   235→        </div>
   236→      )}
   237→    </div>
   238→  );
   239→}
   240→

