  1455→  async uploadPhysiquePhotos(
  1456→    front?: File,
  1457→    side?: File,
  1458→    back?: File
  1459→  ): Promise<{
  1460→    front_photo_url: string | null;
  1461→    side_photo_url: string | null;
  1462→    back_photo_url: string | null;
  1463→    created_at: string;
  1464→    updated_at: string;
  1465→  }> {
  1466→    const formData = new FormData();
  1467→    if (front) formData.append('front', front);
  1468→    if (side) formData.append('side', side);
  1469→    if (back) formData.append('back', back);
  1470→
  1471→    const authToken = this.getToken();
  1472→    const headers: Record<string, string> = {};
  1473→    if (authToken) {
  1474→      headers['Authorization'] = `Bearer ${authToken}`;
  1475→    }
  1476→
  1477→    const response = await fetch(`${API_URL}/physique/upload`, {
  1478→      method: 'POST',
  1479→      headers,
  1480→      body: formData,
  1481→    });
  1482→
  1483→    if (!response.ok) {
  1484→      const error = await response.json().catch(() => ({ detail: 'Upload failed' }));
  1485→      throw new Error(error.detail || `HTTP ${response.status}`);
  1486→    }
  1487→
  1488→    return response.json();
  1489→  }
