'use client';

import { useState, useRef, useCallback, useEffect } from 'react';
import { api, UserSearchResult } from '@/lib/api';

interface MentionInputProps {
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
  rows?: number;
  maxLength?: number;
  className?: string;
  disabled?: boolean;
}

/**
 * MentionInput - Textarea with @mention autocomplete
 *
 * When user types @, shows a dropdown of users matching the prefix.
 * Clicking a user inserts @username into the text.
 */
export function MentionInput({
  value,
  onChange,
  placeholder,
  rows = 4,
  maxLength = 10000,
  className = '',
  disabled = false,
}: MentionInputProps) {
  const [showDropdown, setShowDropdown] = useState(false);
  const [dropdownPosition, setDropdownPosition] = useState({ top: 0, left: 0 });
  const [mentionQuery, setMentionQuery] = useState('');
  const [mentionStartIndex, setMentionStartIndex] = useState(-1);
  const [suggestions, setSuggestions] = useState<UserSearchResult[]>([]);
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(false);

  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const debounceRef = useRef<NodeJS.Timeout | null>(null);

  // Handle text changes and detect @mentions
  const handleChange = useCallback(
    (e: React.ChangeEvent<HTMLTextAreaElement>) => {
      const newValue = e.target.value;
      const cursorPos = e.target.selectionStart;

      onChange(newValue);

      // Check if we're in a mention context
      const textBeforeCursor = newValue.slice(0, cursorPos);
      const lastAtIndex = textBeforeCursor.lastIndexOf('@');

      if (lastAtIndex !== -1) {
        const textAfterAt = textBeforeCursor.slice(lastAtIndex + 1);
        // Only trigger if there's no space after the @ and it's a valid prefix
        if (/^[a-zA-Z0-9_]*$/.test(textAfterAt) && textAfterAt.length <= 30) {
          // Check if the @ is at the start or after a whitespace
          const charBeforeAt = lastAtIndex > 0 ? newValue[lastAtIndex - 1] : ' ';
          if (/\s/.test(charBeforeAt) || lastAtIndex === 0) {
            setMentionStartIndex(lastAtIndex);
            setMentionQuery(textAfterAt);
            setShowDropdown(true);
            setSelectedIndex(0);

            // Calculate dropdown position
            if (textareaRef.current) {
              const rect = textareaRef.current.getBoundingClientRect();
              // Simple positioning below the textarea
              setDropdownPosition({
                top: rect.height + 4,
                left: 0,
              });
            }

            return;
          }
        }
      }

      // Close dropdown if not in mention context
      setShowDropdown(false);
      setMentionQuery('');
      setMentionStartIndex(-1);
    },
    [onChange]
  );

  // Fetch user suggestions with debounce
  useEffect(() => {
    if (!showDropdown || mentionQuery.length < 1) {
      setSuggestions([]);
      return;
    }

    // Clear previous debounce
    if (debounceRef.current) {
      clearTimeout(debounceRef.current);
    }

    // Debounce API call by 200ms
    debounceRef.current = setTimeout(async () => {
      setIsLoading(true);
      try {
        const result = await api.searchUsers(mentionQuery, { limit: 8 });
        setSuggestions(result.users);
      } catch (error) {
        console.error('Error searching users:', error);
        setSuggestions([]);
      } finally {
        setIsLoading(false);
      }
    }, 200);

    return () => {
      if (debounceRef.current) {
        clearTimeout(debounceRef.current);
      }
    };
  }, [mentionQuery, showDropdown]);

  // Insert selected mention
  const insertMention = useCallback(
    (user: UserSearchResult) => {
      if (mentionStartIndex === -1) return;

      const before = value.slice(0, mentionStartIndex);
      const after = value.slice(mentionStartIndex + mentionQuery.length + 1);
      const newValue = `${before}@${user.username} ${after}`;

      onChange(newValue);
      setShowDropdown(false);
      setMentionQuery('');
      setMentionStartIndex(-1);

      // Focus back to textarea and set cursor position
      setTimeout(() => {
        if (textareaRef.current) {
          const newCursorPos = mentionStartIndex + user.username.length + 2; // +2 for @ and space
          textareaRef.current.focus();
          textareaRef.current.setSelectionRange(newCursorPos, newCursorPos);
        }
      }, 0);
    },
    [value, onChange, mentionStartIndex, mentionQuery]
  );

  // Handle keyboard navigation
  const handleKeyDown = useCallback(
    (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
      if (!showDropdown || suggestions.length === 0) return;

      switch (e.key) {
        case 'ArrowDown':
          e.preventDefault();
          setSelectedIndex((prev) => (prev + 1) % suggestions.length);
          break;
        case 'ArrowUp':
          e.preventDefault();
          setSelectedIndex((prev) => (prev - 1 + suggestions.length) % suggestions.length);
          break;
        case 'Enter':
          if (showDropdown && suggestions[selectedIndex]) {
            e.preventDefault();
            insertMention(suggestions[selectedIndex]);
          }
          break;
        case 'Tab':
          if (showDropdown && suggestions[selectedIndex]) {
            e.preventDefault();
            insertMention(suggestions[selectedIndex]);
          }
          break;
        case 'Escape':
          e.preventDefault();
          setShowDropdown(false);
          break;
      }
    },
    [showDropdown, suggestions, selectedIndex, insertMention]
  );

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (e: MouseEvent) => {
      if (
        dropdownRef.current &&
        !dropdownRef.current.contains(e.target as Node) &&
        textareaRef.current &&
        !textareaRef.current.contains(e.target as Node)
      ) {
        setShowDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div className="relative">
      <textarea
        ref={textareaRef}
        value={value}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
        placeholder={placeholder}
        rows={rows}
        maxLength={maxLength}
        disabled={disabled}
        className={`w-full bg-neutral-900/50 border border-white/10 rounded-xl px-5 py-4 text-white text-sm placeholder-neutral-600 focus:border-cyan-500/50 focus:outline-none resize-y min-h-[100px] hover:border-white/20 transition-colors ${className}`}
      />

      {/* Mention autocomplete dropdown */}
      {showDropdown && (suggestions.length > 0 || isLoading) && (
        <div
          ref={dropdownRef}
          className="absolute z-50 w-full max-w-[300px] bg-neutral-900 border border-white/10 rounded-xl shadow-lg shadow-black/30 overflow-hidden"
          style={{
            top: dropdownPosition.top,
            left: dropdownPosition.left,
          }}
        >
          {isLoading ? (
            <div className="px-4 py-3 text-neutral-500 text-sm">Searching...</div>
          ) : (
            <ul className="max-h-[200px] overflow-y-auto">
              {suggestions.map((user, index) => (
                <li key={user.id}>
                  <button
                    type="button"
                    onClick={() => insertMention(user)}
                    onMouseEnter={() => setSelectedIndex(index)}
                    className={`w-full px-4 py-2.5 text-left text-sm flex items-center gap-3 transition-colors ${
                      index === selectedIndex
                        ? 'bg-cyan-500/20 text-cyan-400'
                        : 'text-neutral-300 hover:bg-white/5'
                    }`}
                  >
                    <span className="text-cyan-500">@</span>
                    <span className="font-medium">{user.username}</span>
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
}

