   401→    case 'gold': return 'bg-amber-500/10 border-amber-500/20';
   402→    case 'silver': return 'bg-neutral-400/10 border-neutral-400/20';
   403→    case 'bronze': return 'bg-amber-700/10 border-amber-700/20';
   404→    default: return 'bg-neutral-500/10 border-neutral-500/20';
   405→  }
   406→}
   407→
   408→export function getKarmaTierLabel(tier: KarmaTier): string {
