'use client';

import { useEffect, useState, useCallback } from 'react';
import { useParams } from 'next/navigation';
import Link from 'next/link';
import { api } from '@/lib/api';
import { ForumUserProfile, UserPostItem, UserCommentItem, VoteType } from '@/types/forum';
import { UserProfileCard } from '@/components/forum/UserProfileCard';
import { ForumHeader, ForumBreadcrumb, VoteButtons } from '@/components/forum';
import { formatDistanceToNow } from '@/lib/utils';
import { MessageSquare, FileText, User, Pin, Sparkles, ArrowUpRight } from 'lucide-react';

// ============================================
// SKELETON COMPONENTS
// ============================================

function ProfileSkeleton() {
