'use client';

import { useEffect, useState, useCallback } from 'react';
import { useParams, useRouter } from 'next/navigation';
import Link from 'next/link';
import { api } from '@/lib/api';
import { ForumUserProfile, UserPostItem, UserCommentItem, VoteType } from '@/types/forum';
import { UserProfileCard } from '@/components/forum/UserProfileCard';
import { ForumHeader, ForumBreadcrumb, VoteButtons } from '@/components/forum';
import { formatDistanceToNow } from '@/lib/utils';
import { MessageSquare, FileText, User, Pin, Sparkles, ArrowUpRight } from 'lucide-react';

/**
 * Check if a string is a valid UUID v4
 */
function isUUID(str: string): boolean {
  const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
  return uuidRegex.test(str);
}

// ============================================
// SKELETON COMPONENTS
// ============================================

function ProfileSkeleton() {
  return (
    <div className="rounded-2xl bg-neutral-900/30 border border-white/5 overflow-hidden animate-pulse">
      <div className="h-24 bg-neutral-800" />
      <div className="px-6 pt-12 pb-6">
        <div className="h-7 w-40 bg-neutral-800 rounded mb-2" />
        <div className="h-4 w-32 bg-neutral-800 rounded mb-6" />
        <div className="grid grid-cols-3 gap-4">
          {[1, 2, 3].map((i) => (
            <div key={i} className="h-24 bg-neutral-800 rounded-xl" />
          ))}
        </div>
      </div>
    </div>
  );
}

function PostItemSkeleton() {
  return (
    <div className="p-4 rounded-xl bg-neutral-900/30 border border-white/5 animate-pulse">
      <div className="flex gap-4">
        <div className="w-10 space-y-2">
          <div className="h-5 w-5 bg-neutral-800 rounded mx-auto" />
          <div className="h-4 w-4 bg-neutral-800 rounded mx-auto" />
        </div>
        <div className="flex-1 space-y-2">
          <div className="h-5 w-3/4 bg-neutral-800 rounded" />
          <div className="h-4 w-full bg-neutral-800 rounded" />
          <div className="h-3 w-32 bg-neutral-800 rounded" />
        </div>
      </div>
    </div>
  );
}

function CommentItemSkeleton() {
  return (
    <div className="p-4 rounded-xl bg-neutral-900/30 border border-white/5 animate-pulse">
      <div className="flex gap-4">
        <div className="w-10 space-y-2">
          <div className="h-5 w-5 bg-neutral-800 rounded mx-auto" />
          <div className="h-4 w-4 bg-neutral-800 rounded mx-auto" />
        </div>
        <div className="flex-1 space-y-2">
          <div className="h-4 w-48 bg-neutral-800 rounded" />
          <div className="h-4 w-full bg-neutral-800 rounded" />
          <div className="h-3 w-24 bg-neutral-800 rounded" />
        </div>
      </div>
    </div>
  );
}

// ============================================
// POST ITEM COMPONENT
// ============================================

interface UserPostCardProps {
  post: UserPostItem;
  onVote?: (postId: string, voteType: VoteType) => void;
}

function UserPostCard({ post, onVote }: UserPostCardProps) {
  return (
    <article className="group relative rounded-xl bg-neutral-900/30 border border-white/5 hover:border-cyan-500/20 transition-all overflow-hidden">
      <div className="flex">
        {/* Vote column */}
        <div className="w-12 bg-neutral-900/50 border-r border-white/5 flex flex-col items-center py-4">
          <VoteButtons
            voteCount={post.voteCount}
            userVote={post.userVote}
            onVote={(voteType) => onVote?.(post.id, voteType)}
            size="sm"
          />
        </div>

        {/* Content */}
        <div className="flex-1 p-4 min-w-0">
          {/* Meta line */}
          <div className="flex items-center flex-wrap gap-2 mb-2">
            {post.isPinned && (
              <span className="flex items-center gap-1 px-2 py-0.5 rounded-md bg-cyan-500/10 border border-cyan-500/20 text-[9px] font-black uppercase tracking-wider text-cyan-400">
                <Pin size={10} />
                Pinned
              </span>
            )}
            {post.isGuide && (
              <span className="flex items-center gap-1 px-2 py-0.5 rounded-md bg-green-500/10 border border-green-500/20 text-[9px] font-black uppercase tracking-wider text-green-400">
                <Sparkles size={10} />
                Guide
              </span>
            )}
            <Link
              href={`/forum/${post.categorySlug}`}
              className="text-[10px] font-black uppercase tracking-widest text-cyan-400 hover:text-cyan-300"
            >
              {post.subForumSlug}
            </Link>
            <span className="text-[10px] text-neutral-700">|</span>
            <span className="text-[10px] text-neutral-600">{formatDistanceToNow(post.createdAt)}</span>
          </div>

          {/* Title */}
          <Link href={`/forum/post/${post.id}`}>
            <h3 className="text-base font-bold text-white group-hover:text-cyan-400 transition-colors leading-snug mb-2">
              {post.title}
            </h3>
          </Link>

          {/* Preview text */}
          {post.contentPreview && (
            <p className="text-neutral-500 text-sm line-clamp-2 mb-3 leading-relaxed">
              {post.contentPreview}
            </p>
          )}

          {/* Stats */}
          <div className="flex items-center gap-3">
            <span className="flex items-center gap-1.5 text-[10px] font-medium text-neutral-500">
              <MessageSquare size={12} />
              {post.commentCount}
            </span>
          </div>
        </div>
      </div>
    </article>
  );
}

// ============================================
// COMMENT ITEM COMPONENT
// ============================================

interface UserCommentCardProps {
  comment: UserCommentItem;
  onVote?: (commentId: string, voteType: VoteType) => void;
}

function UserCommentCard({ comment, onVote }: UserCommentCardProps) {
  return (
    <article className="group relative rounded-xl bg-neutral-900/30 border border-white/5 hover:border-cyan-500/20 transition-all overflow-hidden">
      <div className="flex">
        {/* Vote column */}
        <div className="w-12 bg-neutral-900/50 border-r border-white/5 flex flex-col items-center py-4">
          <VoteButtons
            voteCount={comment.voteCount}
            userVote={comment.userVote}
            onVote={(voteType) => onVote?.(comment.id, voteType)}
            size="sm"
          />
        </div>

        {/* Content */}
        <div className="flex-1 p-4 min-w-0">
          {/* Post reference */}
          <Link
            href={`/forum/post/${comment.postId}`}
            className="flex items-center gap-2 text-[10px] font-medium text-neutral-500 hover:text-cyan-400 transition-colors mb-2"
          >
            <ArrowUpRight size={12} />
            <span className="line-clamp-1">{comment.postTitle}</span>
          </Link>

          {/* Comment content */}
          <p className="text-neutral-300 text-sm leading-relaxed line-clamp-3 mb-3">
            {comment.content}
          </p>

          {/* Meta */}
          <div className="flex items-center gap-3">
            <span className="text-[10px] text-neutral-600">
              {formatDistanceToNow(comment.createdAt)}
            </span>
          </div>
        </div>
      </div>
    </article>
  );
}

// ============================================
// MAIN PAGE
// ============================================

type TabType = 'posts' | 'comments';

export default function UserProfilePage() {
  const params = useParams();
  const router = useRouter();
  const identifier = params.userId as string; // Could be UUID or username

  const [profile, setProfile] = useState<ForumUserProfile | null>(null);
  const [posts, setPosts] = useState<UserPostItem[]>([]);
  const [comments, setComments] = useState<UserCommentItem[]>([]);
  const [activeTab, setActiveTab] = useState<TabType>('posts');

  const [isLoadingProfile, setIsLoadingProfile] = useState(true);
  const [isLoadingContent, setIsLoadingContent] = useState(false);
  const [hasMorePosts, setHasMorePosts] = useState(true);
  const [hasMoreComments, setHasMoreComments] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const PAGE_SIZE = 20;

  // Fetch profile - handles both UUID and username
  useEffect(() => {
    async function fetchProfile() {
      setIsLoadingProfile(true);
      setError(null);
      try {
        let data: ForumUserProfile;

        // Check if identifier is a UUID or username
        if (isUUID(identifier)) {
          // It's a UUID, use the standard endpoint
          data = await api.getForumUserProfile(identifier);
        } else {
          // It's a username, use the username lookup endpoint
          data = await api.getForumUserProfileByUsername(identifier);
          // Redirect to canonical URL with user ID for cleaner URLs
          router.replace(`/forum/user/${data.id}`);
        }

        setProfile(data);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to load profile');
      } finally {
        setIsLoadingProfile(false);
      }
    }

    fetchProfile();
  }, [identifier, router]);

  // Fetch posts
  const fetchPosts = useCallback(async (offset = 0) => {
    setIsLoadingContent(true);
    try {
      const data = await api.getUserForumPosts(userId, { limit: PAGE_SIZE, offset });
      if (offset === 0) {
        setPosts(data.posts);
      } else {
        setPosts((prev) => [...prev, ...data.posts]);
      }
      setHasMorePosts(data.hasMore);
    } catch {
      // Silent fail for content loading
    } finally {
      setIsLoadingContent(false);
    }
  }, [userId]);

  // Fetch comments
  const fetchComments = useCallback(async (offset = 0) => {
    setIsLoadingContent(true);
    try {
      const data = await api.getUserForumComments(userId, { limit: PAGE_SIZE, offset });
      if (offset === 0) {
        setComments(data.comments);
      } else {
        setComments((prev) => [...prev, ...data.comments]);
      }
      setHasMoreComments(data.hasMore);
    } catch {
      // Silent fail for content loading
    } finally {
      setIsLoadingContent(false);
    }
  }, [userId]);

  // Fetch content when tab changes or on initial load
  useEffect(() => {
    if (activeTab === 'posts' && posts.length === 0) {
      fetchPosts();
    } else if (activeTab === 'comments' && comments.length === 0) {
      fetchComments();
    }
  }, [activeTab, posts.length, comments.length, fetchPosts, fetchComments]);

  const handleLoadMore = () => {
    if (activeTab === 'posts' && hasMorePosts) {
      fetchPosts(posts.length);
    } else if (activeTab === 'comments' && hasMoreComments) {
      fetchComments(comments.length);
    }
  };

  const handleVotePost = async (postId: string, voteType: VoteType) => {
    try {
      const result = await api.voteForumPost(postId, voteType);
      setPosts((prev) =>
        prev.map((p) =>
          p.id === postId
            ? { ...p, voteCount: result.newVoteCount, userVote: result.userVote }
            : p
        )
      );
    } catch {
      // Silent fail
    }
  };

  const handleVoteComment = async (commentId: string, voteType: VoteType) => {
    try {
      const result = await api.voteForumComment(commentId, voteType);
      setComments((prev) =>
        prev.map((c) =>
          c.id === commentId
            ? { ...c, voteCount: result.newVoteCount, userVote: result.userVote }
            : c
        )
      );
    } catch {
      // Silent fail
    }
  };

  return (
    <div className="min-h-screen bg-black selection:bg-cyan-500/30">
      <ForumHeader />
      <ForumBreadcrumb
        items={[
          { label: 'Community', href: '/forum' },
          { label: profile?.username || 'User Profile' },
        ]}
      />

      {/* Content */}
      <div className="max-w-4xl mx-auto px-6 py-10">
        {/* Error */}
        {error && (
          <div className="p-4 rounded-xl bg-red-500/10 border border-red-500/20 mb-6">
            <p className="text-red-400 text-sm">{error}</p>
          </div>
        )}

        {/* Profile Card */}
        {isLoadingProfile ? (
          <ProfileSkeleton />
        ) : profile ? (
          <UserProfileCard profile={profile} variant="full" />
        ) : !error ? (
          <div className="text-center py-16 rounded-2xl bg-neutral-900/30 border border-white/5">
            <User size={48} className="mx-auto text-neutral-700 mb-4" />
            <h2 className="text-xl font-black text-white mb-3">User not found</h2>
            <p className="text-neutral-400 mb-6">
              This user doesn&apos;t exist or has been banned.
            </p>
            <Link
              href="/forum"
              className="inline-flex items-center gap-2 px-6 py-3 bg-cyan-500 text-black text-[10px] font-black uppercase tracking-widest rounded-xl hover:bg-cyan-400 transition-all"
            >
              Go back to forum
            </Link>
          </div>
        ) : null}

        {/* Tabs */}
        {profile && (
          <>
            <div className="flex gap-2 mt-8 mb-6">
              <button
                onClick={() => setActiveTab('posts')}
                className={`flex items-center gap-2 px-5 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all ${
                  activeTab === 'posts'
                    ? 'bg-cyan-500 text-black'
                    : 'bg-neutral-900/30 text-neutral-400 border border-white/5 hover:border-cyan-500/20 hover:text-cyan-400'
                }`}
              >
                <FileText size={14} />
                Posts ({profile.forumPostsCount})
              </button>
              <button
                onClick={() => setActiveTab('comments')}
                className={`flex items-center gap-2 px-5 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all ${
                  activeTab === 'comments'
                    ? 'bg-cyan-500 text-black'
                    : 'bg-neutral-900/30 text-neutral-400 border border-white/5 hover:border-cyan-500/20 hover:text-cyan-400'
                }`}
              >
                <MessageSquare size={14} />
                Comments ({profile.forumCommentsCount})
              </button>
            </div>

            {/* Content */}
            {activeTab === 'posts' && (
              <div className="space-y-4">
                {isLoadingContent && posts.length === 0 ? (
                  <>
                    {[1, 2, 3].map((i) => (
                      <PostItemSkeleton key={i} />
                    ))}
                  </>
                ) : posts.length > 0 ? (
                  <>
                    {posts.map((post) => (
                      <UserPostCard key={post.id} post={post} onVote={handleVotePost} />
                    ))}
                    {hasMorePosts && (
                      <button
                        onClick={handleLoadMore}
                        disabled={isLoadingContent}
                        className="w-full py-4 rounded-xl bg-neutral-900/30 border border-white/5 text-neutral-400 text-[10px] font-black uppercase tracking-widest hover:border-cyan-500/20 hover:text-cyan-400 transition-all disabled:opacity-50"
                      >
                        {isLoadingContent ? 'Loading...' : 'Load More Posts'}
                      </button>
                    )}
                  </>
                ) : (
                  <div className="text-center py-12 rounded-2xl bg-neutral-900/30 border border-white/5">
                    <FileText size={32} className="mx-auto text-neutral-700 mb-3" />
                    <p className="text-neutral-400">No posts yet</p>
                  </div>
                )}
              </div>
            )}

            {activeTab === 'comments' && (
              <div className="space-y-4">
                {isLoadingContent && comments.length === 0 ? (
                  <>
                    {[1, 2, 3].map((i) => (
                      <CommentItemSkeleton key={i} />
                    ))}
                  </>
                ) : comments.length > 0 ? (
                  <>
                    {comments.map((comment) => (
                      <UserCommentCard
                        key={comment.id}
                        comment={comment}
                        onVote={handleVoteComment}
                      />
                    ))}
                    {hasMoreComments && (
                      <button
                        onClick={handleLoadMore}
                        disabled={isLoadingContent}
                        className="w-full py-4 rounded-xl bg-neutral-900/30 border border-white/5 text-neutral-400 text-[10px] font-black uppercase tracking-widest hover:border-cyan-500/20 hover:text-cyan-400 transition-all disabled:opacity-50"
                      >
                        {isLoadingContent ? 'Loading...' : 'Load More Comments'}
                      </button>
                    )}
                  </>
                ) : (
                  <div className="text-center py-12 rounded-2xl bg-neutral-900/30 border border-white/5">
                    <MessageSquare size={32} className="mx-auto text-neutral-700 mb-3" />
                    <p className="text-neutral-400">No comments yet</p>
                  </div>
                )}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

