   392→
   393→class ApiClient {
   394→  private token: string | null = null;
   395→
   396→  setToken(token: string | null) {
   397→    this.token = token;
   398→  }
   399→
   400→  getToken(): string | null {
   401→    if (typeof window !== 'undefined') {
   402→      return this.token || localStorage.getItem('auth_token');
   403→    }
   404→    return this.token;
   405→  }
   406→
   407→  private async request<T>(endpoint: string, options: ApiOptions = {}): Promise<T> {
   408→    const { method = 'GET', body, token } = options;
   409→    const authToken = token || this.getToken();
   410→
   411→    const headers: Record<string, string> = {
   412→      'Content-Type': 'application/json',
   413→    };
   414→
   415→    if (authToken) {
   416→      headers['Authorization'] = `Bearer ${authToken}`;
   417→    }
   418→
   419→    const response = await fetch(`${API_URL}${endpoint}`, {
   420→      method,
   421→      headers,
   422→      body: body ? JSON.stringify(body) : undefined,
   423→    });
   424→
   425→    if (!response.ok) {
   426→      const error = await response.json().catch(() => ({ detail: 'Request failed' }));
   427→      throw new Error(error.detail || `HTTP ${response.status}`);
   428→    }
   429→
   430→    // Handle empty responses (204 No Content)
   431→    if (response.status === 204) {
   432→      return undefined as T;
   433→    }
   434→
   435→    return response.json();
   436→  }
