     1→/**
     2→ * API Client for LOOKSMAXX Backend
     3→ *
     4→ * Handles all authenticated requests to the Railway-hosted API
     5→ */
     6→
     7→import type { UserRank, LeaderboardEntry, LeaderboardData, UserProfile } from '@/types/results';
     8→
     9→const API_URL = process.env.NEXT_PUBLIC_API_URL || 'http://localhost:8000';
    10→
    11→// API Response types (snake_case from backend)
    12→interface LeaderboardApiResponse {
    13→  user_id: string;
    14→  score: number;
    15→  global_rank: number;
    16→  gender_rank: number;
    17→  percentile: number;
    18→  total_users: number;
    19→  gender_total: number;
    20→  anonymous_name: string;
    21→  updated_at: string;
    22→}
    23→
    24→interface LeaderboardEntryResponse {
    25→  rank: number;
    26→  score: number;
    27→  anonymous_name: string;
    28→  gender: 'male' | 'female';
    29→  face_photo_url: string | null;
    30→  is_current_user: boolean;
    31→}
    32→
    33→interface LeaderboardListResponse {
    34→  entries: LeaderboardEntryResponse[];
    35→  total_count: number;
    36→  user_rank: LeaderboardApiResponse | null;
    37→}
    38→
    39→interface UserProfileResponse {
    40→  rank: number;
    41→  score: number;
    42→  anonymous_name: string;
    43→  gender: 'male' | 'female';
    44→  face_photo_url: string | null;
    45→  top_strengths: string[];
    46→  top_improvements: string[];
    47→}
    48→
    49→// Transform functions (snake_case -> camelCase)
    50→function transformUserRank(data: LeaderboardApiResponse): UserRank {
    51→  return {
    52→    userId: data.user_id,
    53→    score: data.score,
    54→    globalRank: data.global_rank,
    55→    genderRank: data.gender_rank,
    56→    percentile: data.percentile,
    57→    totalUsers: data.total_users,
    58→    genderTotal: data.gender_total,
    59→    anonymousName: data.anonymous_name,
    60→    updatedAt: data.updated_at,
    61→  };
    62→}
    63→
    64→function transformLeaderboardEntry(data: LeaderboardEntryResponse): LeaderboardEntry {
    65→  return {
    66→    rank: data.rank,
    67→    score: data.score,
    68→    anonymousName: data.anonymous_name,
    69→    gender: data.gender,
    70→    facePhotoUrl: data.face_photo_url,
    71→    isCurrentUser: data.is_current_user,
    72→  };
    73→}
    74→
    75→function transformLeaderboardData(data: LeaderboardListResponse): LeaderboardData {
    76→  return {
    77→    entries: data.entries.map(transformLeaderboardEntry),
    78→    totalCount: data.total_count,
    79→    userRank: data.user_rank ? transformUserRank(data.user_rank) : null,
    80→  };
    81→}
    82→
    83→function transformUserProfile(data: UserProfileResponse): UserProfile {
    84→  return {
    85→    rank: data.rank,
    86→    score: data.score,
    87→    anonymousName: data.anonymous_name,
    88→    gender: data.gender,
    89→    facePhotoUrl: data.face_photo_url,
    90→    topStrengths: data.top_strengths,
    91→    topImprovements: data.top_improvements,
    92→  };
    93→}
    94→
    95→interface ApiOptions {
    96→  method?: 'GET' | 'POST' | 'PUT' | 'DELETE';
    97→  body?: unknown;
    98→  token?: string;
    99→}
   100→
   101→interface Analysis {
   102→  id: string;
   103→  front_image_url: string | null;
   104→  side_image_url: string | null;
   105→  front_landmarks: Record<string, unknown> | null;
   106→  side_landmarks: Record<string, unknown> | null;
   107→  scores: Record<string, unknown> | null;
   108→  gender: string | null;
   109→  ethnicity: string | null;
   110→  created_at: string;
   111→}
   112→
   113→class ApiClient {
   114→  private token: string | null = null;
   115→
   116→  setToken(token: string | null) {
   117→    this.token = token;
   118→  }
   119→
   120→  getToken(): string | null {
   121→    if (typeof window !== 'undefined') {
   122→      return this.token || localStorage.getItem('auth_token');
   123→    }
   124→    return this.token;
   125→  }
   126→
   127→  private async request<T>(endpoint: string, options: ApiOptions = {}): Promise<T> {
   128→    const { method = 'GET', body, token } = options;
   129→    const authToken = token || this.getToken();
   130→
   131→    const headers: Record<string, string> = {
   132→      'Content-Type': 'application/json',
   133→    };
   134→
   135→    if (authToken) {
   136→      headers['Authorization'] = `Bearer ${authToken}`;
   137→    }
   138→
   139→    const response = await fetch(`${API_URL}${endpoint}`, {
   140→      method,
   141→      headers,
   142→      body: body ? JSON.stringify(body) : undefined,
   143→    });
   144→
   145→    if (!response.ok) {
   146→      const error = await response.json().catch(() => ({ detail: 'Request failed' }));
   147→      throw new Error(error.detail || `HTTP ${response.status}`);
   148→    }
   149→
   150→    // Handle empty responses (204 No Content)
   151→    if (response.status === 204) {
   152→      return undefined as T;
   153→    }
   154→
   155→    return response.json();
   156→  }
   157→
   158→  // Health check
   159→  async health(): Promise<{ status: string; detection_ready: boolean }> {
   160→    return this.request('/health');
   161→  }
   162→
   163→  // Analyses CRUD
   164→  async listAnalyses(limit = 50, offset = 0): Promise<Analysis[]> {
   165→    return this.request(`/analyses?limit=${limit}&offset=${offset}`);
   166→  }
   167→
   168→  async createAnalysis(data: {
