/**
 * FaceIQ-Style Results Page Types
 * Complete type definitions for the results UI
 */

import { QualityTier, SeverityLevel, MeasurementUnit } from '@/lib/faceiq-scoring';

// ============================================
// ILLUSTRATION TYPES
// ============================================

export interface IllustrationPoint {
  type: 'landmark' | 'calculated';
  landmarkId?: string;
  x?: number;
  y?: number;
  label?: string;
}

export interface IllustrationLine {
  from: string;
  to: string;
  color: string;
  style?: 'solid' | 'dashed';
  label?: string;
  labelColor?: string;
  labelPosition?: 'start' | 'middle' | 'end';
}

export interface RatioIllustration {
  points: Record<string, IllustrationPoint>;
  lines: Record<string, IllustrationLine>;
}

// ============================================
// RATIO/MEASUREMENT TYPES
// ============================================

export interface Ratio {
  id: string;
  name: string;
  value: number;
  score: number;  // 0-10 scale
  standardizedScore: number;
  unit: 'x' | 'mm' | '%' | '°';
  idealMin: number;
  idealMax: number;
  rangeMin: number;
  rangeMax: number;
  description: string;
  category: string;
  qualityLevel: QualityTier;
  severity: SeverityLevel;
  illustration: RatioIllustration;
  mayIndicateFlaws: string[];
  mayIndicateStrengths: string[];
  usedLandmarks: string[];
  scoringCurveConfig?: {
    decayRate: number;
    maxScore: number;
  };
  // FaceIQ Taxonomy classification
  taxonomyPrimary?: string;    // 'harmony' | 'dimorphism' | 'angularity' | 'features'
  taxonomySecondary?: string;  // Subcategory ID
}

// ============================================
// STRENGTH/FLAW TYPES
// ============================================

export interface ResponsibleRatio {
  ratioName: string;
  ratioId: string;
  score: number;
  value: number;
  isObfuscated?: boolean;
  // Additional properties for AI descriptions
  idealMin: number;
  idealMax: number;
  unit: string;
  category?: string;
}

export interface Strength {
  id: string;
  strengthName: string;
  summary: string;
  avgScore: number;
  qualityLevel: QualityTier;
  categoryName: string;
  responsibleRatios: ResponsibleRatio[];
}

export interface Flaw {
  id: string;
  flawName: string;
  summary: string;
  harmonyPercentageLost: number;
  standardizedImpact: number;
  categoryName: string;

