     1→/**
     2→ * Advice Engine - Client-side implementation matching the Python AdviceEngine
     3→ * Processes plans.json trigger rules against user metrics
     4→ */
     5→
     6→import { Plan } from '@/components/results/cards/PlanActionCard';
     7→
     8→// ============================================
     9→// TYPES
    10→// ============================================
    11→
    12→interface Threshold {
    13→  operator: '<' | '>';
    14→  value: number;
    15→}
    16→
    17→interface TriggerRules {
    18→  metrics: string[];
    19→  condition: 'OR' | 'AND';
    20→  thresholds: Record<string, Threshold>;
    21→}
    22→
    23→interface PlanContent {
    24→  description: string;
    25→  cost_min: number;
    26→  cost_max: number;
    27→  time_min?: string;
    28→  time_max?: string;
    29→  risks?: string;
    30→  citations?: string[];
    31→  tags?: string[];
    32→}
    33→
    34→interface RawPlan {
    35→  id: string;
    36→  title: string;
    37→  trigger_rules: TriggerRules;
    38→  content: PlanContent;
    39→}
    40→
    41→interface TriggerResult {
    42→  metric: string;
    43→  value: number;
    44→  threshold: number;
    45→  operator: '<' | '>';
    46→}
    47→
