   727→interface ApiOptions {
   728→  method?: 'GET' | 'POST' | 'PUT' | 'DELETE';
   729→  body?: unknown;
   730→  token?: string;
   731→}
   732→
   733→interface Analysis {
   734→  id: string;
   735→  front_image_url: string | null;
   736→  side_image_url: string | null;
   737→  front_landmarks: Record<string, unknown> | null;
   738→  side_landmarks: Record<string, unknown> | null;
   739→  scores: Record<string, unknown> | null;
   740→  gender: string | null;
