    score: number;
    tier: string;
    percentile: number;
    breakdown: {
      face: { raw: number; weighted: number };
      height: { raw: number; weighted: number };
      body: { raw: number; weighted: number };
      bonuses: { threshold: number; synergy: number; total: number };
