  }

  async getMyHeight(): Promise<{
    height_cm: number;
    height_rating: number;
    height_display: string;
  } | null> {
    return this.request('/psl/my-height');
  }

  async updateUserWeight(weight_kg: number): Promise<{
    weight_kg: number;
    weight_display: string;
    bmi: number | null;
  }> {
