}

export interface BodyAnalysisResult {
  estimated_body_fat: number;
  muscle_mass: string;
  frame_size: string;
  shoulder_width: string;
  waist_definition: string;
  posture: string;
  confidence: number;
  notes: string | null;
}

// Export singleton instance
export const api = new ApiClient();

// Export types
export type { Analysis };

