/**
 * Forum Types - Matches backend Pydantic schemas
 */

// === ENUMS ===

export type VoteType = 'up' | 'down';

export type TargetType = 'post' | 'comment';

export type ReportReason =
  | 'spam'
  | 'harassment'
  | 'misinformation'
  | 'off_topic'
  | 'inappropriate'
  | 'other';

export type SortOrder = 'hot' | 'new' | 'top';

// === SUB-FORUM ===

export interface SubForum {
  id: string;
  name: string;
  slug: string;
  description: string | null;
  icon: string | null;
  displayOrder: number;
  postCount: number;
}

// === CATEGORY ===

export interface Category {
  id: string;
  name: string;
  slug: string;
  description: string | null;
  icon: string | null;
  displayOrder: number;
  postCount: number;
  subForums: SubForum[];
}

export interface CategoryListItem {
  id: string;
  name: string;
  slug: string;
  description: string | null;
  icon: string | null;
  displayOrder: number;
  postCount: number;
}

// === POST ===

export interface PostAuthor {
  id: string;
  username: string;
}

export interface Post {
  id: string;
  title: string;
  content: string;
  subForumId: string;
  subForumSlug: string;
  categorySlug: string;
  author: PostAuthor;
  isPinned: boolean;
  isGuide: boolean;
  voteCount: number;
  commentCount: number;
  userVote: VoteType | null;
  createdAt: string;
  updatedAt: string;
}

export interface PostListItem {
  id: string;
  title: string;
  contentPreview: string;
  subForumSlug: string;
  categorySlug: string;
  author: PostAuthor;
  isPinned: boolean;
  isGuide: boolean;
  voteCount: number;
  commentCount: number;
  userVote: VoteType | null;
  createdAt: string;
}

export interface PostListResponse {
  posts: PostListItem[];
  totalCount: number;
  hasMore: boolean;
}

export interface PostCreate {
  title: string;
  content: string;
  subForumId: string;
}

export interface PostUpdate {
  title?: string;
  content?: string;
}

// === COMMENT ===

export interface Comment {
  id: string;
  content: string;
  postId: string;
  author: PostAuthor;
  parentId: string | null;
  voteCount: number;
  userVote: VoteType | null;
  depth: number;
  replies: Comment[];
  createdAt: string;
  updatedAt: string;
}

export interface CommentCreate {
  content: string;
  parentId?: string;
}

export interface CommentUpdate {
  content: string;
}

// === VOTE ===

export interface VoteRequest {
  voteType: VoteType;
}

export interface VoteResponse {
  success: boolean;
  newVoteCount: number;
  userVote: VoteType | null;
}

// === REPORT ===

export interface ReportCreate {
  targetType: TargetType;
  targetId: string;
  reason: ReportReason;
  details?: string;
}

export interface Report {
  id: string;
  targetType: TargetType;
  targetId: string;
  reason: ReportReason;
  status: string;
  createdAt: string;
}

// === GUIDES SECTION ===

export interface GuideSection {
  category: CategoryListItem;
  guides: PostListItem[];
}

// === RECOMMENDED FORUMS ===

export interface RecommendedForum {
  category: CategoryListItem;
  matchedFlaws: string[];
  priority: number;
}

// === SEARCH ===

export type SearchResultType = 'post' | 'comment';

export interface ForumSearchResult {
  id: string;
  type: SearchResultType;
  title: string | null;  // Only for posts
  contentPreview: string;
  postId: string | null;  // Only for comments
  postTitle: string | null;  // Only for comments
  author: PostAuthor;
  voteCount: number;
  categorySlug: string;
  subForumSlug: string;
  createdAt: string;
}

export interface ForumSearchResponse {
  results: ForumSearchResult[];
  totalCount: number;
  query: string;
  searchType: string;
  hasMore: boolean;
}

// === USER PROFILE ===

export interface ForumUserProfile {
  id: string;
  username: string;
  forumPostsCount: number;
  forumCommentsCount: number;
  forumKarma: number;
  memberSince: string;
}

export interface UserPostItem {
  id: string;
  title: string;
  contentPreview: string;
  subForumSlug: string;
  categorySlug: string;
  isPinned: boolean;
  isGuide: boolean;
  voteCount: number;
  commentCount: number;
  userVote: VoteType | null;
  createdAt: string;
}

export interface UserPostListResponse {
  posts: UserPostItem[];
  totalCount: number;
  hasMore: boolean;
}

export interface UserCommentItem {
  id: string;
  content: string;
  postId: string;
  postTitle: string;
  categorySlug: string;
  voteCount: number;
  userVote: VoteType | null;
  createdAt: string;
}

export interface UserCommentListResponse {
  comments: UserCommentItem[];
  totalCount: number;
  hasMore: boolean;
}

// === BOOKMARK TYPES ===

export interface BookmarkToggleResponse {
  isBookmarked: boolean;
  message: string;
}

export interface BookmarkStatus {
  postId: string;
  isBookmarked: boolean;
  bookmarkedAt: string | null;
}

export interface BookmarkedPostsResponse {
  posts: PostListItem[];
  totalCount: number;
  hasMore: boolean;
}

// === ADMIN/MODERATION TYPES ===

export type ReportStatusEnum = 'pending' | 'reviewed' | 'resolved' | 'dismissed';

export type ActionTaken = 'content_removed' | 'user_warned' | 'user_banned' | 'no_action';

export interface ReportDetail {
  id: string;
  targetType: TargetType;
  targetId: string;
  reason: ReportReason;
  details: string | null;
  status: ReportStatusEnum;
  reporterId: string;
  reporterUsername: string;
  reviewedBy: string | null;
  reviewedAt: string | null;
  resolutionNotes: string | null;
  actionTaken: ActionTaken | null;
  createdAt: string;
  // Denormalized content for preview
  contentPreview: string | null;
  contentAuthorId: string | null;
  contentAuthorUsername: string | null;
}

export interface AdminReportListResponse {
  reports: ReportDetail[];
  totalCount: number;
  hasMore: boolean;
}

export interface ResolveReportRequest {
  status: ReportStatusEnum;
  resolutionNotes?: string;
  actionTaken?: ActionTaken;
}

export interface ModerationStats {
  pendingReports: number;
  resolvedToday: number;
  resolvedThisWeek: number;
  totalReports: number;
  reportsByReason: Record<string, number>;
  reportsByStatus: Record<string, number>;
}

export interface BanUserRequest {
  reason?: string;
}

export interface BanUserResponse {
  userId: string;
  username: string;
  isBanned: boolean;
  message: string;
}

export interface BannedUser {
  id: string;
  username: string;
  email: string;
  isBanned: boolean;
  createdAt: string | null;
  updatedAt: string | null;
}

export interface BannedUsersResponse {
  users: BannedUser[];
  totalCount: number;
  hasMore: boolean;
}

