import Link from 'next/link';
import { useMemo } from 'react';

// Pattern for @mentions: @username where username is 3-30 alphanumeric or underscore chars
const MENTION_PATTERN = /@([a-zA-Z0-9_]{3,30})(?![a-zA-Z0-9_])/g;

interface MentionLinkProps {
  content: string;
  className?: string;
}

interface ContentPart {
  type: 'text' | 'mention';
  content: string;
  username?: string;
}

/**
 * MentionLink - Renders @mentions as clickable links
 *
 * Parses content and replaces @username patterns with links to user profiles.
 */
export function MentionLink({ content, className = '' }: MentionLinkProps) {
  // Parse content into parts (text and mentions)
  const parts = useMemo(() => {
    const result: ContentPart[] = [];
    let lastIndex = 0;
    let match;

    // Reset regex state
    MENTION_PATTERN.lastIndex = 0;

    while ((match = MENTION_PATTERN.exec(content)) !== null) {
      // Add text before the mention
      if (match.index > lastIndex) {
        result.push({
          type: 'text',
          content: content.slice(lastIndex, match.index),
        });
      }

      // Add the mention
      result.push({
        type: 'mention',
        content: match[0], // Full match including @
        username: match[1], // Just the username
      });

      lastIndex = match.index + match[0].length;
    }

    // Add remaining text after last mention
    if (lastIndex < content.length) {
      result.push({
        type: 'text',
        content: content.slice(lastIndex),
      });
    }

    return result;
  }, [content]);

  // If no mentions found, just return the content as-is
  if (parts.length === 0 || (parts.length === 1 && parts[0].type === 'text')) {
    return <span className={className}>{content}</span>;
  }

  return (
    <span className={className}>
      {parts.map((part, index) => {
        if (part.type === 'text') {
          return <span key={index}>{part.content}</span>;
        }

        // Render mention as a link
        // Note: We link to the user's profile page by username
        // The backend would need to handle username-based lookups
        return (
          <Link
            key={index}
            href={`/forum/user/username/${part.username}`}
            className="text-cyan-400 hover:text-cyan-300 hover:underline transition-colors"
            onClick={(e) => e.stopPropagation()}
          >
            @{part.username}
          </Link>
        );
      })}
    </span>
  );
}

/**
 * Utility function to check if content has any mentions
 */
export function hasMentions(content: string): boolean {
  MENTION_PATTERN.lastIndex = 0;
  return MENTION_PATTERN.test(content);
}

/**
 * Utility function to extract all mentions from content
 */
export function extractMentions(content: string): string[] {
  const mentions: string[] = [];
  let match;

  MENTION_PATTERN.lastIndex = 0;
  while ((match = MENTION_PATTERN.exec(content)) !== null) {
    mentions.push(match[1]);
  }

  return [...new Set(mentions)]; // Remove duplicates
}

