'use client';

import Link from 'next/link';

// Pattern for @mentions: @username where username is 3-30 alphanumeric or underscore chars
const MENTION_PATTERN = /@([a-zA-Z0-9_]{3,30})(?![a-zA-Z0-9_])/g;

interface MentionLinkProps {
  content: string;
  className?: string;
}

/**
 * Renders text content with @mentions as clickable links to user profiles
 */
export function MentionLink({ content, className = '' }: MentionLinkProps) {
  // Split content by mentions and interleave text with links
  const parts: (string | { type: 'mention'; username: string })[] = [];
  let lastIndex = 0;
  let match: RegExpExecArray | null;

  // Reset regex state
  MENTION_PATTERN.lastIndex = 0;

  while ((match = MENTION_PATTERN.exec(content)) !== null) {
    // Add text before the mention
    if (match.index > lastIndex) {
      parts.push(content.slice(lastIndex, match.index));
    }
    // Add the mention
    parts.push({ type: 'mention', username: match[1] });
    lastIndex = match.index + match[0].length;
  }

  // Add remaining text after last mention
  if (lastIndex < content.length) {
    parts.push(content.slice(lastIndex));
  }

  // If no mentions found, return plain text
  if (parts.length === 0) {
    return <span className={className}>{content}</span>;
  }

  return (
    <span className={className}>
      {parts.map((part, index) => {
        if (typeof part === 'string') {
          return <span key={index}>{part}</span>;
        }
        return (
          <MentionTag key={index} username={part.username} />
        );
      })}
    </span>
  );
}

interface MentionTagProps {
  username: string;
}

/**
 * Individual mention tag that links to user profile
 */
function MentionTag({ username }: MentionTagProps) {
  return (
    <Link
      href={`/forum/user/${username}`}
      className="text-cyan-400 hover:text-cyan-300 hover:underline font-medium transition-colors"
      onClick={(e) => e.stopPropagation()}
    >
      @{username}
    </Link>
  );
}

/**
 * Utility function to extract mentions from content
 */
export function extractMentions(content: string): string[] {
  if (!content) return [];

  const matches: string[] = [];
  let match: RegExpExecArray | null;

  // Reset regex state
  MENTION_PATTERN.lastIndex = 0;

  while ((match = MENTION_PATTERN.exec(content)) !== null) {
    if (!matches.includes(match[1])) {
      matches.push(match[1]);
    }
  }

  return matches;
}

