   101→  const configs: Record<RecommendationPhase, { color: string }> = {
   102→    'Surgical': { color: '#ef4444' },
   103→    'Minimally Invasive': { color: '#fbbf24' },
   104→    'Foundational': { color: '#22c55e' },
   105→  };
   106→
   107→  const config = configs[phase];
   108→  const sizeClasses = {
   109→    sm: 'px-2 py-0.5 text-[9px]',
   110→    md: 'px-2.5 py-1 text-[10px]',
   111→    lg: 'px-3 py-1.5 text-xs',
   112→  };
   113→
   114→  const labels: Record<RecommendationPhase, string> = {
   115→    'Surgical': 'SURGICAL',
   116→    'Minimally Invasive': 'MINI-INVASIVE',
   117→    'Foundational': 'FOUNDATIONAL',
   118→  };
   119→
   120→  return (
   121→    <span
   122→      className={`inline-flex items-center font-black uppercase tracking-wider rounded-lg ${sizeClasses[size]}`}
   123→      style={{
   124→        color: config.color,
   125→        backgroundColor: `${config.color}12`,
   126→        border: `1px solid ${config.color}20`,
   127→      }}
   128→    >
   129→      {labels[phase]}
   130→    </span>
   131→  );
   132→}
   133→
   134→// ============================================
   135→// SCORE CIRCLE
   136→// ============================================
   137→
   138→interface ScoreCircleProps {
   139→  score: number | string;
   140→  maxScore?: number;
   141→  size?: 'sm' | 'md' | 'lg' | 'xl';
   142→  showLabel?: boolean;
   143→  animate?: boolean;
   144→}
   145→
   146→export function ScoreCircle({
   147→  score,
   148→  maxScore = 10,
   149→  size = 'md',
   150→  showLabel = true,
   151→  animate = true,
   152→}: ScoreCircleProps) {
   153→  const numericScore = typeof score === 'number' ? score : 0;
   154→  const color = getScoreColor(numericScore);
   155→  const percentage = (numericScore / maxScore) * 100;
   156→
   157→  const sizeConfigs = {
   158→    sm: { size: 48, stroke: 4, fontSize: 'text-sm' },
   159→    md: { size: 64, stroke: 5, fontSize: 'text-lg' },
   160→    lg: { size: 96, stroke: 6, fontSize: 'text-2xl' },
   161→    xl: { size: 140, stroke: 8, fontSize: 'text-4xl' },
   162→  };
   163→
   164→  const config = sizeConfigs[size];
   165→  const radius = (config.size - config.stroke) / 2;
   166→  const circumference = radius * 2 * Math.PI;
   167→  const offset = circumference - (percentage / 100) * circumference;
   168→
   169→  return (
   170→    <div className="relative inline-flex items-center justify-center">
   171→      <svg
   172→        width={config.size}
   173→        height={config.size}
   174→        className="transform -rotate-90"
   175→      >
   176→        {/* Background circle */}
   177→        <circle
   178→          cx={config.size / 2}
   179→          cy={config.size / 2}
   180→          r={radius}
   181→          fill="none"
   182→          stroke="currentColor"
   183→          strokeWidth={config.stroke}
   184→          className="text-neutral-800"
   185→        />
   186→        {/* Progress circle */}
   187→        <motion.circle
   188→          cx={config.size / 2}
   189→          cy={config.size / 2}
   190→          r={radius}
   191→          fill="none"
   192→          stroke={color}
   193→          strokeWidth={config.stroke}
   194→          strokeLinecap="round"
   195→          strokeDasharray={circumference}
   196→          initial={animate ? { strokeDashoffset: circumference } : { strokeDashoffset: offset }}
   197→          animate={{ strokeDashoffset: offset }}
   198→          transition={{ duration: 1, ease: 'easeOut' }}
   199→        />
   200→      </svg>
   201→      {showLabel && (
   202→        <div className="absolute inset-0 flex flex-col items-center justify-center">
   203→          <motion.span
   204→            className={`font-bold ${config.fontSize}`}
   205→            style={{ color }}
   206→            initial={animate ? { opacity: 0, scale: 0.5 } : { opacity: 1, scale: 1 }}
   207→            animate={{ opacity: 1, scale: 1 }}
   208→            transition={{ duration: 0.5, delay: 0.5 }}
   209→          >
   210→            {typeof score === 'number' ? score.toFixed(1) : score}
   211→          </motion.span>
   212→          {size !== 'sm' && (
   213→            <span className="text-xs text-neutral-500">/ {maxScore}</span>
   214→          )}
   215→        </div>
   216→      )}
   217→    </div>
   218→  );
   219→}
   220→
   221→// ============================================
   222→// SCORE BAR
   223→// ============================================
   224→
   225→interface ScoreBarProps {
   226→  score: number;
   227→  maxScore?: number;
   228→  height?: number;
   229→  showValue?: boolean;
   230→  animate?: boolean;
   231→}
   232→
   233→export function ScoreBar({
   234→  score,
   235→  maxScore = 10,
   236→  height = 8,
   237→  showValue = false,
   238→  animate = true,
   239→}: ScoreBarProps) {
   240→  const color = getScoreColor(score);
   241→  const percentage = (score / maxScore) * 100;
   242→
   243→  return (
   244→    <div className="w-full">
   245→      <div
   246→        className="w-full bg-neutral-800 rounded-full overflow-hidden"
   247→        style={{ height }}
   248→      >
   249→        <motion.div
   250→          className="h-full rounded-full"
