     1→/**
     2→ * Region Detection & Link Resolution Utility
     3→ * Handles auto-detection and multi-region affiliate links
     4→ */
     5→
     6→import {
     7→  Region,
     8→  RegionConfig,
     9→  REGION_CONFIGS,
    10→  LOCALE_TO_REGION,
    11→  TIMEZONE_TO_REGION,
    12→  ProductRegionLinks,
    13→} from '@/types/guides';
    14→
    15→const STORAGE_KEY = 'looksmaxx_user_region';
    16→const DEFAULT_REGION: Region = 'us';
    17→
    18→/**
    19→ * Detection Priority:
    20→ * 1. localStorage (user preference - highest priority)
    21→ * 2. navigator.language (browser locale)
    22→ * 3. Intl.DateTimeFormat timezone (fallback)
    23→ * 4. 'us' (default fallback)
    24→ */
    25→export function detectRegion(): Region {
    26→  if (typeof window === 'undefined') {
    27→    return DEFAULT_REGION;
    28→  }
    29→
    30→  // 1. Check localStorage for user preference
    31→  try {
    32→    const stored = localStorage.getItem(STORAGE_KEY);
    33→    if (stored && isValidRegion(stored)) {
    34→      return stored as Region;
    35→    }
    36→  } catch {
    37→    // localStorage not available
    38→  }
    39→
    40→  // 2. Check browser locale
    41→  const locale = navigator.language;
    42→  if (locale && LOCALE_TO_REGION[locale]) {
    43→    return LOCALE_TO_REGION[locale];
    44→  }
    45→
    46→  // Try partial match (e.g., 'en' -> 'us')
    47→  const langCode = locale?.split('-')[0];
    48→  if (langCode) {
    49→    const partialMatch = Object.keys(LOCALE_TO_REGION).find(
    50→      (key) => key.startsWith(langCode + '-')
    51→    );
    52→    if (partialMatch) {
    53→      return LOCALE_TO_REGION[partialMatch];
    54→    }
    55→  }
    56→
    57→  // 3. Check timezone
    58→  try {
    59→    const timezone = Intl.DateTimeFormat().resolvedOptions().timeZone;
    60→    if (timezone && TIMEZONE_TO_REGION[timezone]) {
    61→      return TIMEZONE_TO_REGION[timezone];
    62→    }
    63→
    64→    // Try continent-based fallback
    65→    if (timezone) {
    66→      if (timezone.startsWith('America/')) return 'us';
    67→      if (timezone.startsWith('Europe/')) {
    68→        if (timezone.includes('London')) return 'uk';
    69→        if (timezone.includes('Paris')) return 'fr';
    70→        if (timezone.includes('Berlin')) return 'de';
    71→        return 'uk'; // Default European
    72→      }
    73→      if (timezone.startsWith('Australia/')) return 'au';
    74→      if (timezone.startsWith('Asia/')) return 'asia';
    75→    }
    76→  } catch {
    77→    // Intl not available
    78→  }
    79→
    80→  // 4. Default fallback
    81→  return DEFAULT_REGION;
    82→}
    83→
    84→/**
    85→ * Check if a region code is valid
    86→ */
    87→export function isValidRegion(region: string): region is Region {
    88→  return region in REGION_CONFIGS;
    89→}
    90→
    91→/**
    92→ * Get region configuration
