  'America/Chicago': 'us',
  'America/Denver': 'us',
  'America/Toronto': 'us',
  // Europe
  'Europe/London': 'uk',
  'Europe/Berlin': 'de',
  'Europe/Paris': 'fr',
  'Europe/Vienna': 'de',
  'Europe/Zurich': 'de',
  // Asia Pacific
  'Australia/Sydney': 'au',
  'Australia/Melbourne': 'au',
  'Australia/Brisbane': 'au',
  'Asia/Tokyo': 'asia',
  'Asia/Seoul': 'asia',
  'Asia/Shanghai': 'asia',
  'Asia/Hong_Kong': 'asia',
  'Asia/Singapore': 'asia',
};

// ============================================
// HELPER TYPES
// ============================================

export interface ProductClick {
  productId: string;
  region: Region;
  guideId?: string;
  sectionId?: string;
  timestamp: number;
}

export interface GuideView {
  guideId: string;
  region: Region;
  scrollDepth: number;  // 0-100
  timeSpentSeconds: number;
  timestamp: number;
}

