     1→'use client';
     2→
     3→import { useState, useMemo, forwardRef } from 'react';
     4→import { motion, AnimatePresence } from 'framer-motion';
     5→import {
     6→  ShoppingBag,
     7→  Package,
     8→  ExternalLink,
     9→  Search,
    10→  Sparkles,
    11→  Zap,
    12→  Shield,
    13→  Clock,
    14→  TrendingUp,
    15→  CheckCircle,
    16→  Scissors,
    17→  Droplet,
    18→  Pill,
    19→  Dumbbell,
    20→  Eye,
    21→  Smile,
    22→} from 'lucide-react';
    23→import { useResults } from '@/contexts/ResultsContext';
    24→import { useRegion } from '@/contexts/RegionContext';
    25→import { TabContent } from '../ResultsLayout';
    26→import { GUIDE_PRODUCTS } from '@/data/guides/products-registry';
    27→import { GuideProduct } from '@/types/guides';
    28→
    29→// ============================================
    30→// SECTION HEADER
    31→// ============================================
    32→
    33→function SectionHeader({ title, children }: { title: string; children?: React.ReactNode }) {
    34→  return (
    35→    <div className="flex items-center gap-4 mb-6">
    36→      <h2 className="text-[10px] font-black uppercase tracking-[0.4em] text-neutral-600 whitespace-nowrap">
    37→        {title}
    38→      </h2>
    39→      <div className="flex-1 h-px bg-neutral-800" />
    40→      {children}
    41→    </div>
    42→  );
    43→}
    44→
    45→// ============================================
    46→// CATEGORY DEFINITIONS
    47→// ============================================
    48→
    49→type ProductCategory = 'all' | 'supplements' | 'skincare' | 'tools' | 'grooming' | 'hygiene' | 'hair' | 'teeth';
    50→
    51→const CATEGORIES: { id: ProductCategory; label: string; icon: React.ReactNode }[] = [
    52→  { id: 'all', label: 'All Products', icon: <Package size={14} /> },
    53→  { id: 'supplements', label: 'Supplements', icon: <Pill size={14} /> },
    54→  { id: 'skincare', label: 'Skincare', icon: <Droplet size={14} /> },
    55→  { id: 'tools', label: 'Tools', icon: <Scissors size={14} /> },
    56→  { id: 'grooming', label: 'Grooming', icon: <Dumbbell size={14} /> },
    57→  { id: 'hygiene', label: 'Hygiene', icon: <Sparkles size={14} /> },
    58→  { id: 'hair', label: 'Hair', icon: <Eye size={14} /> },
    59→  { id: 'teeth', label: 'Teeth', icon: <Smile size={14} /> },
    60→];
    61→
    62→// Map guide product categories to our categories
    63→function mapCategory(cat: string): ProductCategory {
    64→  const mapping: Record<string, ProductCategory> = {
    65→    supplements: 'supplements',
    66→    skincare: 'skincare',
    67→    miscellaneous: 'tools',
    68→    grooming: 'grooming',
    69→    hygiene: 'hygiene',
    70→    hair: 'hair',
    71→    beard: 'grooming',
    72→    teeth: 'teeth',
    73→    kbeauty: 'skincare',
    74→    hormonal: 'supplements',
    75→    surgery: 'tools',
    76→  };
    77→  return mapping[cat] || 'all';
    78→}
    79→
    80→// ============================================
    81→// HERO BUNDLE CARD
    82→// ============================================
    83→
    84→interface HeroBundleProps {
    85→  title: string;
    86→  subtitle: string;
    87→  products: GuideProduct[];
    88→  savings: number;
    89→  gradient: string;
    90→  icon: React.ReactNode;
    91→  bundleUrl?: string;
    92→}
    93→
    94→function HeroBundleCard({ title, subtitle, products, savings, gradient, icon, bundleUrl }: HeroBundleProps) {
    95→  const { formatAmount, getLink } = useRegion();
    96→
    97→  const totalPrice = useMemo(() => {
    98→    return products.reduce((sum, p) => sum + (p.priceRange?.max || 0), 0);
    99→  }, [products]);
   100→
   101→  const bundlePrice = Math.round(totalPrice * (1 - savings / 100));
   102→
   103→  // Generate Amazon add-to-cart URL for multiple items
   104→  const generateBundleLink = () => {
   105→    if (bundleUrl) return bundleUrl;
   106→
   107→    // Build Amazon add-to-cart URL with multiple ASINs
   108→    // Format: https://www.amazon.com/gp/aws/cart/add.html?ASIN.1=XXX&Quantity.1=1&ASIN.2=YYY&Quantity.2=1...
   109→    const baseUrl = 'https://www.amazon.com/gp/aws/cart/add.html?';
   110→    const params = products
   111→      .slice(0, 4)
   112→      .map((p, idx) => {
   113→        // Extract ASIN from regionLinks.us URL
   114→        const usLink = p.regionLinks?.['us'] || '';
   115→        const asinMatch = usLink.match(/\/dp\/([A-Z0-9]{10})/);
   116→        const asin = asinMatch ? asinMatch[1] : '';
   117→        if (!asin) return '';
   118→        return `ASIN.${idx + 1}=${asin}&Quantity.${idx + 1}=1`;
   119→      })
   120→      .filter(Boolean)
   121→      .join('&');
   122→
   123→    return params ? `${baseUrl}${params}&tag=looksmaxx-20` : '#';
   124→  };
   125→
   126→  const handleBundleClick = () => {
   127→    const link = generateBundleLink();
   128→    if (link !== '#') {
   129→      window.open(link, '_blank', 'noopener,noreferrer');
   130→    }
   131→  };
   132→
   133→  return (
   134→    <motion.div
   135→      className={`rounded-[2rem] ${gradient} border border-white/10 p-6 md:p-8 relative overflow-hidden`}
   136→      initial={{ opacity: 0, y: 20 }}
   137→      animate={{ opacity: 1, y: 0 }}
   138→    >
   139→      {/* Decorative blur */}
   140→      <div className="absolute top-0 right-0 w-64 h-64 rounded-full bg-white/10 blur-[100px] -translate-y-1/2 translate-x-1/2" />
   141→
   142→      <div className="relative z-10">
   143→        <div className="flex items-start justify-between gap-4 mb-6">
   144→          <div className="flex items-center gap-4">
   145→            <div className="w-14 h-14 rounded-2xl bg-white/20 border border-white/20 flex items-center justify-center shadow-xl">
   146→              {icon}
   147→            </div>
   148→            <div>
   149→              <h3 className="text-xl font-black uppercase tracking-wider text-white">{title}</h3>
   150→              <p className="text-sm text-white/70">{subtitle}</p>
   151→            </div>
   152→          </div>
   153→          <div className="px-3 py-1.5 bg-white/20 border border-white/20 rounded-lg">
   154→            <span className="text-sm font-black text-white">Save {savings}%</span>
   155→          </div>
   156→        </div>
   157→
   158→        {/* Products grid */}
   159→        <div className="grid grid-cols-2 md:grid-cols-4 gap-3 mb-6">
   160→          {products.slice(0, 4).map((product, idx) => {
   161→            const productLink = product.regionLinks ? getLink(product.regionLinks) : '#';
   162→            return (
   163→              <a
   164→                key={product.id}
   165→                href={productLink}
   166→                target="_blank"
   167→                rel="noopener noreferrer"
   168→                className="bg-black/30 rounded-xl p-3 border border-white/10 overflow-hidden hover:border-white/30 transition-colors group"
   169→              >
   170→                <div className="flex items-center gap-2 mb-2 min-w-0">
   171→                  <div className="w-6 h-6 rounded-lg bg-white/10 flex items-center justify-center flex-shrink-0">
   172→                    <span className="text-xs font-black text-white/80">{idx + 1}</span>
   173→                  </div>
   174→                  <span className="text-[10px] font-bold uppercase tracking-wider text-white/60 truncate">{product.brand}</span>
   175→                </div>
   176→                <p className="text-sm font-bold text-white group-hover:text-cyan-300 transition-colors line-clamp-2 leading-tight">{product.name}</p>
   177→                {product.priceRange && (
   178→                  <p className="text-[10px] font-bold text-white/50 mt-1">${product.priceRange.min}-${product.priceRange.max}</p>
   179→                )}
   180→              </a>
   181→            );
   182→          })}
   183→        </div>
   184→
   185→        {/* Pricing & CTA */}
   186→        <div className="flex items-center justify-between gap-4 pt-4 border-t border-white/10">
   187→          <div>
   188→            <p className="text-[10px] font-bold uppercase tracking-wider text-white/60 mb-1">Bundle Total</p>
   189→            <div className="flex items-baseline gap-2">
   190→              <span className="text-2xl font-black text-white">{formatAmount(bundlePrice)}</span>
   191→              <span className="text-sm font-bold text-white/50 line-through">{formatAmount(totalPrice)}</span>
   192→            </div>
   193→          </div>
   194→          <button
   195→            onClick={handleBundleClick}
   196→            className="px-6 py-3 bg-white text-black text-xs font-black uppercase tracking-wider rounded-xl hover:bg-white/90 transition-colors flex items-center gap-2"
   197→          >
   198→            <ShoppingBag size={16} />
   199→            Add All to Cart
   200→          </button>
   201→        </div>
   202→      </div>
   203→    </motion.div>
   204→  );
   205→}
   206→
   207→// ============================================
   208→// PRODUCT CARD
   209→// ============================================
   210→
   211→interface ProductCardProps {
   212→  product: GuideProduct;
   213→  rank?: number;
   214→  isRecommended?: boolean;
   215→}
   216→
   217→const ProductCard = forwardRef<HTMLDivElement, ProductCardProps>(
   218→  function ProductCard({ product, rank, isRecommended }, ref) {
   219→    const { getLink, formatAmount, region } = useRegion();
   220→    const link = product.regionLinks ? getLink(product.regionLinks) : '#';
   221→
   222→    return (
   223→      <motion.div
   224→        ref={ref}
   225→        className={`rounded-2xl bg-neutral-900/40 border ${isRecommended ? 'border-cyan-500/30' : 'border-white/5'} p-5 hover:border-white/10 transition-all group`}
   226→        initial={{ opacity: 0, y: 20 }}
   227→        animate={{ opacity: 1, y: 0 }}
   228→      >
   229→        <div className="flex items-start gap-4 mb-4">
   230→          {rank && (
   231→            <div className="w-8 h-8 rounded-lg bg-neutral-900 border border-white/10 flex items-center justify-center flex-shrink-0">
   232→              <span className="text-xs font-black text-neutral-400">#{rank}</span>
   233→            </div>
   234→          )}
   235→          <div className="flex-1 min-w-0">
   236→            <div className="flex items-center gap-2 mb-1">
   237→              <span className="text-[9px] font-black uppercase tracking-wider text-neutral-500">{product.brand || 'Brand'}</span>
   238→              {isRecommended && (
   239→                <span className="px-1.5 py-0.5 bg-cyan-500/20 border border-cyan-500/30 rounded text-[8px] font-black uppercase tracking-wider text-cyan-400">
   240→                  Recommended
   241→                </span>
   242→              )}
   243→              {product.isBaseStack && (
   244→                <span className="px-1.5 py-0.5 bg-green-500/20 border border-green-500/30 rounded text-[8px] font-black uppercase tracking-wider text-green-400">
   245→                  Essential
   246→                </span>
   247→              )}
   248→            </div>
   249→            <h4 className="text-sm font-black uppercase tracking-wider text-white group-hover:text-cyan-400 transition-colors leading-tight">
   250→              {product.name}
   251→            </h4>
   252→          </div>
   253→          {product.priceRange && (
   254→            <div className="text-right flex-shrink-0">
   255→              <p className="text-sm font-black text-white">
   256→                {formatAmount(product.priceRange.min)}
   257→                {product.priceRange.max !== product.priceRange.min && (
   258→                  <span className="text-neutral-500">-{formatAmount(product.priceRange.max)}</span>
   259→                )}
   260→              </p>
   261→            </div>
   262→          )}
   263→        </div>
   264→
   265→        {product.tagline && (
   266→          <p className="text-xs text-neutral-400 mb-4 leading-relaxed line-clamp-2 italic">
   267→            &ldquo;{product.tagline}&rdquo;
   268→          </p>
   269→        )}
   270→
   271→        {product.description && !product.tagline && (
   272→          <p className="text-xs text-neutral-500 mb-4 leading-relaxed line-clamp-2">
   273→            {product.description}
   274→          </p>
   275→        )}
   276→
   277→        <a
   278→          href={!link || link === '#' ? undefined : link}
   279→          target={!link || link === '#' ? undefined : '_blank'}
   280→          rel="noopener noreferrer"
   281→          className={`
   282→            flex items-center justify-center gap-2 w-full py-2.5 rounded-xl text-[10px] font-black uppercase tracking-wider transition-colors
   283→            ${!link || link === '#'
   284→              ? 'bg-neutral-800/50 text-neutral-600 cursor-not-allowed border border-white/5'
   285→              : 'bg-neutral-800 hover:bg-neutral-700 border border-white/5 text-white'
   286→            }
   287→          `}
   288→          onClick={(e) => {
   289→            if (!link || link === '#') {
   290→              e.preventDefault();
   291→            }
   292→          }}
   293→        >
   294→          <ExternalLink size={12} />
   295→          {!link || link === '#' ? 'Unavailable' : `Buy on ${region === 'asia' ? 'Amazon' : region.toUpperCase()}`}
   296→        </a>
   297→      </motion.div>
   298→    );
   299→  }
   300→);
   301→
   302→// ============================================
   303→// STATS CARD
   304→// ============================================
   305→
   306→function StatsCard({ products }: { products: GuideProduct[] }) {
   307→  const essentialCount = products.filter(p => p.isBaseStack).length;
   308→  const categories = new Set(products.map(p => p.category)).size;
   309→
   310→  return (
   311→    <div className="rounded-[2rem] bg-neutral-900/40 border border-white/5 p-6">
   312→      <div className="grid grid-cols-3 gap-4">
   313→        <div className="text-center">
   314→          <p className="text-3xl font-black text-cyan-400">{products.length}</p>
   315→          <p className="text-[10px] font-bold uppercase tracking-wider text-neutral-500">Products</p>
   316→        </div>
   317→        <div className="text-center border-x border-white/5">
   318→          <p className="text-3xl font-black text-green-400">{essentialCount}</p>
   319→          <p className="text-[10px] font-bold uppercase tracking-wider text-neutral-500">Essential</p>
   320→        </div>
   321→        <div className="text-center">
   322→          <p className="text-3xl font-black text-purple-400">{categories}</p>
   323→          <p className="text-[10px] font-bold uppercase tracking-wider text-neutral-500">Categories</p>
   324→        </div>
   325→      </div>
   326→    </div>
   327→  );
   328→}
   329→
   330→// ============================================
   331→// SHOP TAB
   332→// ============================================
   333→
   334→export function ShopTab() {
   335→  const { gender } = useResults();
   336→  const [selectedCategory, setSelectedCategory] = useState<ProductCategory>('all');
   337→  const [searchQuery, setSearchQuery] = useState('');
   338→
   339→  // Get all products
   340→  const allProducts = useMemo(() => {
   341→    // Guide products are more complete, use those
   342→    return GUIDE_PRODUCTS.sort((a, b) => {
   343→      // Base stack items first
   344→      if (a.isBaseStack && !b.isBaseStack) return -1;
   345→      if (!a.isBaseStack && b.isBaseStack) return 1;
   346→      // Then by priority
   347→      return a.priority - b.priority;
   348→    });
   349→  }, []);
   350→
   351→  // Filter products
   352→  const filteredProducts = useMemo(() => {
   353→    let products = allProducts;
   354→
   355→    if (selectedCategory !== 'all') {
   356→      products = products.filter(p => mapCategory(p.category) === selectedCategory);
   357→    }
   358→
   359→    if (searchQuery.trim()) {
   360→      const query = searchQuery.toLowerCase();
   361→      products = products.filter(p =>
   362→        p.name.toLowerCase().includes(query) ||
   363→        p.brand?.toLowerCase().includes(query) ||
   364→        p.tagline?.toLowerCase().includes(query) ||
   365→        p.description?.toLowerCase().includes(query)
   366→      );
   367→    }
   368→
   369→    return products;
   370→  }, [allProducts, selectedCategory, searchQuery]);
   371→
   372→  // Get products for hero bundles
   373→  const starterBundle = useMemo(() => {
   374→    return allProducts.filter(p => p.isBaseStack).slice(0, 4);
   375→  }, [allProducts]);
   376→
   377→  const skinBundle = useMemo(() => {
   378→    return allProducts.filter(p => p.category === 'skincare' || p.category === 'kbeauty').slice(0, 4);
   379→  }, [allProducts]);
   380→
   381→  const maleBundle = useMemo(() => {
   382→    return allProducts.filter(p =>
   383→      p.category === 'grooming' ||
   384→      p.category === 'beard' ||
   385→      p.category === 'hair'
   386→    ).slice(0, 4);
   387→  }, [allProducts]);
   388→
   389→  // Supplements bundle
   390→  const supplementBundle = useMemo(() => {
   391→    return allProducts.filter(p => p.category === 'supplements').slice(0, 4);
   392→  }, [allProducts]);
   393→
   394→  // Hair growth bundle (minoxidil, dermaroller, etc)
   395→  const hairBundle = useMemo(() => {
   396→    return allProducts.filter(p =>
   397→      p.category === 'hair' ||
   398→      p.name.toLowerCase().includes('minox') ||
   399→      p.name.toLowerCase().includes('derma')
   400→    ).slice(0, 4);
   401→  }, [allProducts]);
   402→
   403→  // Teeth/smile bundle
   404→  const teethBundle = useMemo(() => {
   405→    return allProducts.filter(p =>
   406→      p.category === 'teeth' ||
   407→      p.name.toLowerCase().includes('whiten') ||
   408→      p.name.toLowerCase().includes('tooth') ||
   409→      p.name.toLowerCase().includes('dental')
   410→    ).slice(0, 4);
   411→  }, [allProducts]);
   412→
   413→  return (
   414→    <TabContent
   415→      title="Shop"
   416→      subtitle="Curated products to optimize your potential"
   417→    >
   418→      <div className="space-y-10">
   419→        {/* Stats */}
   420→        <StatsCard products={allProducts} />
   421→
   422→        {/* Hero Bundles */}
   423→        <section>
   424→          <SectionHeader title="Featured Bundles">
   425→            <span className="px-3 py-1.5 bg-purple-500/20 border border-purple-500/30 text-purple-400 text-[10px] font-black uppercase tracking-wider rounded-lg">
   426→              Save Up to 20%
   427→            </span>
   428→          </SectionHeader>
   429→
   430→          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
   431→            <HeroBundleCard
   432→              title="Starter Stack"
   433→              subtitle="The essentials for your enhancement journey"
   434→              products={starterBundle}
   435→              savings={15}
   436→              gradient="bg-gradient-to-br from-cyan-600 to-blue-700"
   437→              icon={<Zap size={24} className="text-white" />}
   438→            />
   439→            <HeroBundleCard
   440→              title="Skin Protocol"
   441→              subtitle="Complete skincare routine for clear skin"
   442→              products={skinBundle}
   443→              savings={20}
   444→              gradient="bg-gradient-to-br from-rose-600 to-pink-700"
   445→              icon={<Droplet size={24} className="text-white" />}
   446→            />
   447→          </div>
   448→
   449→          {/* Second row of bundles */}
   450→          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mt-6">
   451→            {supplementBundle.length >= 4 && (
   452→              <HeroBundleCard
   453→                title="Supplement Stack"
   454→                subtitle="Optimize hormones, skin, and recovery"
   455→                products={supplementBundle}
   456→                savings={15}
   457→                gradient="bg-gradient-to-br from-green-600 to-emerald-700"
   458→                icon={<Pill size={24} className="text-white" />}
   459→              />
   460→            )}
   461→            {hairBundle.length >= 3 && (
   462→              <HeroBundleCard
   463→                title="Hair Growth Protocol"
   464→                subtitle="Combat hair loss and boost density"
   465→                products={hairBundle}
   466→                savings={18}
   467→                gradient="bg-gradient-to-br from-amber-600 to-orange-700"
   468→                icon={<TrendingUp size={24} className="text-white" />}
   469→              />
   470→            )}
   471→          </div>
   472→
   473→          {/* Third row - gender specific */}
   474→          {gender === 'male' && maleBundle.length >= 4 && (
   475→            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mt-6">
   476→              <HeroBundleCard
   477→                title="Male Grooming Kit"
   478→                subtitle="Essential grooming for peak appearance"
   479→                products={maleBundle}
   480→                savings={15}
   481→                gradient="bg-gradient-to-br from-neutral-700 to-neutral-900"
   482→                icon={<Scissors size={24} className="text-white" />}
   483→              />
   484→              {teethBundle.length >= 2 && (
   485→                <HeroBundleCard
   486→                  title="Smile Makeover"
   487→                  subtitle="Whiten and perfect your smile"
   488→                  products={teethBundle}
   489→                  savings={12}
   490→                  gradient="bg-gradient-to-br from-violet-600 to-purple-700"
   491→                  icon={<Smile size={24} className="text-white" />}
   492→                />
   493→              )}
   494→            </div>
   495→          )}
   496→        </section>
   497→
   498→        {/* Why These Products */}
   499→        <section className="rounded-[2rem] bg-neutral-900/40 border border-white/5 p-6 md:p-8">
   500→          <h3 className="text-sm font-black uppercase tracking-wider text-white mb-6 flex items-center gap-3">
   501→            <div className="w-10 h-10 rounded-xl bg-neutral-900 border border-white/10 flex items-center justify-center">
   502→              <Shield size={18} className="text-cyan-400" />
   503→            </div>
   504→            Why We Recommend These Products
   505→          </h3>
   506→          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
   507→            <div className="flex items-start gap-4">
   508→              <div className="w-8 h-8 rounded-lg bg-green-500/20 border border-green-500/30 flex items-center justify-center flex-shrink-0">
   509→                <CheckCircle size={14} className="text-green-400" />
   510→              </div>
   511→              <div>
   512→                <p className="text-sm font-bold text-white mb-1">Evidence-Based</p>
   513→                <p className="text-xs text-neutral-500">Every product is backed by clinical research and real results</p>
   514→              </div>
   515→            </div>
   516→            <div className="flex items-start gap-4">
   517→              <div className="w-8 h-8 rounded-lg bg-cyan-500/20 border border-cyan-500/30 flex items-center justify-center flex-shrink-0">
   518→                <TrendingUp size={14} className="text-cyan-400" />
   519→              </div>
   520→              <div>
   521→                <p className="text-sm font-bold text-white mb-1">Results-Focused</p>
   522→                <p className="text-xs text-neutral-500">Selected for maximum impact on your facial aesthetics</p>
   523→              </div>
   524→            </div>
   525→            <div className="flex items-start gap-4">
   526→              <div className="w-8 h-8 rounded-lg bg-purple-500/20 border border-purple-500/30 flex items-center justify-center flex-shrink-0">
