    const link = product.regionLinks ? getLink(product.regionLinks) : '#';

    return (
      <motion.div
        ref={ref}
        className={`rounded-2xl bg-neutral-900/40 border ${isRecommended ? 'border-cyan-500/30' : 'border-white/5'} p-5 hover:border-white/10 transition-all group`}
