        <a
          href={!link || link === '#' ? undefined : link}
          target={!link || link === '#' ? undefined : '_blank'}
          rel="noopener noreferrer"
          className={`
            flex items-center justify-center gap-2 w-full py-2.5 rounded-xl text-[10px] font-black uppercase tracking-wider transition-colors
