/**
 * Guide System Type Definitions
 * Product guides with multi-region affiliate links
 */

// ============================================
// REGION TYPES
// ============================================

export type Region = 'us' | 'uk' | 'de' | 'fr' | 'au' | 'asia';

export interface RegionConfig {
  code: Region;
  name: string;
  flag: string;
  amazonDomain: string;
  affiliateTag: string;
  currency: string;
  currencySymbol: string;
}

/**
 * Affiliate tag environment variables with fallbacks:
 * - NEXT_PUBLIC_AFFILIATE_TAG_US (default: 'looksmaxx-20')
 * - NEXT_PUBLIC_AFFILIATE_TAG_UK (default: 'looksmaxx-21')
 * - NEXT_PUBLIC_AFFILIATE_TAG_DE (default: 'looksmaxx-21')
 * - NEXT_PUBLIC_AFFILIATE_TAG_FR (default: 'looksmaxx-21')
 * - NEXT_PUBLIC_AFFILIATE_TAG_AU (default: 'looksmaxx-22')
 * - NEXT_PUBLIC_AFFILIATE_TAG_ASIA (default: 'looksmaxx-22')
 */
const getAffiliateTag = (region: Region): string => {
  const envTags: Record<Region, string | undefined> = {
    us: process.env.NEXT_PUBLIC_AFFILIATE_TAG_US,
    uk: process.env.NEXT_PUBLIC_AFFILIATE_TAG_UK,
    de: process.env.NEXT_PUBLIC_AFFILIATE_TAG_DE,
    fr: process.env.NEXT_PUBLIC_AFFILIATE_TAG_FR,
    au: process.env.NEXT_PUBLIC_AFFILIATE_TAG_AU,
    asia: process.env.NEXT_PUBLIC_AFFILIATE_TAG_ASIA,
  };

  const defaults: Record<Region, string> = {
    us: 'looksmaxx-20',
    uk: 'looksmaxx-21',
    de: 'looksmaxx-21',
    fr: 'looksmaxx-21',
    au: 'looksmaxx-22',
    asia: 'looksmaxx-22',
  };

  return envTags[region] || defaults[region];
};

export const REGION_CONFIGS: Record<Region, RegionConfig> = {
  us: {
    code: 'us',
    name: 'United States',
    flag: '🇺🇸',
    amazonDomain: 'amazon.com',
