    severityDict?: Record<string, string>
  ): Plan[] {
    return this.getRecommendations(metricsDict, severityDict).sort(
      (a, b) => a.content.cost_min - b.content.cost_min
    );
  }

  /**
   * Get recommendations grouped by phase/tag
