"use client";

import { motion } from "framer-motion";
import { Sparkles, ArrowRight, Zap } from "lucide-react";
import clsx from "clsx";

export default function Home() {
  return (
    <main className="min-h-screen flex flex-col items-center justify-center p-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="text-center max-w-2xl"
      >
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ delay: 0.2, type: "spring", stiffness: 200 }}
          className="inline-flex items-center gap-2 px-4 py-2 rounded-full bg-background-secondary border border-border mb-8"
        >
          <Sparkles className="w-4 h-4 text-accent" />
          <span className="text-sm text-foreground-muted">Next.js 14 + Tailwind + Framer Motion</span>
        </motion.div>

        <h1 className="text-5xl font-bold mb-6 bg-gradient-to-r from-foreground to-accent bg-clip-text text-transparent">
          LOOKSMAXX
        </h1>

        <p className="text-foreground-muted text-lg mb-8">
          Your Next.js 14 project is ready with TypeScript, Tailwind CSS,
          Framer Motion, and a custom dark theme.
        </p>

        <div className="flex flex-col sm:flex-row gap-4 justify-center">
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className={clsx(
              "btn btn-primary",
              "shadow-glow"
            )}
          >
            <Zap className="w-4 h-4" />
            Get Started
          </motion.button>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="btn btn-secondary"
          >
            Learn More
            <ArrowRight className="w-4 h-4" />
          </motion.button>
        </div>
      </motion.div>

      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.8 }}
        className="mt-16 grid grid-cols-1 sm:grid-cols-3 gap-6 max-w-4xl"
      >
        {[
          { title: "Framer Motion", desc: "Smooth animations" },
          { title: "Lucide Icons", desc: "Beautiful icons" },
          { title: "Dark Theme", desc: "Easy on the eyes" },
        ].map((item, i) => (
          <motion.div
            key={item.title}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.9 + i * 0.1 }}
            className="card text-center hover:border-accent transition-colors"
          >
            <h3 className="font-semibold text-accent mb-2">{item.title}</h3>
            <p className="text-sm text-foreground-muted">{item.desc}</p>
          </motion.div>
        ))}
      </motion.div>
    </main>
  );
}
