'use client';

import { useState, useCallback } from 'react';
import { useRouter } from 'next/navigation';
import { useDropzone } from 'react-dropzone';
import { motion, AnimatePresence } from 'framer-motion';
import { Upload, X, Image as ImageIcon, ArrowRight } from 'lucide-react';

export default function UploadPage() {
  const router = useRouter();
  const [file, setFile] = useState<File | null>(null);
  const [preview, setPreview] = useState<string | null>(null);

  const onDrop = useCallback((acceptedFiles: File[]) => {
    const selectedFile = acceptedFiles[0];
    if (selectedFile) {
      setFile(selectedFile);
      const objectUrl = URL.createObjectURL(selectedFile);
      setPreview(objectUrl);
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.jpeg', '.jpg', '.png', '.webp']
    },
    maxFiles: 1,
    multiple: false
  });

  const removeImage = () => {
    if (preview) {
      URL.revokeObjectURL(preview);
    }
    setFile(null);
    setPreview(null);
  };

  const handleNext = () => {
    if (file) {
      router.push('/analysis');
    }
  };

  return (
    <main className="min-h-screen bg-black flex flex-col items-center justify-center px-4 py-12">
      {/* Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="text-center mb-10"
      >
        <h1 className="text-4xl md:text-5xl font-bold text-white mb-4">
          Upload Your Photo
        </h1>
        <p className="text-foreground-dim text-lg max-w-md mx-auto">
          Upload a clear, front-facing photo for the most accurate analysis
        </p>
      </motion.div>

      {/* Upload Container */}
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: 0.1 }}
        className="w-full max-w-xl"
      >
        <AnimatePresence mode="wait">
          {!preview ? (
            /* Dropzone */
            <motion.div
              key="dropzone"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              {...getRootProps()}
              className={`
                relative w-full aspect-[4/3] rounded-2xl border-2 border-dashed
                flex flex-col items-center justify-center gap-4
                cursor-pointer transition-all duration-300
                ${isDragActive
                  ? 'border-accent bg-accent/10 shadow-[0_0_40px_rgba(0,243,255,0.3)]'
                  : 'border-border bg-background-secondary hover:border-accent/50 hover:bg-background-tertiary'
                }
              `}
            >
              <input {...getInputProps()} />

              {/* Upload Icon */}
              <motion.div
                animate={isDragActive ? { scale: 1.1, y: -5 } : { scale: 1, y: 0 }}
                transition={{ duration: 0.2 }}
                className={`
                  p-6 rounded-full
                  ${isDragActive ? 'bg-accent/20' : 'bg-background-tertiary'}
                  transition-colors duration-300
                `}
              >
                <Upload
                  className={`
                    w-12 h-12 transition-colors duration-300
                    ${isDragActive ? 'text-accent' : 'text-foreground-dim'}
                  `}
                />
              </motion.div>

              {/* Text */}
              <div className="text-center px-4">
                <p className={`
                  text-lg font-medium transition-colors duration-300
                  ${isDragActive ? 'text-accent' : 'text-white'}
                `}>
                  {isDragActive ? 'Drop your image here' : 'Drag & drop your image here'}
                </p>
                <p className="text-foreground-dim mt-2">
                  or <span className="text-accent hover:text-accent-hover underline">browse</span> to upload
                </p>
              </div>

              {/* Supported formats */}
              <div className="flex items-center gap-2 text-sm text-foreground-dim mt-2">
                <ImageIcon className="w-4 h-4" />
                <span>JPG, PNG, WEBP supported</span>
              </div>

              {/* Decorative corners */}
              <div className="absolute top-4 left-4 w-8 h-8 border-l-2 border-t-2 border-border-light rounded-tl-lg" />
              <div className="absolute top-4 right-4 w-8 h-8 border-r-2 border-t-2 border-border-light rounded-tr-lg" />
              <div className="absolute bottom-4 left-4 w-8 h-8 border-l-2 border-b-2 border-border-light rounded-bl-lg" />
              <div className="absolute bottom-4 right-4 w-8 h-8 border-r-2 border-b-2 border-border-light rounded-br-lg" />
            </motion.div>
          ) : (
            /* Preview */
            <motion.div
              key="preview"
              initial={{ opacity: 0, scale: 0.95 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.95 }}
              className="relative w-full aspect-[4/3] rounded-2xl overflow-hidden bg-black border border-border"
            >
              {/* Image */}
              <img
                src={preview}
                alt="Preview"
                className="w-full h-full object-contain bg-black"
              />

              {/* Remove button */}
              <motion.button
                initial={{ opacity: 0, scale: 0 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ delay: 0.2 }}
                onClick={removeImage}
                className="
                  absolute top-4 right-4 p-2 rounded-full
                  bg-black/80 border border-border hover:border-error
                  text-white hover:text-error
                  transition-all duration-200
                  hover:shadow-[0_0_15px_rgba(239,68,68,0.5)]
                "
              >
                <X className="w-5 h-5" />
              </motion.button>

              {/* File info */}
              <div className="absolute bottom-4 left-4 right-4 px-4 py-3 rounded-lg bg-black/80 border border-border">
                <p className="text-white font-medium truncate">{file?.name}</p>
                <p className="text-foreground-dim text-sm">
                  {file && (file.size / 1024 / 1024).toFixed(2)} MB
                </p>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>

      {/* Next Button */}
      <motion.button
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.2 }}
        onClick={handleNext}
        disabled={!file}
        className={`
          mt-10 px-10 py-4 rounded-lg font-semibold text-lg
          flex items-center gap-3
          transition-all duration-300
          ${file
            ? 'bg-accent text-black hover:bg-accent-hover hover:shadow-[0_0_30px_rgba(0,243,255,0.5)] cursor-pointer'
            : 'bg-background-tertiary text-foreground-dim cursor-not-allowed'
          }
        `}
      >
        Next
        <ArrowRight className="w-5 h-5" />
      </motion.button>

      {/* Back link */}
      <motion.button
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5, delay: 0.3 }}
        onClick={() => router.push('/gender')}
        className="mt-6 text-foreground-dim hover:text-accent transition-colors duration-200"
      >
        ← Back to gender selection
      </motion.button>
    </main>
  );
}

