'use client';

import { useRouter } from 'next/navigation';
import { motion } from 'framer-motion';
import { ArrowLeft, ArrowRight, Check, Globe } from 'lucide-react';
import { useEthnicity, Ethnicity, ethnicityLabels } from '@/contexts/EthnicityContext';

const ethnicityOptions: Exclude<Ethnicity, null>[] = [
  'white',
  'black',
  'asian',
  'south-asian',
  'hispanic',
  'middle-eastern',
  'pacific-islander',
  'native-american',
  'mixed',
];

export default function EthnicityPage() {
  const router = useRouter();
  const { ethnicity, setEthnicity } = useEthnicity();

  const handleSelect = (selected: Ethnicity) => {
    setEthnicity(selected);
  };

  const handleContinue = () => {
    if (ethnicity) {
      router.push('/upload');
    }
  };

  const handleBack = () => {
    router.push('/gender');
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.05,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 },
  };

  return (
    <main className="min-h-screen bg-black flex flex-col items-center justify-center px-4 py-12">
      {/* Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="text-center mb-10"
      >
        <div className="flex items-center justify-center gap-3 mb-4">
          <Globe className="w-8 h-8 text-accent" />
          <h1 className="text-4xl md:text-5xl font-bold text-white">
            Select Your Ethnicity
          </h1>
        </div>
        <p className="text-foreground-dim text-lg max-w-md mx-auto">
          This helps us provide more accurate analysis based on facial structure norms
        </p>
      </motion.div>

      {/* Ethnicity Options */}
      <motion.div
        variants={containerVariants}
        initial="hidden"
        animate="visible"
        className="w-full max-w-md space-y-3 mb-10"
      >
        {ethnicityOptions.map((option) => (
          <motion.button
            key={option}
            variants={itemVariants}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => handleSelect(option)}
            className={`
              w-full h-14 rounded-xl border-2
              flex items-center justify-between px-5
              transition-all duration-300 cursor-pointer
              ${ethnicity === option
                ? 'border-accent bg-accent/10 shadow-[0_0_20px_rgba(0,243,255,0.3)]'
                : 'border-border bg-background-secondary hover:border-border-light hover:bg-background-tertiary'
              }
            `}
          >
            <span className={`
              font-medium transition-colors duration-300
              ${ethnicity === option ? 'text-accent' : 'text-white'}
            `}>
              {ethnicityLabels[option]}
            </span>

            {/* Checkmark when selected */}
            {ethnicity === option && (
              <motion.div
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                className="w-6 h-6 rounded-full bg-accent flex items-center justify-center"
              >
                <Check className="w-4 h-4 text-black" strokeWidth={3} />
              </motion.div>
            )}
          </motion.button>
        ))}
      </motion.div>

      {/* Navigation Buttons */}
      <div className="flex items-center gap-4">
        {/* Back Button */}
        <motion.button
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.3, delay: 0.3 }}
          onClick={handleBack}
          className="
            px-6 py-4 rounded-lg font-semibold
            flex items-center gap-2
            bg-background-tertiary text-foreground-dim
            hover:text-white hover:border-accent/50
            border border-border
            transition-all duration-300
          "
        >
          <ArrowLeft className="w-5 h-5" />
          Back
        </motion.button>

        {/* Continue Button */}
        <motion.button
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.3, delay: 0.3 }}
          onClick={handleContinue}
          disabled={!ethnicity}
          className={`
            px-10 py-4 rounded-lg font-semibold text-lg
            flex items-center gap-3
            transition-all duration-300
            ${ethnicity
              ? 'bg-accent text-black hover:bg-accent-hover hover:shadow-[0_0_30px_rgba(0,243,255,0.5)] cursor-pointer'
              : 'bg-background-tertiary text-foreground-dim cursor-not-allowed'
            }
          `}
        >
          Continue
          <ArrowRight className="w-5 h-5" />
        </motion.button>
      </div>
    </main>
  );
}

