'use client';

import React, { createContext, useContext, useState, ReactNode } from 'react';

export type Ethnicity =
  | 'white'
  | 'black'
  | 'asian'
  | 'south-asian'
  | 'hispanic'
  | 'middle-eastern'
  | 'pacific-islander'
  | 'native-american'
  | 'mixed'
  | null;

export const ethnicityLabels: Record<Exclude<Ethnicity, null>, string> = {
  'white': 'White / Caucasian',
  'black': 'Black / African',
  'asian': 'East Asian',
  'south-asian': 'South Asian',
  'hispanic': 'Hispanic / Latino',
  'middle-eastern': 'Middle Eastern',
  'pacific-islander': 'Pacific Islander',
  'native-american': 'Native American',
  'mixed': 'Mixed / Other',
};

interface EthnicityContextType {
  ethnicity: Ethnicity;
  setEthnicity: (ethnicity: Ethnicity) => void;
}

const EthnicityContext = createContext<EthnicityContextType | undefined>(undefined);

export function EthnicityProvider({ children }: { children: ReactNode }) {
  const [ethnicity, setEthnicity] = useState<Ethnicity>(null);

  return (
    <EthnicityContext.Provider value={{ ethnicity, setEthnicity }}>
      {children}
    </EthnicityContext.Provider>
  );
}

export function useEthnicity() {
  const context = useContext(EthnicityContext);
  if (context === undefined) {
    throw new Error('useEthnicity must be used within an EthnicityProvider');
  }
  return context;
}

