    13// Score color gradient function
    14function getScoreColorRGB(score: number): string {
    15  if (typeof score !== 'number') return 'rgb(37, 99, 235)';
    16  const clamp = Math.max(1, Math.min(10, score));
    17  const stops = [
    18    { t: 1, c: [185, 28, 28] },
    19    { t: 2, c: [220, 53, 53] },
    20    { t: 3, c: [239, 88, 88] },
    21    { t: 4, c: [245, 130, 75] },
