    26    { t: 9, c: [40, 205, 170] },
    27    { t: 10, c: [6, 182, 212] }
    28  ];
    29  let out = stops[stops.length - 1].c;
    30  for (let i = 0; i < stops.length - 1; i++) {
    31    const a = stops[i], b = stops[i + 1];
    32    if (clamp >= a.t && clamp <= b.t) {
    33      const f = (clamp - a.t) / (b.t - a.t);
    34      out = [
    35        Math.round(a.c[0] + f * (b.c[0] - a.c[0])),
    36        Math.round(a.c[1] + f * (b.c[1] - a.c[1])),
    37        Math.round(a.c[2] + f * (b.c[2] - a.c[2]))
    38      ];
    39      break;
    40    }
    41  }
    42  return `rgb(${out[0]}, ${out[1]}, ${out[2]})`;
