   244    wallRightMaterial.map = createVerticalGradientTexture('#253145', '#0d121a');
   245    const wallRight = new THREE.Mesh(new THREE.PlaneGeometry(400, 400), wallRightMaterial);
   246    wallRight.rotation.y = -Math.PI / 2;
   247    wallRight.position.set(200, 170, 0);
   248    roomGroup.add(wallRight);
   249
   250    scene.add(roomGroup);
   251
   252    // Set initial camera position
   253    camera.position.set(0, 10, 100);
   254    camera.lookAt(0, 0, 0);
   255    camera.updateProjectionMatrix();
   256
   257    // Store refs
   258    const sceneData = {
   259      scene,
   260      camera,
   261      renderer,
   262      composer,
   263      headModel: null as THREE.Group | null,
   264      roomGroup,
   265      outlinePass,
   266      bloomPass,
   267      rgbShiftPass,
   268      pointer: { x: 0, y: 0 },
   269      pointerTarget: { x: 0, y: 0 },
   270      currentScore: 0,
   271      targetScore: 4.27,
   272      currentYaw: 0,
   273      targetYaw: 0,
   274      cameraBaseZ: 50,
   275      cameraBaseY: 5,
   276      cameraTargetY: 0,
   277      gridColorsAttr,
   278      floorY
   279    };
   280    sceneRef.current = sceneData;
   281
   282    // Animation loop
   283    const animateScene = () => {
   284      const {
   285        camera: cam,
   286        composer: comp,
   287        headModel: head,
   288        roomGroup: room,
   289        outlinePass: outline,
   290        bloomPass: bloom,
   291        rgbShiftPass: rgbShift,
   292        pointer: ptr,
   293        pointerTarget: ptrTarget,
   294        cameraBaseZ: camBaseZ,
   295        cameraBaseY: camBaseY,
   296        cameraTargetY: camTargetY,
   297        gridColorsAttr: gridColors
   298      } = sceneData;
   299
   300      const t = performance.now() * 0.001;
   301
   302      // Interpolate yaw
   303      sceneData.currentYaw = THREE.MathUtils.lerp(
   304        sceneData.currentYaw,
   305        sceneData.targetYaw,
   306        0.08
   307      );
   308
   309      // Smooth pointer interpolation
   310      ptr.x += (ptrTarget.x - ptr.x) * 0.08;
   311      ptr.y += (ptrTarget.y - ptr.y) * 0.08;
   312
   313      const isMobile = window.innerWidth <= 800;
   314
   315      // Base pitch and yaw values
   316      const headBasePitch = isMobile ? -0.35 : -0.10;
   317      const headBaseYaw = isMobile ? 0.0 : 0.0;
   318      const roomBasePitch = isMobile ? -0.35 : -0.10;
   319      const roomBaseYaw = isMobile ? -0.2 : 0.0;
   320
   321      // Animate head model
   322      if (head) {
   323        head.rotation.y = headBaseYaw - sceneData.currentYaw + ptr.x * 0.1;
   324        head.rotation.x = headBasePitch + ptr.y * 0.03;
   325      }
   326
   327      // Animate room
   328      if (room) {
   329        room.rotation.y = roomBaseYaw + ptr.x * 0.08;
   330        room.rotation.x = roomBasePitch + ptr.y * 0.04;
   331      }
   332
   333      // Camera sway
   334      const sway = Math.max(0.02, Math.min(0.06, camBaseZ * 0.0004));
   335      const lift = Math.max(0.015, Math.min(0.05, camBaseZ * 0.0003));
   336      cam.position.set(
   337        ptr.x * camBaseZ * sway,
   338        camBaseY + ptr.y * camBaseZ * lift,
   339        camBaseZ
   340      );
   341      cam.lookAt(0, camTargetY, 0);
   342
   343      // Animate score
   344      sceneData.currentScore += (sceneData.targetScore - sceneData.currentScore) * 0.08;
   345      const scoreColor = getScoreColorRGB(sceneData.currentScore);
   346
   347      // Update outline color
   348      if (outline) {
   349        outline.visibleEdgeColor.setStyle(scoreColor);
   350        outline.edgeStrength = 2.0 + 1.5 * (0.5 + 0.5 * Math.sin(t * 1.2));
   351      }
   352
   353      // Update bloom
   354      if (bloom) {
   355        const targetBloom = 0.25 + Math.max(0, Math.min(1, (sceneData.currentScore - 5) / 5)) * 0.7;
   356        bloom.strength += (targetBloom - bloom.strength) * 0.05;
   357        bloom.radius = 0.4 + 0.2 * (0.5 + 0.5 * Math.sin(t * 0.6));
   358      }
   359
   360      // Update RGB shift
   361      if (rgbShift) {
   362        rgbShift.uniforms['amount'].value = 0.0012 + 0.0008 * (0.5 + 0.5 * Math.sin(t * 0.75));
   363      }
   364
   365      // Update grid colors
   366      if (gridColors) {
   367        const rgbMatch = scoreColor.match(/\d+/g);
   368        if (rgbMatch) {
   369          const r = parseInt(rgbMatch[0]) / 255;
   370          const g = parseInt(rgbMatch[1]) / 255;
   371          const b = parseInt(rgbMatch[2]) / 255;
   372          const arr = gridColors.array as Float32Array;
   373          for (let i = 0; i < arr.length; i += 3) {
   374            const wave = 0.3 + 0.7 * (0.5 + 0.5 * Math.sin(t * 0.5 + i * 0.01));
   375            arr[i] = r * wave;
   376            arr[i + 1] = g * wave;
   377            arr[i + 2] = b * wave;
   378          }
   379          gridColors.needsUpdate = true;
   380        }
   381      }
   382
   383      comp.render();
   384      animationRef.current = requestAnimationFrame(animateScene);
   385    };
   386
   387    // Event handlers
   388    const handlePointer = (e: PointerEvent) => {
   389      const rect = container.getBoundingClientRect();
   390      const nx = ((e.clientX - rect.left) / rect.width) * 2 - 1;
   391      const ny = ((e.clientY - rect.top) / rect.height) * 2 - 1;
   392      sceneData.pointerTarget.x = Math.max(-1, Math.min(1, nx));
   393      sceneData.pointerTarget.y = Math.max(-1, Math.min(1, ny));
   394    };
   395
   396    const handleLeave = () => {
   397      sceneData.pointerTarget.x = 0;
   398      sceneData.pointerTarget.y = 0;
   399    };
   400
   401    const handleResize = () => {
   402      const rect = container.getBoundingClientRect();
   403      const w = rect.width;
   404      const h = rect.height;
   405
   406      camera.aspect = w / h;
   407      camera.updateProjectionMatrix();
   408      renderer.setSize(w, h);
   409      composer.setSize(w, h);
   410    };
   411
   412    window.addEventListener('pointermove', handlePointer);
   413    window.addEventListener('pointerleave', handleLeave);
   414    window.addEventListener('resize', handleResize);
   415
   416    // Load 3D Model
   417    const loader = new GLTFLoader();
   418    loader.load('/face.glb', (gltf) => {
   419      const model = gltf.scene;
   420
   421      const meshes: THREE.Mesh[] = [];
   422      model.traverse((obj) => {
   423        if ((obj as THREE.Mesh).isMesh) {
   424          meshes.push(obj as THREE.Mesh);
   425        }
   426      });
   427
   428      meshes.forEach((mesh) => {
   429        const skinMaterial = new THREE.MeshStandardMaterial({
   430          color: 0xf5f2ed,
   431          emissive: 0xffffff,
   432          emissiveIntensity: 0.08,
   433          metalness: 0.08,
   434          roughness: 0.4,
   435          transparent: false,
   436          opacity: 1,
   437          depthWrite: true,
   438          depthTest: true,
   439          side: THREE.FrontSide
   440        });
   441
   442        skinMaterial.onBeforeCompile = (shader) => {
   443          shader.uniforms.uFresnelColor = { value: new THREE.Color(0xffffff) };
   444          shader.uniforms.uFresnelPower = { value: 1.8 };
   445          shader.uniforms.uFresnelIntensity = { value: 0.25 };
   446          shader.fragmentShader = shader.fragmentShader.replace(
   447            '#include <output_fragment>',
   448            `
   449            float fresnel = pow(1.0 - dot(normalize(vNormal), normalize(vViewPosition)), uFresnelPower);
   450            vec3 fres = uFresnelColor * fresnel * uFresnelIntensity;
   451            gl_FragColor = vec4( outgoingLight + fres, diffuseColor.a );
   452            `
   453          );
   454        };
   455        skinMaterial.needsUpdate = true;
   456
   457        mesh.material = skinMaterial;
   458        mesh.castShadow = true;
   459        mesh.receiveShadow = true;
   460
   461        if (mesh.geometry) {
   462          const overlayMaterial = new THREE.MeshBasicMaterial({
   463            color: 0xffffff,
   464            wireframe: true,
   465            transparent: true,
   466            opacity: 0.045,
   467            depthWrite: false
   468          });
   469          const overlayMesh = new THREE.Mesh(mesh.geometry, overlayMaterial);
   470          overlayMesh.renderOrder = (mesh.renderOrder || 0) + 1;
   471          mesh.add(overlayMesh);
   472        }
   473      });
   474
   475      if (meshes.length === 0) {
   476        setIsLoading(false);
   477        return;
   478      }
   479
   480      const scaleFactor = 3;
   481      model.scale.set(scaleFactor, scaleFactor, scaleFactor);
   482      model.updateMatrixWorld(true);
   483
   484      const box = new THREE.Box3().setFromObject(model);
   485      const center = box.getCenter(new THREE.Vector3());
   486
   487      const headGroup = new THREE.Group();
   488      headGroup.position.set(0, 0, 0);
   489
   490      model.position.x = -center.x;
   491      model.position.y = -center.y;
   492      model.position.z = -center.z;
   493      model.rotation.x = 0;
   494      model.updateMatrixWorld(true);
   495
   496      headGroup.add(model);
   497
   498      const finalBox = new THREE.Box3().setFromObject(headGroup);
   499      scene.add(headGroup);
   500
   501      sceneData.headModel = headGroup;
   502      sceneData.outlinePass.selectedObjects = [headGroup];
   503
   504      const sphere = new THREE.Sphere();
   505      finalBox.getBoundingSphere(sphere);
   506      const modelSphereRadius = sphere.radius;
   507
   508      const fovRad = (camera.fov * Math.PI) / 180;
   509      const baseDistance = (modelSphereRadius / Math.tan(fovRad / 2)) * 1.2;
   510
   511      const isMobile = window.innerWidth <= 800;
   512      const zMult = isMobile ? 1.5 : 1.0;
   513      const finalZ = baseDistance * zMult;
   514      const finalY = finalZ * (isMobile ? 0.44 : 0.15);
   515      const targetY = isMobile ? modelSphereRadius * 1.1 : 0;
   516
   517      sceneData.cameraBaseZ = finalZ;
   518      sceneData.cameraBaseY = finalY;
   519      sceneData.cameraTargetY = targetY;
   520
   521      camera.position.set(0, finalY, finalZ);
   522      camera.lookAt(0, targetY, 0);
   523      camera.updateProjectionMatrix();
   524
   525      setIsLoading(false);
   526    },
   527    undefined,
   528    (error) => {
   529      console.error('Error loading model:', error);
   530      setIsLoading(false);
   531    });
   532
   533    // Start animation loop
   534    animationRef.current = requestAnimationFrame(animateScene);
   535
   536    return () => {
   537      window.removeEventListener('pointermove', handlePointer);
   538      window.removeEventListener('pointerleave', handleLeave);
   539      window.removeEventListener('resize', handleResize);
   540
   541      if (animationRef.current) {
   542        cancelAnimationFrame(animationRef.current);
   543      }
   544
   545      renderer.dispose();
   546      composer.dispose();
   547    };
   548  }, []);
   549
   550  return (
   551    <div
   552      ref={containerRef}
   553      className={`relative ${className}`}
   554      style={{ width: '100%', height: '100%', minHeight: '100vh' }}
   555    >
   556      {isLoading && (
   557        <div className="absolute inset-0 flex items-center justify-center bg-black/50 z-10">
   558          <div className="w-8 h-8 border-2 border-blue-400 border-t-transparent rounded-full animate-spin" />
   559        </div>
   560      )}
   561    </div>
   562  );
   563}
