   214      const duration = 1500;
   215      const startTime = Date.now();
   216
   217      const animate = () => {
   218        const elapsed = Date.now() - startTime;
   219        const progress = Math.min(elapsed / duration, 1);
   220        const eased = 1 - Math.pow(1 - progress, 3);
   221        setDisplayScore(startScore + (targetScore - startScore) * eased);
   222
   223        if (progress < 1) {
