"use client";

import { motion } from "framer-motion";
import { useState, useEffect, useRef } from "react";
import dynamic from "next/dynamic";
import {
  ArrowRight,
  CloudUpload,
  Target,
  FileText,
  Gauge,
  Ruler,
  CheckCircle,
  TrendingUp,
  Pencil,
  Zap,
  Trophy,
  Check,
  X,
} from "lucide-react";
import Link from "next/link";


// Animation variants
const fadeIn = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0 },
};

// Rotating text words from original
const rotatingWords = ['proportions', 'symmetry', 'harmony', 'ratios', 'measurements', 'balance'];

// Advice data for floating card cycling
const adviceData = [
  { profile: 'Lateral Canthal Tilt', text: '3.96' },
  { profile: 'Facial Thirds', text: '1:1:0.92' },
  { profile: 'Eye Spacing', text: 'Ideal' },
  { profile: 'Nasal Projection', text: '0.67x' },
  { profile: 'Jaw Angle', text: '128' },
];

// ============================================================================
// NAVBAR COMPONENT
// ============================================================================
function Navbar() {
  return (
    <nav className="fixed top-0 inset-x-0 z-30 transition-all duration-300 bg-black/90 backdrop-blur-sm">
      <div className="max-w-7xl mx-auto px-6">
        <div className="flex items-center justify-between h-16">
          <Link href="/" className="flex items-center gap-2 text-sm font-semibold">
            <div className="h-5 w-5 rounded bg-blue-500/20 flex items-center justify-center">
              <span className="text-blue-400 text-xs font-bold">F</span>
            </div>
            <span>LOOKSMAXX</span>
          </Link>
          <div className="hidden md:flex items-center gap-8">
            <a href="#how-it-works" className="text-sm text-gray-300 hover:text-white transition-colors">
              How it works
            </a>
            <a href="#your-plan" className="text-sm text-gray-300 hover:text-white transition-colors">
              Your Plan
            </a>
            <a href="#pricing" className="text-sm text-gray-300 hover:text-white transition-colors">
              Pricing
            </a>
            <Link
              href="/gender"
              className="text-sm font-medium bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-500 transition-colors"
            >
              Get started
            </Link>
          </div>
          <div className="md:hidden">
            <Link
              href="/gender"
              className="text-sm font-medium bg-blue-600 text-white px-3 py-2 rounded-lg hover:bg-blue-500 transition-colors"
            >
              Get started
            </Link>
          </div>
        </div>
      </div>
    </nav>
  );
}

// ============================================================================
// ROTATING TEXT COMPONENT
// ============================================================================
function RotatingText() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentIndex((prev) => (prev + 1) % rotatingWords.length);
        setIsAnimating(false);
      }, 300);
    }, 2500);

    return () => clearInterval(timer);
  }, []);

  return (
    <span
      className="text-blue-400 inline-block"
      style={{
        transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
        minWidth: '280px',
        opacity: isAnimating ? 0 : 1,
        transform: isAnimating ? 'translateY(10px)' : 'translateY(0)',
      }}
    >
      {rotatingWords[currentIndex]}
    </span>
  );
}

// ============================================================================
// FLOATING ADVICE CARD COMPONENT
// ============================================================================
function FloatingAdviceCard() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const [yOffset, setYOffset] = useState(0);

  // Cycle through advice data
  useEffect(() => {
    const timer = setInterval(() => {
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentIndex((prev) => (prev + 1) % adviceData.length);
        setIsAnimating(false);
      }, 200);
    }, 3000);

    return () => clearInterval(timer);
  }, []);

  // Gentle floating animation
  useEffect(() => {
    let animationFrame: number;
    let startTime: number;

    const animate = (timestamp: number) => {
      if (!startTime) startTime = timestamp;
      const elapsed = (timestamp - startTime) / 1000;
      setYOffset(Math.sin(elapsed * 0.8) * 5);
      animationFrame = requestAnimationFrame(animate);
    };

    animationFrame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationFrame);
  }, []);

  const advice = adviceData[currentIndex];

  return (
    <motion.div
      initial={{ opacity: 0, x: -30 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ delay: 0.6, duration: 0.5 }}
      className="absolute top-[26%] left-[15%] lg:left-[20%] -translate-x-1/2 -translate-y-1/2 pointer-events-none z-20"
    >
      <div
        className="rounded-xl bg-white/5 backdrop-blur-xl px-4 py-3 shadow-2xl border border-white/20 transition-all duration-300"
        style={{ transform: `translateY(${yOffset}px)` }}
      >
        <div
          className="text-[10px] uppercase tracking-wider font-semibold text-gray-300 mb-1 transition-opacity duration-200"
          style={{ opacity: isAnimating ? 0 : 1 }}
        >
          {advice.profile}
        </div>
