function CTA() {
  return (
    <section className="py-20 md:py-28 px-6 bg-black text-white relative overflow-hidden">
      <div
        className="absolute inset-0 z-0 opacity-[0.03]"
        style={{
          backgroundImage: "radial-gradient(circle at 50% 50%, rgba(255,255,255,0.1) 0%, transparent 70%)",
        }}
      />
      <motion.div
        variants={fadeIn}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
        className="max-w-3xl mx-auto text-center relative z-10"
      >
        <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold tracking-tight mb-4">
          Ready to Transform Your Look?
        </h2>
        <p className="text-base md:text-lg text-gray-400 mb-8 leading-relaxed">
          Get your facial analysis and a personalized action plan to maximize your harmony score.
        </p>
        <div className="flex flex-col sm:flex-row items-center justify-center gap-4">
          <Link
            href="/gender"
            className="inline-flex items-center gap-2 font-medium bg-blue-600 text-white px-7 py-3.5 rounded-lg shadow-lg shadow-blue-500/25 hover:bg-blue-500 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-blue-500/40 transition-all hover:shadow-xl hover:shadow-blue-500/30"
          >
            Get Your Analysis
            <ArrowRight className="h-4 w-4" />
          </Link>
          <a
            href="#your-plan"
            className="inline-flex items-center font-medium text-gray-300 hover:text-white transition-colors"
          >
            See your plan
          </a>
        </div>
      </motion.div>
    </section>
  );
}

// ============================================================================
// FOOTER COMPONENT
// ============================================================================
function Footer() {
  return (
    <footer className="border-t border-white/5 py-10 px-6 bg-black">
      <div className="max-w-7xl mx-auto">
        <div className="flex flex-col sm:flex-row items-center justify-between gap-4">
          <div className="flex items-center gap-2 text-sm font-semibold">
            <div className="h-4 w-4 rounded bg-blue-500/20 flex items-center justify-center">
              <span className="text-blue-400 text-[10px] font-bold">L</span>
            </div>
            <span>LOOKSMAXX</span>
          </div>
          <div className="flex items-center gap-6 text-sm">
            <Link href="/privacy" className="text-gray-400 hover:text-white transition-colors">
              Privacy
            </Link>
            <Link href="/terms" className="text-gray-400 hover:text-white transition-colors">
              Terms
            </Link>
          </div>
          <div className="text-sm text-gray-400"> 2025 LOOKSMAXX</div>
        </div>
      </div>
    </footer>
  );
}

// ============================================================================
// MAIN PAGE COMPONENT
// ============================================================================
export default function Home() {
  return (
    <main className="bg-black text-gray-100 antialiased">
      <Navbar />
      <HeroSection />
      <HowItWorks />
      <FeatureGrid />
      <YourPlan />
      <Pricing />
      <CTA />
      <Footer />
    </main>
  );
}

