// ============================================================================
// FLOATING ADVICE CARD COMPONENT
// ============================================================================
function FloatingAdviceCard() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const [yOffset, setYOffset] = useState(0);

  // Cycle through advice data
  useEffect(() => {
    const timer = setInterval(() => {
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentIndex((prev) => (prev + 1) % adviceData.length);
        setIsAnimating(false);
      }, 200);
    }, 3000);

    return () => clearInterval(timer);
  }, []);

  // Gentle floating animation
  useEffect(() => {
    let animationFrame: number;
    let startTime: number;

    const animate = (timestamp: number) => {
      if (!startTime) startTime = timestamp;
      const elapsed = (timestamp - startTime) / 1000;
      setYOffset(Math.sin(elapsed * 0.8) * 5);
      animationFrame = requestAnimationFrame(animate);
    };

    animationFrame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationFrame);
  }, []);

  const advice = adviceData[currentIndex];

  return (
    <motion.div
      initial={{ opacity: 0, x: -30 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ delay: 0.6, duration: 0.5 }}
      className="absolute top-[26%] left-[15%] lg:left-[20%] -translate-x-1/2 -translate-y-1/2 pointer-events-none z-20"
    >
      <div
        className="rounded-xl bg-white/5 backdrop-blur-xl px-4 py-3 shadow-2xl border border-white/20 transition-all duration-300"
        style={{ transform: `translateY(${yOffset}px)` }}
      >
        <div
          className="text-[10px] uppercase tracking-wider font-semibold text-gray-300 mb-1 transition-opacity duration-200"
          style={{ opacity: isAnimating ? 0 : 1 }}
        >
          {advice.profile}
        </div>
        <div
          className="text-lg font-semibold text-gray-100 leading-tight transition-opacity duration-200"
          style={{ opacity: isAnimating ? 0 : 1 }}
        >
          {advice.text}
        </div>
      </div>
    </motion.div>
  );
}

// ============================================================================
// FLOATING SCORE CARD COMPONENT
// ============================================================================
// Score values to cycle through for continuous animation
const scoreValues = [4.27, 6.42, 7.85, 5.91, 8.12, 3.56];

function FloatingScoreCard() {
  const [yOffset, setYOffset] = useState(0);
  const [displayScore, setDisplayScore] = useState(0);
  const scoreIndexRef = useRef(0);

  // Animate score cycling continuously
  useEffect(() => {
    let animationFrame: number;
    let timeoutId: NodeJS.Timeout;

    const animateToScore = (targetScore: number, startScore: number, onComplete: () => void) => {
      const duration = 1500;
      const startTime = Date.now();

      const animate = () => {
        const elapsed = Date.now() - startTime;
        const progress = Math.min(elapsed / duration, 1);
        const eased = 1 - Math.pow(1 - progress, 3);
        setDisplayScore(startScore + (targetScore - startScore) * eased);

        if (progress < 1) {
          animationFrame = requestAnimationFrame(animate);
        } else {
          onComplete();
        }
      };

      animationFrame = requestAnimationFrame(animate);
    };

    const cycleScore = () => {
      const currentIndex = scoreIndexRef.current;
      const nextIndex = (currentIndex + 1) % scoreValues.length;
      const currentScore = scoreValues[currentIndex];
      const nextScore = scoreValues[nextIndex];

      scoreIndexRef.current = nextIndex;

      animateToScore(nextScore, currentScore, () => {
        timeoutId = setTimeout(cycleScore, 2000);
      });
    };

    // Initial animation from 0 to first score
    const initialTimeout = setTimeout(() => {
      animateToScore(scoreValues[0], 0, () => {
        timeoutId = setTimeout(cycleScore, 2000);
      });
    }, 800);

    return () => {
      clearTimeout(initialTimeout);
      clearTimeout(timeoutId);
      cancelAnimationFrame(animationFrame);
    };
  }, []);

  // Gentle floating animation
  useEffect(() => {
    let animationFrame: number;
    let startTime: number;

    const animate = (timestamp: number) => {
      if (!startTime) startTime = timestamp;
      const elapsed = (timestamp - startTime) / 1000;
      setYOffset(Math.sin(elapsed * 0.6 + 1) * 6);
      animationFrame = requestAnimationFrame(animate);
    };

    animationFrame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationFrame);
  }, []);

  // Color interpolation based on score
  const getScoreColor = (score: number) => {
    if (score < 3) return 'rgb(239, 68, 68)';
    if (score < 5) return 'rgb(247, 142, 71)';
    if (score < 7) return 'rgb(234, 179, 8)';
    return 'rgb(34, 197, 94)';
  };

  return (
    <motion.div
      initial={{ opacity: 0, x: 30 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ delay: 0.8, duration: 0.5 }}
      className="absolute bottom-[30%] right-[5%] lg:right-[10%] translate-y-1/2 pointer-events-none z-20"
    >
      <div
        className="rounded-xl bg-white/5 backdrop-blur-xl px-5 py-4 shadow-2xl border border-white/20 transition-all duration-300"
        style={{ transform: `translateY(${yOffset}px)` }}
      >
        <div className="text-[10px] uppercase tracking-wider font-semibold text-gray-300 mb-1.5">
          Overall Score
        </div>
        <div className="flex items-baseline gap-0.5">
          <span
            className="text-4xl font-bold tabular-nums transition-colors duration-300"
            style={{ color: getScoreColor(displayScore) }}
          >
            {displayScore.toFixed(2)}
          </span>
          <span className="text-xl font-medium text-gray-400">/10</span>
        </div>
      </div>
    </motion.div>
  );
}

