    { label: "Projected", value: "7.85", color: "emerald", percent: 78.5, rank: "Top 8%", gain: "+1.43" },
    { label: "Realistic Max", value: "8.12", color: "blue", percent: 81.2, rank: "Top 4%" },
    { label: "Potential", value: "8.94", color: "purple", percent: 89.4, rank: "Top 1%" },
  ];

  const timeline = [
    { step: 1, title: "Mewing & Posture", gain: "+0.42", desc: "Daily exercises  3-6 months", color: "emerald" },
    { step: 2, title: "Skincare Routine", gain: "+0.28", desc: "Daily routine  2-4 months", color: "blue" },
    { step: 3, title: "Eyebrow Grooming", gain: "+0.35", desc: "Professional shaping  Immediate", color: "purple" },
  ];

  const flaws = [
    { name: "Recessed Chin", color: "orange" },
    { name: "Uneven Eyebrows", color: "yellow" },
    { name: "Poor Skin Texture", color: "red" },
  ];

  return (
    <section id="your-plan" className="py-20 md:py-28 px-6 bg-black relative overflow-hidden">
      <div
        className="absolute inset-0 opacity-[0.02]"
        style={{
          backgroundImage:
            "linear-gradient(135deg, rgba(16,185,129,0.3) 0%, transparent 50%, rgba(0,243,255,0.2) 100%)",
        }}
      />
      <div className="max-w-7xl mx-auto relative">
        <motion.div
          variants={fadeIn}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          className="text-center max-w-2xl mx-auto mb-14"
        >
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold tracking-tight mb-4 text-white">
            Your Personalized Plan
          </h2>
          <p className="text-base md:text-lg text-gray-400 leading-relaxed">
            Get a step-by-step roadmap tailored to your unique facial features with projected
            improvements
          </p>
        </motion.div>

        {/* Score Projections */}
        <motion.div
          variants={fadeIn}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          className="grid grid-cols-2 lg:grid-cols-4 gap-3 sm:gap-4 mb-8"
        >
          {projections.map((proj) => (
            <div
              key={proj.label}
              className="relative p-4 sm:p-5 rounded-xl border bg-black/90 overflow-hidden"
              style={{
                borderColor: `rgba(var(--${proj.color}-rgb), 0.2)`,
                backgroundColor: `rgba(var(--${proj.color}-rgb), 0.03)`,
              }}
            >
              <div className="flex items-center gap-1.5 mb-2">
                {proj.label === "Projected" && <Target className="w-3.5 h-3.5 text-emerald-400 hidden sm:block" />}
                {proj.label === "Realistic Max" && <Zap className="w-3.5 h-3.5 text-blue-400 hidden sm:block" />}
                {proj.label === "Potential" && <Trophy className="w-3.5 h-3.5 text-purple-400 hidden sm:block" />}
                <span className="text-xs sm:text-sm font-medium text-gray-400">{proj.label}</span>
              </div>
              <div className="flex items-baseline gap-2">
                <span
                  className={`text-2xl sm:text-3xl font-bold ${
                    proj.color === "yellow"
                      ? "text-yellow-400"
                      : proj.color === "emerald"
                        ? "text-emerald-400"
                        : proj.color === "blue"
                          ? "text-blue-400"
                          : "text-purple-400"
                  }`}
                >
                  {proj.value}
                </span>
                {proj.gain && (
                  <span className="text-[10px] sm:text-xs font-semibold px-1.5 py-0.5 rounded-full bg-emerald-500/10 text-emerald-400">
                    {proj.gain}
                  </span>
                )}
              </div>
              <div
                className={`h-1.5 w-full rounded-full overflow-hidden mt-2 ${
                  proj.color === "yellow"
                    ? "bg-yellow-500/15"
                    : proj.color === "emerald"
                      ? "bg-emerald-500/15"
                      : proj.color === "blue"
                        ? "bg-blue-500/15"
                        : "bg-purple-500/15"
                }`}
              >
                <div
                  className={`h-full rounded-full ${
                    proj.color === "yellow"
                      ? "bg-yellow-400"
                      : proj.color === "emerald"
                        ? "bg-emerald-400"
                        : proj.color === "blue"
                          ? "bg-blue-500"
                          : "bg-purple-400"
                  }`}
                  style={{ width: `${proj.percent}%` }}
                />
              </div>
              <div className="text-[10px] sm:text-xs text-gray-500 mt-2">{proj.rank}</div>
            </div>
          ))}
        </motion.div>

        {/* Timeline & Flaws */}
        <div className="grid lg:grid-cols-2 gap-6">
          {/* Timeline */}
          <motion.div
            variants={fadeIn}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
            className="bg-black/80 border border-gray-800 rounded-xl p-6"
          >
            <div className="flex items-center gap-3 mb-5">
              <div className="w-10 h-10 rounded-lg bg-emerald-500/10 border border-emerald-500/20 flex items-center justify-center">
                <Pencil className="w-5 h-5 text-emerald-400" />
              </div>
              <div>
                <h3 className="text-lg font-semibold text-white">Actionable Timeline</h3>
                <p className="text-sm text-gray-400">Step-by-step roadmap to your goals</p>
              </div>
            </div>

            <div className="space-y-3">
              {timeline.map((item) => (
                <div
                  key={item.step}
                  className={`flex items-start gap-3 p-3 bg-black/50 border rounded-lg ${
                    item.color === "emerald"
                      ? "border-emerald-500/20"
                      : item.color === "blue"
                        ? "border-blue-500/20"
                        : "border-purple-500/20"
                  }`}
                >
                  <div
                    className={`w-6 h-6 rounded-full flex items-center justify-center flex-shrink-0 mt-0.5 ${
                      item.color === "emerald"
                        ? "bg-emerald-500/20"
                        : item.color === "blue"
                          ? "bg-blue-500/20"
                          : "bg-purple-500/20"
                    }`}
                  >
                    <span
                      className={`text-[10px] font-bold ${
                        item.color === "emerald"
                          ? "text-emerald-400"
                          : item.color === "blue"
                            ? "text-blue-400"
                            : "text-purple-400"
                      }`}
                    >
                      {item.step}
                    </span>
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center justify-between mb-1">
                      <span className="text-sm font-medium text-white">{item.title}</span>
                      <span
                        className={`text-[10px] px-2 py-0.5 rounded-full ${
                          item.color === "emerald"
                            ? "bg-emerald-500/20 text-emerald-300"
                            : item.color === "blue"
                              ? "bg-blue-500/20 text-blue-400"
                              : "bg-purple-500/20 text-purple-300"
                        }`}
                      >
                        {item.gain}
                      </span>
                    </div>
                    <p className="text-xs text-gray-500">{item.desc}</p>
                  </div>
                </div>
              ))}
              <div className="pt-2 text-center">
                <span className="text-xs text-gray-500">+5 more personalized actions</span>
              </div>
            </div>
          </motion.div>

          {/* Flaws Addressed */}
          <motion.div
            variants={fadeIn}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
            transition={{ delay: 0.1 }}
            className="bg-black/80 border border-gray-800 rounded-xl p-6"
          >
            <div className="flex items-center gap-3 mb-5">
              <div className="w-10 h-10 rounded-lg bg-blue-500/10 border border-blue-500/20 flex items-center justify-center">
                <CheckCircle className="w-5 h-5 text-blue-400" />
              </div>
              <div>
                <h3 className="text-lg font-semibold text-white">Flaws Addressed</h3>
                <p className="text-sm text-gray-400">Issues your plan will improve</p>
              </div>
            </div>

            <div className="space-y-3">
              {flaws.map((flaw) => (
                <div
                  key={flaw.name}
                  className={`flex items-center justify-between p-3 bg-black/50 border rounded-lg ${
                    flaw.color === "orange"
                      ? "border-orange-500/20"
                      : flaw.color === "yellow"
                        ? "border-yellow-500/20"
                        : "border-red-500/20"
                  }`}
                >
                  <div className="flex items-center gap-2">
                    <div
                      className={`w-2 h-2 rounded-full ${
                        flaw.color === "orange"
                          ? "bg-orange-400"
                          : flaw.color === "yellow"
                            ? "bg-yellow-400"
                            : "bg-red-400"
                      }`}
                    />
                    <span className="text-sm text-gray-300">{flaw.name}</span>
                  </div>
                  <div className="flex items-center gap-1.5">
                    <Check className="w-3 h-3 text-emerald-400" />
                    <span className="text-xs text-emerald-400">Addressed</span>
                  </div>
                </div>
              ))}

              <div className="mt-4 p-3 bg-emerald-500/5 border border-emerald-500/20 rounded-lg">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-gray-300">Flaws Covered</span>
                  <span className="text-lg font-bold text-emerald-400">6/8</span>
                </div>
                <div className="h-1.5 w-full rounded-full overflow-hidden bg-emerald-500/15 mt-2">
                  <div className="h-full rounded-full bg-emerald-400" style={{ width: "75%" }} />
                </div>
              </div>
            </div>
          </motion.div>
        </div>

        {/* CTA */}
        <motion.div
          variants={fadeIn}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          className="mt-10 text-center"
        >
          <Link
            href="/gender"
            className="inline-flex items-center gap-2 font-medium bg-emerald-600 text-white px-7 py-3.5 rounded-lg shadow-lg shadow-emerald-500/20 hover:bg-emerald-500 transition-all"
          >
            Build Your Plan
            <ArrowRight className="w-4 h-4" />
          </Link>
        </motion.div>
      </div>
    </section>
  );
}

// ============================================================================
// PRICING COMPONENT
// ============================================================================
function Pricing() {
  const plans = [
    {
      name: "Free",
      price: "$0",
      period: "forever",
      features: [
        { text: "10 Free Facial Ratios", included: true },
        { text: "2 Free Strengths & Flaws", included: true },
        { text: "Side Profile Score", included: true },
        { text: "Limited Insights", included: true },
        { text: "Overall Harmony Score", included: false },
        { text: "Your Plan", included: false },
      ],
      highlighted: false,
      order: "lg:order-1",
    },
    {
      name: "Pro",
      price: "$49.99",
      period: "/month",
      badge: "Best value",
      features: [
        { text: "60+ Facial Ratios", included: true },
        { text: "Overall Harmony Score", included: true },
        { text: "500 Harmony Analysis Credits", included: true },
        { text: "Full Strength & Flaws Analysis", included: true },
        { text: "Scoring guide charts", included: true },
        { text: "Compare Analysis", included: true },
        { text: "Your Plan (All Treatment Options)", included: true, highlight: true },
        { text: "Your Potential Score Projection", included: true },
        { text: "Surgical Treatment Options", included: true },
        { text: "Early access to beta features", included: true },
      ],
      highlighted: true,
      order: "lg:order-2",
    },
    {
      name: "Basic",
      price: "$24.99",
      period: "/month",
      features: [
        { text: "60+ Facial Ratios", included: true },
        { text: "Overall Harmony Score", included: true },
        { text: "25 Harmony Analysis Credits", included: true },
        { text: "Full Strength & Flaws Analysis", included: true },
        { text: "Scoring guide charts", included: true },
        { text: "Your Plan (Non-Surgical)", included: true, highlight: true },
      ],
      highlighted: false,
      order: "lg:order-3",
    },
  ];

  return (
    <section id="pricing" className="py-20 md:py-28 px-6 scroll-mt-16 bg-black">
      <div className="max-w-7xl mx-auto">
        <motion.div
          variants={fadeIn}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          className="text-center max-w-2xl mx-auto mb-14"
        >
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold tracking-tight mb-4 text-white">
            Pricing
          </h2>
          <p className="text-base md:text-lg text-gray-400 leading-relaxed">
            Choose the plan that works for you. Change or cancel anytime.
          </p>
        </motion.div>

        <div className="grid lg:grid-cols-3 gap-5 items-start">
          {plans.map((plan, index) => (
            <motion.div
              key={plan.name}
              variants={fadeIn}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
              transition={{ delay: index * 0.1 }}
              className={`relative bg-black/70 rounded-xl p-7 h-full flex flex-col transition-all duration-300 ${plan.order} ${
                plan.highlighted
                  ? "border-2 border-blue-500/50 shadow-xl shadow-blue-500/15 hover:border-blue-500/70 hover:shadow-blue-500/25"
                  : "border border-gray-800 hover:border-gray-700"
              }`}
            >
              {plan.badge && (
                <div className="absolute top-0 right-6 -mt-2.5 bg-gradient-to-r from-blue-500 to-blue-400 text-white text-[10px] font-bold uppercase tracking-wider px-2.5 py-1.5 rounded-full">
                  {plan.badge}
                </div>
              )}
              <h3 className="text-lg font-semibold mb-2.5 text-white">{plan.name}</h3>
              <div className="mb-7">
                <span className="text-4xl font-bold text-white">{plan.price}</span>
                <span className={`text-base ml-2 ${plan.highlighted ? "text-blue-400/80" : "text-gray-400"}`}>
                  {plan.period}
                </span>
              </div>
              <ul className={`space-y-3 text-sm mb-7 flex-grow ${plan.highlighted ? "text-gray-200" : "text-gray-300"}`}>
                {plan.features.map((feature) => (
                  <li key={feature.text} className="flex items-start gap-3">
                    {feature.included ? (
                      <Check
                        className={`h-5 w-5 flex-shrink-0 mt-0.5 ${
                          feature.highlight ? "text-emerald-400" : "text-blue-400"
                        }`}
                      />
                    ) : (
                      <X className="h-5 w-5 text-gray-500 flex-shrink-0 mt-0.5" />
                    )}
                    <span className={feature.included ? (feature.highlight ? "text-emerald-300" : "") : "text-gray-500"}>
                      {feature.text}
                    </span>
                  </li>
                ))}
              </ul>
              <Link
                href="/gender"
                className={`block w-full text-center font-medium rounded-lg px-4 py-2.5 transition-colors ${
                  plan.highlighted
                    ? "bg-blue-600 text-white hover:bg-blue-500 shadow-lg shadow-blue-500/20"
                    : "bg-gray-800 border border-gray-700 text-white hover:bg-gray-700"
                }`}
              >
                Get started
              </Link>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}

// ============================================================================
// CTA COMPONENT
// ============================================================================
function CTA() {
  return (
    <section className="py-20 md:py-28 px-6 bg-black text-white relative overflow-hidden">
      <div
        className="absolute inset-0 z-0 opacity-[0.03]"
        style={{
          backgroundImage: "radial-gradient(circle at 50% 50%, rgba(255,255,255,0.1) 0%, transparent 70%)",
        }}
      />
      <motion.div
        variants={fadeIn}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
        className="max-w-3xl mx-auto text-center relative z-10"
      >
        <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold tracking-tight mb-4">
          Ready to Transform Your Look?
        </h2>
        <p className="text-base md:text-lg text-gray-400 mb-8 leading-relaxed">
          Get your facial analysis and a personalized action plan to maximize your harmony score.
        </p>
        <div className="flex flex-col sm:flex-row items-center justify-center gap-4">
          <Link
            href="/gender"
            className="inline-flex items-center gap-2 font-medium bg-blue-600 text-white px-7 py-3.5 rounded-lg shadow-lg shadow-blue-500/25 hover:bg-blue-500 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-blue-500/40 transition-all hover:shadow-xl hover:shadow-blue-500/30"
          >
            Get Your Analysis
            <ArrowRight className="h-4 w-4" />
          </Link>
          <a
            href="#your-plan"
            className="inline-flex items-center font-medium text-gray-300 hover:text-white transition-colors"
          >
            See your plan
          </a>
        </div>
      </motion.div>
    </section>
  );
}

// ============================================================================
// FOOTER COMPONENT
// ============================================================================
function Footer() {
  return (
    <footer className="border-t border-white/5 py-10 px-6 bg-black">
      <div className="max-w-7xl mx-auto">
        <div className="flex flex-col sm:flex-row items-center justify-between gap-4">
          <div className="flex items-center gap-2 text-sm font-semibold">
            <div className="h-4 w-4 rounded bg-blue-500/20 flex items-center justify-center">
              <span className="text-blue-400 text-[10px] font-bold">L</span>
            </div>
            <span>LOOKSMAXX</span>
          </div>
          <div className="flex items-center gap-6 text-sm">
            <Link href="/privacy" className="text-gray-400 hover:text-white transition-colors">
              Privacy
            </Link>
            <Link href="/terms" className="text-gray-400 hover:text-white transition-colors">
              Terms
            </Link>
          </div>
          <div className="text-sm text-gray-400"> 2025 LOOKSMAXX</div>
        </div>
      </div>
    </footer>
  );
}

// ============================================================================
// MAIN PAGE COMPONENT
// ============================================================================
export default function Home() {
  return (
    <main className="bg-black text-gray-100 antialiased">
      <Navbar />
      <HeroSection />
      <HowItWorks />
      <FeatureGrid />
      <YourPlan />
      <Pricing />
      <CTA />
      <Footer />
    </main>
  );
}

