html {
    scroll-behavior: smooth;
}

body {
    overflow-x: hidden;
    background: #0d1117 !important;
}

.fade-in {
    opacity: 0;
    transform: translateY(20px);
    transition: opacity 0.6s ease, transform 0.6s ease;
}

.fade-in.visible {
    opacity: 1;
    transform: translateY(0);
}

#head3d {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    min-height: max(100vh, 720px);
    z-index: 0;
    pointer-events: none;
    transform: translate3d(0, 0, 0);
    will-change: transform;
    contain: layout style paint;
}

#panel-1 {
    min-height: max(100vh, 720px);
    height: 100vh;
}

#panel-1>div>div:last-child {
    height: 100vh;
    min-height: max(100vh, 720px);
    position: relative;
}

#head3d>canvas {
    position: absolute !important;
    inset: 0 !important;
    width: 100% !important;
    height: 100% !important;
    z-index: 0;
    display: block;
    transform: translate3d(0, 0, 0);
    will-change: transform;
    backface-visibility: hidden;
}

#overlayCanvas {
    position: absolute !important;
    inset: 0 !important;
    pointer-events: none !important;
    z-index: 1 !important;
    transform: translate3d(0, 0, 0);
    will-change: contents;
    backface-visibility: hidden;
}

#vignetteOverlay {
    position: absolute !important;
    top: 0 !important;
    bottom: 0 !important;
    left: 0 !important;
    width: 100% !important;
    pointer-events: none !important;
    z-index: 2 !important;
    opacity: 0.8 !important;
    transform: translate3d(0, 0, 0);
    will-change: opacity;
    background:
        linear-gradient(to right, rgba(0, 0, 0, 0.65) 0%, rgba(0, 0, 0, 0.5) 20%, rgba(0, 0, 0, 0.35) 35%, transparent 55%),
        linear-gradient(to top, #0d1117 0%, transparent 40%),
        linear-gradient(to bottom, rgba(0, 0, 0, 0.2) 0%, transparent 30%),
        radial-gradient(ellipse at 55% 50%, transparent 35%, rgba(0, 0, 0, 0.18) 60%, rgba(0, 0, 0, 0.35) 82%, rgba(0, 0, 0, 0.5) 100%);
}

@media (max-width: 400px) {

    /* Small mobile styles - smaller text */
    #panel-1 {
        height: 100vh !important;
        min-height: max(100vh, 720px) !important;
    }

    #panel-1>div>div:last-child {
        height: 100vh !important;
        min-height: max(100vh, 720px) !important;
    }

    #head3d {
        width: 100vw !important;
        height: 100vh !important;
        min-height: max(100vh, 720px) !important;
        left: -1.5rem !important;
    }

    #vignetteOverlay {
        opacity: 0.6 !important;
    }

    #heroText {
        position: absolute !important;
        top: 2rem !important;
        left: 0;
        right: 0;
        padding-left: 1.25rem !important;
        padding-right: 1.25rem !important;
        z-index: 30;
    }

    #heroText h1 {
        font-size: 1.75rem !important;
        line-height: 1.2 !important;
        margin-bottom: 0.75rem !important;
    }

    #heroText p {
        font-size: 0.875rem !important;
        margin-bottom: 1rem !important;
    }

    #heroText a[href="/login"] {
        font-size: 0.875rem !important;
        padding: 0.5rem 1rem !important;
    }

    #heroText a[href="#panel-4"] {
        font-size: 0.875rem !important;
    }

    #adviceContainer {
        top: 60% !important;
        left: 1.5rem !important;
        right: auto !important;
        bottom: auto !important;
        transform: translateY(-50%) !important;
    }

    #adviceCard {
        padding: 0.5rem 0.75rem !important;
    }

    #adviceCard #adviceText {
        font-size: 0.95rem !important;
    }

    #scoreContainer {
        top: 92% !important;
        left: auto !important;
        right: 1.5rem !important;
        bottom: auto !important;
        transform: translateY(-50%) !important;
    }

    #scoreCard {
        padding: 0.5rem 0.75rem !important;
    }

    #scoreCard #scoreValue {
        font-size: 1.75rem !important;
    }

    #scoreCard span.text-base,
    #scoreCard span.text-xl {
        font-size: 0.95rem !important;
    }
}

@media (min-width: 401px) and (max-width: 799px) {

    /* Larger mobile styles - 401px to 800px */
    #panel-1 {
        height: 100vh !important;
        min-height: max(100vh, 720px) !important;
    }

    #panel-1>div>div:last-child {
        height: 100vh !important;
        min-height: max(100vh, 720px) !important;
    }

    #head3d {
        width: 100vw !important;
        height: 100vh !important;
        min-height: max(100vh, 720px) !important;
        left: -1.5rem !important;
    }

    #vignetteOverlay {
        opacity: 0.6 !important;
    }

    nav>div {
        max-width: 500px !important;
    }

    #heroText {
        position: absolute !important;
        top: 2rem !important;
        left: 0;
        right: 0;
        padding-left: 0 !important;
        padding-right: 0 !important;
        z-index: 30;
    }

    #heroText>div {
        max-width: 500px !important;
        margin-left: auto !important;
        margin-right: auto !important;
        padding-left: 1.5rem !important;
        padding-right: 1.5rem !important;
    }

    #heroText h1 {
        font-size: 2.25rem !important;
        line-height: 1.2 !important;
        margin-bottom: 1rem !important;
    }

    #heroText p {
        font-size: 1rem !important;
        margin-bottom: 1.25rem !important;
    }

    #heroText a[href="/login"] {
        font-size: 1rem !important;
        padding: 0.625rem 1.5rem !important;
    }

    #heroText a[href="#panel-4"] {
        font-size: 1rem !important;
    }

    #heroText>div>div.flex {
        flex-direction: column !important;
        align-items: stretch !important;
    }

    #adviceContainer {
        top: 60% !important;
        left: 1.5rem !important;
        right: auto !important;
        bottom: auto !important;
        transform: translateY(-50%) !important;
        max-width: calc(100% - 3rem) !important;
    }

    #adviceContainer>div {
        max-width: 100% !important;
    }

    #scoreContainer {
        top: 92% !important;
        right: 1.5rem !important;
        left: auto !important;
        bottom: auto !important;
        transform: translateY(-50%) !important;
        max-width: calc(100% - 3rem) !important;
    }

    #scoreContainer>div {
        max-width: 100% !important;
        margin-left: auto !important;
    }

    #adviceCard {
        padding: 0.5rem 0.75rem !important;
    }

    #adviceCard #adviceText {
        font-size: 0.95rem !important;
    }

    #scoreCard {
        padding: 0.5rem 0.75rem !important;
    }

    #scoreCard #scoreValue {
        font-size: 1.75rem !important;
    }

    #scoreCard span.text-base,
    #scoreCard span.text-xl {
        font-size: 0.95rem !important;
    }
}

@media (min-width: 500px) and (max-width: 799px) {
    #adviceContainer {
        top: 60% !important;
        left: calc(50% - 250px) !important;
        right: auto !important;
        bottom: auto !important;
        transform: translateY(-50%) !important;
        max-width: 500px !important;
    }

    #adviceContainer>div {
        max-width: 500px !important;
    }

    #scoreContainer {
        top: 92% !important;
        right: calc(50% - 250px) !important;
        left: auto !important;
        bottom: auto !important;
        transform: translateY(-50%) !important;
        max-width: 500px !important;
    }

    #scoreContainer>div {
        max-width: 500px !important;
        margin-left: auto !important;
    }
}

@media (min-width: 800px) {
    .nav-links {
        display: flex !important;
    }

    .mobile-nav-button {
        display: none !important;
    }
}

@media (max-width: 799px) {
    .nav-links {
        display: none !important;
    }

    .mobile-nav-button {
        display: block !important;
    }
}

@media (min-width: 700px) and (max-width: 1024px) {
    #panel-1 {
        height: 100vh !important;
        min-height: max(100vh, 720px) !important;
    }

    #panel-1>div>div:last-child {
        height: 100vh !important;
        min-height: max(100vh, 720px) !important;
    }

    #head3d {
        height: 100% !important;
        min-height: max(100vh, 720px) !important;
    }
}

@media (min-width: 800px) and (max-width: 1023px) {

    /* Tablet styles - side-by-side layout with smaller text */
    #panel-1 {
        height: 100vh !important;
        min-height: max(100vh, 720px) !important;
    }

    #panel-1>div {
        flex-direction: row !important;
    }

    #panel-1>div>div:last-child {
        height: 100vh !important;
        min-height: max(100vh, 720px) !important;
    }

    #head3d {
        height: 100% !important;
        min-height: max(100vh, 720px) !important;
    }

    #heroText {
        width: 50% !important;
        position: relative !important;
        order: 1 !important;
        padding-top: 2rem !important;
    }

    #heroText>div {
        max-width: 100% !important;
        text-align: left !important;
    }

    #heroText h1 {
        font-size: 2rem !important;
        line-height: 1.2 !important;
        margin-bottom: 0.875rem !important;
    }

    #heroText p {
        font-size: 0.9375rem !important;
        margin-bottom: 1.25rem !important;
    }

    #heroText a[href="/login"] {
        font-size: 0.9375rem !important;
        padding: 0.625rem 1.25rem !important;
    }

    #heroText a[href="#panel-4"] {
        font-size: 0.9375rem !important;
    }

    #panel-1>div>div:last-child {
        width: 50% !important;
        order: 2 !important;
    }
}

@media (min-width: 1024px) {
    #heroText>div {
        margin-left: 0 !important;
    }
}

