     1â†’'use client';
     2â†’
     3â†’import { useEffect, useRef, useCallback, useState } from 'react';
     4â†’
     5â†’/**
     6â†’ * HeroBackground.tsx
     7â†’ * Extracted from faceiqlabs.com HAR file (animation.js)
     8â†’ *
     9â†’ * This component implements the mouse-following parallax effect
    10â†’ * from the original FaceIQ Labs homepage.
    11â†’ *
    12â†’ * Original Logic:
    13â†’ * - Tracks pointer position within the hero container
    14â†’ * - Normalizes coordinates to -1 to 1 range
    15â†’ * - Applies linear interpolation (lerp) for smooth transitions
    16â†’ * - Applies transforms to background elements
    17â†’ */
    18â†’
    19â†’interface PointerPosition {
    20â†’  x: number;
    21â†’  y: number;
    22â†’}
    23â†’
    24â†’interface HeroBackgroundProps {
    25â†’  children?: React.ReactNode;
    26â†’  className?: string;
    27â†’}
    28â†’
    29â†’// Linear interpolation helper - exact logic from original
    30â†’const lerp = (current: number, target: number, factor: number): number => {
    31â†’  return current + (target - current) * factor;
    32â†’};
    33â†’
    34â†’export default function HeroBackground({ children, className = '' }: HeroBackgroundProps) {
    35â†’  const containerRef = useRef<HTMLDivElement>(null);
    36â†’  const backgroundRef = useRef<HTMLDivElement>(null);
    37â†’  const animationRef = useRef<number | null>(null);
    38â†’
    39â†’  // Pointer position state - matches original variable names
    40â†’  const pointerTarget = useRef<PointerPosition>({ x: 0, y: 0 });
    41â†’  const pointer = useRef<PointerPosition>({ x: 0, y: 0 });
    42â†’
    43â†’  // Track if animation should run
    44â†’  const [isVisible, setIsVisible] = useState(true);
    45â†’  const [isSectionVisible, setIsSectionVisible] = useState(true);
    46â†’
    47â†’  /**
    48â†’   * Pointer move handler
    49â†’   * Exact logic from original initPointerHandlers():
    50â†’   *
    51â†’   * const nx = ((e.clientX - rect.left) / rect.width) * 2 - 1;
    52â†’   * const ny = ((e.clientY - rect.top) / rect.height) * 2 - 1;
    53â†’   * pointerTarget.x = Math.max(-1, Math.min(1, nx));
    54â†’   * pointerTarget.y = Math.max(-1, Math.min(1, ny));
    55â†’   */
    56â†’  const handlePointerMove = useCallback((e: PointerEvent) => {
    57â†’    const container = containerRef.current;
    58â†’    if (!container) return;
    59â†’
    60â†’    const rect = container.getBoundingClientRect();
    61â†’
    62â†’    // Normalize to -1 to 1 range (original math)
    63â†’    const nx = ((e.clientX - rect.left) / rect.width) * 2 - 1;
    64â†’    const ny = ((e.clientY - rect.top) / rect.height) * 2 - 1;
    65â†’
    66â†’    // Clamp values (original logic)
    67â†’    pointerTarget.current.x = Math.max(-1, Math.min(1, nx));
    68â†’    pointerTarget.current.y = Math.max(-1, Math.min(1, ny));
    69â†’  }, []);
    70â†’
    71â†’  /**
    72â†’   * Pointer leave handler
    73â†’   * Resets target to center when mouse leaves
    74â†’   */
    75â†’  const handlePointerLeave = useCallback(() => {
    76â†’    pointerTarget.current.x = 0;
    77â†’    pointerTarget.current.y = 0;
    78â†’  }, []);
    79â†’
    80â†’  /**
    81â†’   * Animation loop
    82â†’   * Exact lerp factor from original: 0.08
    83â†’   *
    84â†’   * Original logic:
    85â†’   * pointer.x += (pointerTarget.x - pointer.x) * 0.08;
    86â†’   * pointer.y += (pointerTarget.y - pointer.y) * 0.08;
    87â†’   */
    88â†’  const animate = useCallback(() => {
    89â†’    if (!isVisible || !isSectionVisible) {
    90â†’      animationRef.current = requestAnimationFrame(animate);
    91â†’      return;
    92â†’    }
    93â†’
    94â†’    // Apply lerp smoothing (exact factor from original: 0.08)
    95â†’    pointer.current.x = lerp(pointer.current.x, pointerTarget.current.x, 0.08);
    96â†’    pointer.current.y = lerp(pointer.current.y, pointerTarget.current.y, 0.08);
    97â†’
    98â†’    // Apply transforms to background element
    99â†’    // Original applied to headModel and roomGroup with these multipliers:
   100â†’    // headModel: x * 0.1, y * 0.03
   101â†’    // roomGroup: x * 0.08, y * 0.04
   102â†’    // Camera sway: x * baseZ * 0.02-0.06
   103â†’    if (backgroundRef.current) {
   104â†’      const isMobile = window.innerWidth <= 800;
   105â†’
   106â†’      // Rotation multipliers (from original)
   107â†’      const rotateX = pointer.current.y * (isMobile ? 2 : 3); // degrees
   108â†’      const rotateY = pointer.current.x * (isMobile ? 5 : 8); // degrees
   109â†’
   110â†’      // Translation multipliers (from original camera sway)
   111â†’      const translateX = pointer.current.x * (isMobile ? 10 : 20); // pixels
   112â†’      const translateY = pointer.current.y * (isMobile ? 5 : 15); // pixels
   113â†’
   114â†’      backgroundRef.current.style.transform = `
   115â†’        perspective(1000px)
   116â†’        translate(${translateX}px, ${translateY}px)
   117â†’        rotateX(${-rotateX}deg)
   118â†’        rotateY(${rotateY}deg)
   119â†’      `;
   120â†’    }
   121â†’
   122â†’    animationRef.current = requestAnimationFrame(animate);
   123â†’  }, [isVisible, isSectionVisible]);
   124â†’
   125â†’  // Set up event listeners and animation loop
   126â†’  useEffect(() => {
   127â†’    // Visibility change handler (from original)
   128â†’    const handleVisibilityChange = () => {
   129â†’      setIsVisible(!document.hidden);
   130â†’    };
   131â†’
   132â†’    // Intersection observer for section visibility (from original initSectionObserver)
   133â†’    const sectionObserver = new IntersectionObserver(
   134â†’      (entries) => {
   135â†’        for (const entry of entries) {
   136â†’          setIsSectionVisible(entry.isIntersecting);
   137â†’        }
   138â†’      },
   139â†’      { threshold: 0.1 }
   140â†’    );
   141â†’
   142â†’    // Add event listeners
   143â†’    window.addEventListener('pointermove', handlePointerMove);
   144â†’    window.addEventListener('pointerleave', handlePointerLeave);
   145â†’    document.addEventListener('visibilitychange', handleVisibilityChange);
   146â†’
   147â†’    // Observe this section
   148â†’    if (containerRef.current) {
   149â†’      sectionObserver.observe(containerRef.current);
   150â†’    }
   151â†’
   152â†’    // Start animation loop
   153â†’    animationRef.current = requestAnimationFrame(animate);
   154â†’
   155â†’    // Cleanup
   156â†’    return () => {
   157â†’      window.removeEventListener('pointermove', handlePointerMove);
   158â†’      window.removeEventListener('pointerleave', handlePointerLeave);
   159â†’      document.removeEventListener('visibilitychange', handleVisibilityChange);
   160â†’      sectionObserver.disconnect();
   161â†’
   162â†’      if (animationRef.current) {
   163â†’        cancelAnimationFrame(animationRef.current);
   164â†’      }
   165â†’    };
   166â†’  }, [handlePointerMove, handlePointerLeave, animate]);
   167â†’
   168â†’  return (
   169â†’    <div
   170â†’      ref={containerRef}
   171â†’      className={`hero-background-container relative overflow-hidden ${className}`}
   172â†’      style={{ minHeight: 'max(100vh, 720px)' }}
   173â†’    >
   174â†’      {/* Animated background layer */}
   175â†’      <div
   176â†’        ref={backgroundRef}
