/**
 * HeroAnimations.css
 * Extracted from faceiqlabs.com HAR file
 *
 * IMPORTANT FINDING: The original site does NOT use a gradient shimmer animation
 * on the "Harmony" text. Instead, it uses:
 * 1. A simple blue color (text-blue-400 / #60a5fa)
 * 2. A rotating word animation with fade/slide transitions
 */

/* =============================================
   ROTATING TEXT ANIMATION
   The h1 cycles through words: proportions, symmetry,
   harmony, ratios, measurements, balance
   ============================================= */

.rotating-text {
  display: inline-block;
  color: #60a5fa; /* Tailwind text-blue-400 */
  min-width: 280px;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.rotating-text.fade-out {
  opacity: 0;
  transform: translateY(10px);
}

.rotating-text.fade-in {
  opacity: 1;
  transform: translateY(0);
}

/* =============================================
   FADE-IN ANIMATION (Intersection Observer)
   Applied to sections as they scroll into view
   ============================================= */

.fade-in {
  opacity: 0;
  transform: translateY(20px);
  transition: opacity 0.6s ease, transform 0.6s ease;
}

.fade-in.visible {
  opacity: 1;
  transform: translateY(0);
}

/* =============================================
   HERO SECTION CONTAINER
   ============================================= */

.hero-section {
  position: relative;
  overflow: hidden;
  min-height: max(100vh, 720px);
  height: 100vh;
  background: #0d1117;
}

/* =============================================
   3D HEAD CONTAINER
   The head3d element hosts the Three.js canvas
   ============================================= */

.head3d-container {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  min-height: max(100vh, 720px);
  z-index: 0;
  pointer-events: none;
  transform: translate3d(0, 0, 0);
  will-change: transform;
  contain: layout style paint;
}

.head3d-container > canvas {
  position: absolute !important;
  inset: 0 !important;
  width: 100% !important;
  height: 100% !important;
  z-index: 0;
  display: block;
  transform: translate3d(0, 0, 0);
  will-change: transform;
  backface-visibility: hidden;
}

/* =============================================
   VIGNETTE OVERLAY
   Creates the gradient fade effect around the 3D head
   ============================================= */

.vignette-overlay {
  position: absolute !important;
  top: 0 !important;
  bottom: 0 !important;
  left: 0 !important;
  width: 100% !important;
  pointer-events: none !important;
  z-index: 2 !important;
  opacity: 0.8 !important;
  transform: translate3d(0, 0, 0);
  will-change: opacity;
  background:
    linear-gradient(to right, rgba(0, 0, 0, 0.65) 0%, rgba(0, 0, 0, 0.5) 20%, rgba(0, 0, 0, 0.35) 35%, transparent 55%),
    linear-gradient(to top, #0d1117 0%, transparent 40%),
    linear-gradient(to bottom, rgba(0, 0, 0, 0.2) 0%, transparent 30%),
    radial-gradient(ellipse at 55% 50%, transparent 35%, rgba(0, 0, 0, 0.18) 60%, rgba(0, 0, 0, 0.35) 82%, rgba(0, 0, 0, 0.5) 100%);
}

/* =============================================
   RESPONSIVE STYLES
   ============================================= */

/* Small mobile (< 400px) */
@media (max-width: 400px) {
  .hero-section {
    height: 100vh !important;
    min-height: max(100vh, 720px) !important;
  }

  .head3d-container {
    width: 100vw !important;
    height: 100vh !important;
    min-height: max(100vh, 720px) !important;
    left: -1.5rem !important;
  }

  .vignette-overlay {
    opacity: 0.6 !important;
  }

  .hero-text {
    position: absolute !important;
    top: 2rem !important;
    left: 0;
    right: 0;
    padding-left: 1.25rem !important;
    padding-right: 1.25rem !important;
    z-index: 30;
  }

  .hero-text h1 {
    font-size: 1.75rem !important;
    line-height: 1.2 !important;
    margin-bottom: 0.75rem !important;
  }

  .hero-text p {
    font-size: 0.875rem !important;
    margin-bottom: 1rem !important;
  }
}

/* Larger mobile (401px - 799px) */
@media (min-width: 401px) and (max-width: 799px) {
  .hero-section {
    height: 100vh !important;
    min-height: max(100vh, 720px) !important;
  }

  .head3d-container {
    width: 100vw !important;
    height: 100vh !important;
    min-height: max(100vh, 720px) !important;
    left: -1.5rem !important;
  }

  .vignette-overlay {
    opacity: 0.6 !important;
  }

  .hero-text {
    position: absolute !important;
    top: 2rem !important;
    left: 0;
    right: 0;
    padding-left: 0 !important;
    padding-right: 0 !important;
    z-index: 30;
  }

  .hero-text h1 {
    font-size: 2.25rem !important;
    line-height: 1.2 !important;
    margin-bottom: 1rem !important;
  }

  .hero-text p {
    font-size: 1rem !important;
    margin-bottom: 1.25rem !important;
  }
}

/* Tablet (800px - 1023px) */
@media (min-width: 800px) and (max-width: 1023px) {
  .hero-text {
    width: 50% !important;
    position: relative !important;
    padding-top: 2rem !important;
  }

  .hero-text h1 {
    font-size: 2rem !important;
    line-height: 1.2 !important;
    margin-bottom: 0.875rem !important;
  }

  .hero-text p {
    font-size: 0.9375rem !important;
    margin-bottom: 1.25rem !important;
  }
}

