          <div className="absolute inset-[15%] rounded-[40%_40%_35%_35%] bg-gradient-to-br from-blue-400/5 via-transparent to-transparent" />

          {/* Facial features overlay - subtle lines */}
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="w-full h-full relative">
              {/* Eye line */}
              <div className="absolute top-[38%] left-[25%] right-[25%] h-[1px] bg-gradient-to-r from-transparent via-blue-400/20 to-transparent" />
              {/* Nose line */}
              <div className="absolute top-[45%] left-1/2 w-[1px] h-[15%] bg-gradient-to-b from-blue-400/10 via-blue-400/20 to-transparent -translate-x-1/2" />
              {/* Mouth line */}
              <div className="absolute top-[65%] left-[35%] right-[35%] h-[1px] bg-gradient-to-r from-transparent via-blue-400/15 to-transparent" />
            </div>
          </div>

          {/* Measurement points */}
          <div className="absolute top-[35%] left-[30%] w-2 h-2 rounded-full bg-blue-400/50 animate-pulse" />
          <div className="absolute top-[35%] right-[30%] w-2 h-2 rounded-full bg-blue-400/50 animate-pulse" style={{ animationDelay: '0.5s' }} />
          <div className="absolute top-[50%] left-1/2 w-2 h-2 rounded-full bg-blue-400/40 -translate-x-1/2 animate-pulse" style={{ animationDelay: '1s' }} />
          <div className="absolute top-[70%] left-1/2 w-2 h-2 rounded-full bg-blue-400/40 -translate-x-1/2 animate-pulse" style={{ animationDelay: '1.5s' }} />
        </div>
      </div>
    </div>
  );
}

