function FloatingScoreCard() {
  const [yOffset, setYOffset] = useState(0);
  const [displayScore, setDisplayScore] = useState(0);

  // Animate score counting up
  useEffect(() => {
    const targetScore = 4.27;
    const duration = 2000;
    const startTime = Date.now();

    const animate = () => {
      const elapsed = Date.now() - startTime;
      const progress = Math.min(elapsed / duration, 1);
      const eased = 1 - Math.pow(1 - progress, 3);
      setDisplayScore(targetScore * eased);

      if (progress < 1) {
        requestAnimationFrame(animate);
      }
    };

    const timer = setTimeout(() => {
      requestAnimationFrame(animate);
    }, 800);

    return () => clearTimeout(timer);
  }, []);

  // Gentle floating animation
  useEffect(() => {
    let animationFrame: number;
    let startTime: number;

    const animate = (timestamp: number) => {
      if (!startTime) startTime = timestamp;
      const elapsed = (timestamp - startTime) / 1000;
      setYOffset(Math.sin(elapsed * 0.6 + 1) * 6);
      animationFrame = requestAnimationFrame(animate);
    };

    animationFrame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationFrame);
  }, []);

  // Color interpolation based on score
  const getScoreColor = (score: number) => {
    if (score < 3) return 'rgb(239, 68, 68)';
    if (score < 5) return 'rgb(247, 142, 71)';
    if (score < 7) return 'rgb(234, 179, 8)';
    return 'rgb(34, 197, 94)';
  };

  return (
    <motion.div
      initial={{ opacity: 0, x: 30 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ delay: 0.8, duration: 0.5 }}
      className="absolute bottom-[30%] right-[5%] lg:right-[10%] translate-y-1/2 pointer-events-none z-20"
    >
      <div
        className="rounded-xl bg-white/5 backdrop-blur-xl px-5 py-4 shadow-2xl border border-white/20 transition-all duration-300"
        style={{ transform: `translateY(${yOffset}px)` }}
      >
        <div className="text-[10px] uppercase tracking-wider font-semibold text-gray-300 mb-1.5">
          Overall Score
        </div>
        <div className="flex items-baseline gap-0.5">
          <span
            className="text-4xl font-bold tabular-nums transition-colors duration-300"
            style={{ color: getScoreColor(displayScore) }}
          >
            {displayScore.toFixed(2)}
          </span>
          <span className="text-xl font-medium text-gray-400">/10</span>
        </div>
      </div>
    </motion.div>
  );
}

