// ============================================================================
// 3D HEAD BACKGROUND COMPONENT
// ============================================================================
function Head3DBackground() {
  const containerRef = useRef<HTMLDivElement>(null);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  const smoothMousePos = useRef({ x: 0, y: 0 });

  const handleMouseMove = useCallback((e: MouseEvent) => {
    if (!containerRef.current) return;
    const rect = containerRef.current.getBoundingClientRect();
    const x = ((e.clientX - rect.left) / rect.width) * 2 - 1;
    const y = ((e.clientY - rect.top) / rect.height) * 2 - 1;
    setMousePos({ x: Math.max(-1, Math.min(1, x)), y: Math.max(-1, Math.min(1, y)) });
  }, []);

  useEffect(() => {
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, [handleMouseMove]);

  useEffect(() => {
    let animationFrame: number;

    const animate = () => {
      smoothMousePos.current.x += (mousePos.x - smoothMousePos.current.x) * 0.08;
      smoothMousePos.current.y += (mousePos.y - smoothMousePos.current.y) * 0.08;

      if (containerRef.current) {
        const rotateX = smoothMousePos.current.y * 3;
        const rotateY = smoothMousePos.current.x * 8;
        const translateX = smoothMousePos.current.x * 20;
        const translateY = smoothMousePos.current.y * 15;

        containerRef.current.style.transform = `
          perspective(1000px)
          translate(${translateX}px, ${translateY}px)
          rotateX(${-rotateX}deg)
          rotateY(${rotateY}deg)
        `;
      }

      animationFrame = requestAnimationFrame(animate);
    };

    animationFrame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationFrame);
  }, [mousePos]);

  return (
    <div className="absolute inset-0" style={{ width: '150vw', left: '-50vw' }}>
      {/* Vignette Overlay */}
      <div
        className="absolute inset-0 pointer-events-none z-[2]"
        style={{
          opacity: 0.8,
          background: `
            linear-gradient(to right, rgba(0, 0, 0, 0.65) 0%, rgba(0, 0, 0, 0.5) 20%, rgba(0, 0, 0, 0.35) 35%, transparent 55%),
            linear-gradient(to top, black 0%, transparent 40%),
            linear-gradient(to bottom, rgba(0, 0, 0, 0.2) 0%, transparent 30%),
            radial-gradient(ellipse at 55% 50%, transparent 35%, rgba(0, 0, 0, 0.18) 60%, rgba(0, 0, 0, 0.35) 82%, rgba(0, 0, 0, 0.5) 100%)
          `,
        }}
      />

      {/* 3D Head Container */}
      <div
        ref={containerRef}
        className="absolute inset-0 flex items-center justify-center will-change-transform"
        style={{ transformStyle: 'preserve-3d' }}
      >
        {/* Head visualization - gradient sphere effect */}
        <div className="relative w-[500px] h-[600px] lg:w-[600px] lg:h-[700px]">
          {/* Outer glow */}
          <div className="absolute inset-0 rounded-full bg-gradient-to-br from-blue-500/10 via-transparent to-purple-500/10 blur-3xl scale-150" />

          {/* Head shape gradient */}
          <div className="absolute inset-[10%] rounded-[40%_40%_35%_35%] bg-gradient-to-b from-gray-700/40 via-gray-800/50 to-gray-900/60 border border-white/5" />

          {/* Inner glow */}
          <div className="absolute inset-[15%] rounded-[40%_40%_35%_35%] bg-gradient-to-br from-blue-400/5 via-transparent to-transparent" />

          {/* Facial features overlay - subtle lines */}
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="w-full h-full relative">
              {/* Eye line */}
              <div className="absolute top-[38%] left-[25%] right-[25%] h-[1px] bg-gradient-to-r from-transparent via-blue-400/20 to-transparent" />
              {/* Nose line */}
              <div className="absolute top-[45%] left-1/2 w-[1px] h-[15%] bg-gradient-to-b from-blue-400/10 via-blue-400/20 to-transparent -translate-x-1/2" />
              {/* Mouth line */}
              <div className="absolute top-[65%] left-[35%] right-[35%] h-[1px] bg-gradient-to-r from-transparent via-blue-400/15 to-transparent" />
            </div>
          </div>

          {/* Measurement points */}
          <div className="absolute top-[35%] left-[30%] w-2 h-2 rounded-full bg-blue-400/50 animate-pulse" />
          <div className="absolute top-[35%] right-[30%] w-2 h-2 rounded-full bg-blue-400/50 animate-pulse" style={{ animationDelay: '0.5s' }} />
          <div className="absolute top-[50%] left-1/2 w-2 h-2 rounded-full bg-blue-400/40 -translate-x-1/2 animate-pulse" style={{ animationDelay: '1s' }} />
          <div className="absolute top-[70%] left-1/2 w-2 h-2 rounded-full bg-blue-400/40 -translate-x-1/2 animate-pulse" style={{ animationDelay: '1.5s' }} />
        </div>
      </div>
    </div>
  );
}

