import * as THREE from 'three';
import { headModel, orbitRadius, cameraBaseZ, cameraBaseY, cameraTargetY } from './model.js';
import { camera, composer, rendererHead, sceneHead, outlinePass, bloomPass, rgbShiftPass, roomGroup } from './scene.js';
import { overlayCtx, overlayCanvas, overlayDpr, drawWidthToHeight, drawEyeSpacing, drawFacialThirds, drawFacialDepth, drawMidfaceRatio, drawCanthalTilt, drawMouthToNose, drawJawAngle, drawGonialAngle, drawCervicalAngle, drawFacialConvexity } from './overlay.js';
import { getScoreColorRGB } from './utils.js';
import { updateAnimatedGridColors } from './grid.js';
import { getState, comments } from './ui.js';

export let pointerTarget = { x: 0, y: 0 };
export let pointer = { x: 0, y: 0 };
export let landmarks3D = null;

export function loadLandmarks() {
    return fetch('landmarks-3d-2.json')
        .then(res => res.json())
        .then(data => {
            landmarks3D = data.landmarks3D;
            console.log('Landmarks loaded:', landmarks3D);
        })
        .catch(err => console.error('Failed to load landmarks:', err));
}

let frameCount = 0;
let lastGridUpdate = 0;
let lastOverlayUpdate = 0;
let lastScoreUpdate = 0;
let isPageVisible = true;
let isSectionVisible = true;
let lastScoreValue = 0;
let lastRenderTime = 0;

const rotatingWords = ['proportions', 'symmetry', 'harmony', 'ratios', 'measurements', 'balance'];
let currentWordIndex = 0;
let lastWordChange = 0;

document.addEventListener('visibilitychange', () => {
    isPageVisible = !document.hidden;
});

export function initSectionObserver() {
    const sectionObserver = new IntersectionObserver((entries) => {
        for (const entry of entries) {
            isSectionVisible = entry.isIntersecting;
        }
    }, { threshold: 0.1 });

    const panel1 = document.getElementById('panel-1');
    if (panel1) {
        sectionObserver.observe(panel1);
    }
}

export function animate() {
    requestAnimationFrame(animate);

    if (!isPageVisible || !isSectionVisible) return;

    frameCount++;
    const nowMs = performance.now();
    const t = nowMs * 0.001;
    const state = getState();

    if (t - lastWordChange > 2.5) {
        currentWordIndex = (currentWordIndex + 1) % rotatingWords.length;
        const textEl = document.getElementById('rotatingText');
        if (textEl) {
            textEl.style.opacity = '0';
            textEl.style.transform = 'translateY(10px)';
            setTimeout(() => {
                textEl.textContent = rotatingWords[currentWordIndex];
                textEl.style.opacity = '1';
                textEl.style.transform = 'translateY(0)';
            }, 300);
        }
        lastWordChange = t;
    }

    state.currentYaw = THREE.MathUtils.lerp(state.currentYaw, state.targetYaw, 0.08);

    pointer.x += (pointerTarget.x - pointer.x) * 0.08;
    pointer.y += (pointerTarget.y - pointer.y) * 0.08;

    const isMobile = (window.innerWidth || 0) <= 800;
    const headBasePitch = isMobile ? -0.35 : -0.10;
    const headBaseYaw = isMobile ? 0.0 : 0.0;
    const roomBasePitch = isMobile ? -0.35 : -0.10;
    const roomBaseYaw = isMobile ? -0.2 : 0.0;

    if (headModel) {
        headModel.rotation.y = headBaseYaw - state.currentYaw + pointer.x * 0.1;
        headModel.rotation.x = headBasePitch + pointer.y * 0.03;
    }

    if (roomGroup) {
        roomGroup.rotation.y = roomBaseYaw + pointer.x * 0.08;
        roomGroup.rotation.x = roomBasePitch + pointer.y * 0.04;
    }

    const baseZ = cameraBaseZ || orbitRadius;
    const baseX = 0;
    const baseY = (cameraBaseY || (orbitRadius * 0.12));
    const sway = Math.max(0.02, Math.min(0.06, baseZ * 0.0004));
    const lift = Math.max(0.015, Math.min(0.05, baseZ * 0.0003));
    camera.position.set(
        baseX + pointer.x * baseZ * sway,
        baseY + pointer.y * baseZ * lift,
        baseZ
    );

    camera.lookAt(0, (cameraTargetY || 0), 0);

    const frameInterval = isMobile ? (1000 / 30) : (1000 / 60);
    if ((nowMs - lastRenderTime) < frameInterval) return;
    lastRenderTime = nowMs;

    if (typeof state.targetScore === 'number') {
        state.currentScore += (state.targetScore - state.currentScore) * 0.08;
        const scoreChanged = Math.abs(state.currentScore - lastScoreValue) > 0.01;

        const scoreColor = getScoreColorRGB(state.currentScore);
        if (scoreChanged || frameCount % 3 === 0) {
            const el = document.getElementById('scoreValue');
            if (el) {
                el.textContent = (Math.round(state.currentScore * 100) / 100).toFixed(2);
                el.style.color = scoreColor;
            }
            lastScoreValue = state.currentScore;
            lastScoreUpdate = t;
        }

        const rgbMatch = scoreColor.match(/\d+/g);
        if (rgbMatch && (frameCount % (isMobile ? 4 : 3) === 0 || t - lastGridUpdate > (isMobile ? 0.1 : 0.05))) {
            const r = parseInt(rgbMatch[0]) / 255;
            const g = parseInt(rgbMatch[1]) / 255;
            const b = parseInt(rgbMatch[2]) / 255;
            updateAnimatedGridColors(t, r, g, b);
            lastGridUpdate = t;
        }

        if (outlinePass && frameCount % (isMobile ? 4 : 2) === 0) {
            outlinePass.visibleEdgeColor.setStyle(scoreColor);
            outlinePass.edgeStrength = 2.0 + 1.5 * (0.5 + 0.5 * Math.sin(t * 1.2));
        }

        if (bloomPass && frameCount % (isMobile ? 3 : 2) === 0) {
            const targetBloom = 0.25 + Math.max(0, Math.min(1, (state.currentScore - 5) / 5)) * 0.7;
            bloomPass.strength += (targetBloom - bloomPass.strength) * 0.05;
            bloomPass.radius = 0.4 + 0.2 * (0.5 + 0.5 * Math.sin(t * 0.6));
        }

        if (rgbShiftPass && frameCount % (isMobile ? 3 : 2) === 0) {
            rgbShiftPass.uniforms['amount'].value = 0.0012 + 0.0008 * (0.5 + 0.5 * Math.sin(t * 0.75));
        }
    }

    if (composer) {
        composer.render();
    } else {
        rendererHead.render(sceneHead, camera);
    }

    if (overlayCtx && overlayCanvas && landmarks3D && (frameCount % (isMobile ? 3 : 2) === 0 || t - lastOverlayUpdate > (isMobile ? 0.15 : 0.1))) {
        const w = overlayCanvas.width / overlayDpr;
        const h = overlayCanvas.height / overlayDpr;
        const col = getScoreColorRGB(state.currentScore);
        const currentComment = comments[state.commentIndex];

        if (currentComment.key === 'fwhr') {
            drawWidthToHeight(overlayCtx, w, h, col, landmarks3D, headModel, camera);
        } else if (currentComment.key === 'midface') {
            drawMidfaceRatio(overlayCtx, w, h, col, landmarks3D, headModel, camera);
        } else if (currentComment.key === 'canthal') {
            drawCanthalTilt(overlayCtx, w, h, col, landmarks3D, headModel, camera);
        } else if (currentComment.key === 'eye') {
            drawEyeSpacing(overlayCtx, w, h, col, landmarks3D, headModel, camera);
        } else if (currentComment.key === 'mouthNose') {
            drawMouthToNose(overlayCtx, w, h, col, landmarks3D, headModel, camera);
        } else if (currentComment.key === 'jawAngle') {
            drawJawAngle(overlayCtx, w, h, col, landmarks3D, headModel, camera);
        } else if (currentComment.key === 'depthHeight') {
            drawFacialDepth(overlayCtx, w, h, col, landmarks3D, headModel, camera);
        } else if (currentComment.key === 'gonial') {
            drawGonialAngle(overlayCtx, w, h, col, landmarks3D, headModel, camera);
        } else if (currentComment.key === 'cervical') {
            drawCervicalAngle(overlayCtx, w, h, col, landmarks3D, headModel, camera);
        } else if (currentComment.key === 'convexity') {
            drawFacialConvexity(overlayCtx, w, h, col, landmarks3D, headModel, camera);
        } else if (currentComment.key === 'thirds') {
            drawFacialThirds(overlayCtx, w, h, col, landmarks3D, headModel, camera);
        }
        lastOverlayUpdate = t;
    }
}

export function initPointerHandlers() {
    window.addEventListener('pointermove', (e) => {
        const host = document.getElementById('head3d');
        const rect = host?.getBoundingClientRect();
        if (!rect) return;
        const nx = ((e.clientX - rect.left) / rect.width) * 2 - 1;
        const ny = ((e.clientY - rect.top) / rect.height) * 2 - 1;
        pointerTarget.x = Math.max(-1, Math.min(1, nx));
        pointerTarget.y = Math.max(-1, Math.min(1, ny));
    });

    window.addEventListener('pointerleave', () => {
        pointerTarget.x = 0;
        pointerTarget.y = 0;
    });
}

