import * as THREE from 'three';
import { EffectComposer } from 'three/addons/postprocessing/EffectComposer.js';
import { RenderPass } from 'three/addons/postprocessing/RenderPass.js';
import { UnrealBloomPass } from 'three/addons/postprocessing/UnrealBloomPass.js';
import { OutlinePass } from 'three/addons/postprocessing/OutlinePass.js';
import { ShaderPass } from 'three/addons/postprocessing/ShaderPass.js';
import { RGBShiftShader } from 'three/addons/shaders/RGBShiftShader.js';
import { RGBELoader } from 'three/addons/loaders/RGBELoader.js';
import { createVerticalGradientTexture } from './utils.js';

export const sceneHead = new THREE.Scene();
export let rendererHead = null;
export let camera = null;
export let composer = null;
export let bloomPass = null;
export let outlinePass = null;
export let rgbShiftPass = null;
export let envMapTexture = null;
export let roomGroup = null;
export let floorY = -30;
export let gridColorsAttr = null;
export let wallBackGridColorsAttr = null;
export let wallLeftGridColorsAttr = null;
export let wallRightGridColorsAttr = null;
export let gridGeometry = null;
export let wallBackGridGeometry = null;
export let wallLeftGridGeometry = null;
export let wallRightGridGeometry = null;
export let floorGridLines = null;
export let floorPoints = null;
export let floorPointsColorsAttr = null;
export let floorPointsGeometry = null;

export function initScene() {
    camera = new THREE.PerspectiveCamera(45, 1, 0.01, 500);

    rendererHead = new THREE.WebGLRenderer({ antialias: (window.devicePixelRatio || 1) <= 1.25, alpha: false });
    rendererHead.setClearColor(0x0d1117, 1);
    rendererHead.setPixelRatio(Math.min(window.devicePixelRatio, 1.5));
    rendererHead.outputColorSpace = THREE.SRGBColorSpace;
    rendererHead.toneMapping = THREE.ACESFilmicToneMapping;
    rendererHead.toneMappingExposure = 1.05;
    rendererHead.shadowMap.enabled = false;
    rendererHead.shadowMap.type = THREE.PCFSoftShadowMap;
    rendererHead.shadowMap.autoUpdate = true;

    const canvasHead = rendererHead.domElement;
    canvasHead.style.display = 'block';
    const host = document.getElementById('head3d');
    const rect = host?.getBoundingClientRect();
    const w = Math.round(rect?.width || window.innerWidth * 0.5);
    const h = Math.round(rect?.height || window.innerHeight);
    rendererHead.setSize(w, h, false);
    camera.aspect = w / h;
    camera.updateProjectionMatrix();
    host.appendChild(canvasHead);

    composer = new EffectComposer(rendererHead);
    composer.setSize(w, h);
    composer.addPass(new RenderPass(sceneHead, camera));

    outlinePass = new OutlinePass(new THREE.Vector2(w, h), sceneHead, camera);
    outlinePass.edgeStrength = 1.3;
    outlinePass.edgeThickness = 1.2;
    outlinePass.pulsePeriod = 3;
    outlinePass.visibleEdgeColor.set(0xffffff);
    outlinePass.hiddenEdgeColor.set(0x000000);
    composer.addPass(outlinePass);

    bloomPass = new UnrealBloomPass(new THREE.Vector2(w, h), 0.2, 0.3, 0.88);
    composer.addPass(bloomPass);

    rgbShiftPass = new ShaderPass(RGBShiftShader);
    rgbShiftPass.uniforms['amount'].value = 0.0015;
    composer.addPass(rgbShiftPass);

    const hemiHead = new THREE.HemisphereLight(0xffffff, 0xffffff, 0.35);

    const keyHead = new THREE.DirectionalLight(0xfff1e0, 1.2);
    keyHead.position.set(10, 20, 15);
    keyHead.castShadow = true;
    keyHead.shadow.mapSize.width = 4096;
    keyHead.shadow.mapSize.height = 4096;
    keyHead.shadow.camera.near = 1;
    keyHead.shadow.camera.far = 300;
    keyHead.shadow.camera.left = -100;
    keyHead.shadow.camera.right = 100;
    keyHead.shadow.camera.top = 100;
    keyHead.shadow.camera.bottom = -100;
    keyHead.shadow.bias = -0.001;
    keyHead.shadow.radius = 1.5;

    const fillHead = new THREE.DirectionalLight(0xe8f0ff, 0.5);
    fillHead.position.set(-5, 3, 5);

    const rimHead = new THREE.DirectionalLight(0xcfe8ff, 0.8);
    rimHead.position.set(2, 3, -5);

    roomGroup = new THREE.Group();

    const floorGeometry = new THREE.PlaneGeometry(400, 400);
    const floorMaterial = new THREE.MeshStandardMaterial({
        color: 0x19212c,
        roughness: 1.0,
        metalness: 0.0,
        shadowSide: THREE.FrontSide
    });
    const floor = new THREE.Mesh(floorGeometry, floorMaterial);
    floor.rotation.x = -Math.PI / 2;
    floor.position.y = -30;
    floorY = floor.position.y;
    floor.receiveShadow = true;
    floor.castShadow = false;

    const gridSize = 400;
    const gridDivisions = 20;
    const gridPoints = [];
    const step = gridSize / gridDivisions;
    const halfSize = gridSize / 2;

    for (let i = 0; i <= gridDivisions; i++) {
        const pos = -halfSize + i * step;
        gridPoints.push(-halfSize, 0, pos, halfSize, 0, pos);
        gridPoints.push(pos, 0, -halfSize, pos, 0, halfSize);
    }

    gridGeometry = new THREE.BufferGeometry();
    gridGeometry.setAttribute('position', new THREE.Float32BufferAttribute(gridPoints, 3));
    const gridMaterial = new THREE.LineBasicMaterial({
        color: 0xffffff,
        transparent: true,
        opacity: 0.3,
        linewidth: 1,
        vertexColors: true,
        blending: THREE.AdditiveBlending,
        depthWrite: false,
        depthTest: true
    });
    gridColorsAttr = new THREE.Float32BufferAttribute(new Float32Array(gridPoints.length), 3);
    gridGeometry.setAttribute('color', gridColorsAttr);
    const gridLines = new THREE.LineSegments(gridGeometry, gridMaterial);
    gridLines.position.y = -29.9;
    floorGridLines = gridLines;

    // Floor intersection points at each grid crossing
    const intersections = [];
    for (let ix = 0; ix <= gridDivisions; ix++) {
        const x = -halfSize + ix * step;
        for (let iz = 0; iz <= gridDivisions; iz++) {
            const z = -halfSize + iz * step;
            intersections.push(x, -29.85, z);
        }
    }
    floorPointsGeometry = new THREE.BufferGeometry();
    floorPointsGeometry.setAttribute('position', new THREE.Float32BufferAttribute(new Float32Array(intersections), 3));
    floorPointsColorsAttr = new THREE.Float32BufferAttribute(new Float32Array(intersections.length), 3);
    floorPointsGeometry.setAttribute('color', floorPointsColorsAttr);
    const floorPointsMat = new THREE.PointsMaterial({
        size: 2.0,
        sizeAttenuation: true,
        transparent: true,
        opacity: 0.9,
        vertexColors: true,
        blending: THREE.AdditiveBlending,
        depthWrite: false,
        depthTest: true
    });
    floorPoints = new THREE.Points(floorPointsGeometry, floorPointsMat);

    const wallBackGridPoints = [];
    for (let i = 0; i <= gridDivisions; i++) {
        const pos = -halfSize + i * step;
        wallBackGridPoints.push(-halfSize, pos, 0, halfSize, pos, 0);
        wallBackGridPoints.push(pos, -halfSize, 0, pos, halfSize, 0);
    }
    wallBackGridGeometry = new THREE.BufferGeometry();
    wallBackGridGeometry.setAttribute('position', new THREE.Float32BufferAttribute(wallBackGridPoints, 3));
    const wallBackGridMaterial = new THREE.LineBasicMaterial({
        color: 0xffffff,
        transparent: true,
        opacity: 0.22,
        linewidth: 1,
        vertexColors: true,
        blending: THREE.AdditiveBlending,
        depthWrite: false,
        depthTest: true
    });
    wallBackGridColorsAttr = new THREE.Float32BufferAttribute(new Float32Array(wallBackGridPoints.length), 3);
    wallBackGridGeometry.setAttribute('color', wallBackGridColorsAttr);
    const wallBackGrid = new THREE.LineSegments(wallBackGridGeometry, wallBackGridMaterial);
    wallBackGrid.position.set(0, 170, -199.9);

    const wallLeftGridPoints = [];
    for (let i = 0; i <= gridDivisions; i++) {
        const pos = -halfSize + i * step;
        wallLeftGridPoints.push(0, pos, -halfSize, 0, pos, halfSize);
        wallLeftGridPoints.push(0, -halfSize, pos, 0, halfSize, pos);
    }
    wallLeftGridGeometry = new THREE.BufferGeometry();
    wallLeftGridGeometry.setAttribute('position', new THREE.Float32BufferAttribute(wallLeftGridPoints, 3));
    const wallLeftGridMaterial = new THREE.LineBasicMaterial({
        color: 0xffffff,
        transparent: true,
        opacity: 0.22,
        linewidth: 1,
        vertexColors: true,
        blending: THREE.AdditiveBlending,
        depthWrite: false,
        depthTest: true
    });
    wallLeftGridColorsAttr = new THREE.Float32BufferAttribute(new Float32Array(wallLeftGridPoints.length), 3);
    wallLeftGridGeometry.setAttribute('color', wallLeftGridColorsAttr);
    const wallLeftGrid = new THREE.LineSegments(wallLeftGridGeometry, wallLeftGridMaterial);
    wallLeftGrid.position.set(-199.9, 170, 0);

    const wallRightGridPoints = [];
    for (let i = 0; i <= gridDivisions; i++) {
        const pos = -halfSize + i * step;
        wallRightGridPoints.push(0, pos, -halfSize, 0, pos, halfSize);
        wallRightGridPoints.push(0, -halfSize, pos, 0, halfSize, pos);
    }
    wallRightGridGeometry = new THREE.BufferGeometry();
    wallRightGridGeometry.setAttribute('position', new THREE.Float32BufferAttribute(wallRightGridPoints, 3));
    const wallRightGridMaterial = new THREE.LineBasicMaterial({
        color: 0xffffff,
        transparent: true,
        opacity: 0.22,
        linewidth: 1,
        vertexColors: true,
        blending: THREE.AdditiveBlending,
        depthWrite: false,
        depthTest: true
    });
    wallRightGridColorsAttr = new THREE.Float32BufferAttribute(new Float32Array(wallRightGridPoints.length), 3);
    wallRightGridGeometry.setAttribute('color', wallRightGridColorsAttr);
    const wallRightGrid = new THREE.LineSegments(wallRightGridGeometry, wallRightGridMaterial);
    wallRightGrid.position.set(199.9, 170, 0);

    const wallBackGeometry = new THREE.PlaneGeometry(400, 400);
    const wallBackMaterial = new THREE.MeshStandardMaterial({
        color: 0xffffff,
        roughness: 1.0,
        metalness: 0.0
    });
    wallBackMaterial.envMapIntensity = 0.0;
    const wallBack = new THREE.Mesh(wallBackGeometry, wallBackMaterial);
    wallBackMaterial.map = createVerticalGradientTexture('#2a344a', '#0f141d');
    wallBackMaterial.needsUpdate = true;
    wallBack.position.set(0, 170, -200);
    wallBack.receiveShadow = true;
    wallBack.castShadow = true;

    const wallLeftGeometry = new THREE.PlaneGeometry(400, 400);
    const wallLeftMaterial = new THREE.MeshStandardMaterial({
        color: 0xffffff,
        roughness: 1.0,
        metalness: 0.0
    });
    wallLeftMaterial.envMapIntensity = 0.0;
    const wallLeft = new THREE.Mesh(wallLeftGeometry, wallLeftMaterial);
    wallLeftMaterial.map = createVerticalGradientTexture('#253145', '#0d121a');
    wallLeftMaterial.needsUpdate = true;
    wallLeft.rotation.y = Math.PI / 2;
    wallLeft.position.set(-200, 170, 0);
    wallLeft.receiveShadow = true;
    wallLeft.castShadow = true;

    const wallRightGeometry = new THREE.PlaneGeometry(400, 400);
    const wallRightMaterial = new THREE.MeshStandardMaterial({
        color: 0xffffff,
        roughness: 1.0,
        metalness: 0.0
    });
    wallRightMaterial.envMapIntensity = 0.0;
    const wallRight = new THREE.Mesh(wallRightGeometry, wallRightMaterial);
    wallRightMaterial.map = createVerticalGradientTexture('#253145', '#0d121a');
    wallRightMaterial.needsUpdate = true;
    wallRight.rotation.y = -Math.PI / 2;
    wallRight.position.set(200, 170, 0);
    wallRight.receiveShadow = true;
    wallRight.castShadow = true;

    roomGroup.add(floor, gridLines, floorPoints, wallBack, wallBackGrid, wallLeft, wallLeftGrid, wallRight, wallRightGrid);
    roomGroup.rotation.y = 0;

    sceneHead.add(hemiHead, keyHead, fillHead, rimHead, roomGroup);
}

export function syncRendererSize() {
    const host = document.getElementById('head3d');
    const rect = host?.getBoundingClientRect();
    const w = Math.max(1, Math.round(rect?.width || window.innerWidth * 0.5));
    const h = Math.max(1, Math.round(rect?.height || window.innerHeight));
    const pr = Math.min(window.devicePixelRatio || 1, 1.5);
    rendererHead.setPixelRatio(pr);
    rendererHead.setSize(w, h, false);
    if (composer) {
        composer.setPixelRatio(pr);
        composer.setSize(w, h);
    }
    if (bloomPass) bloomPass.setSize(w, h);
    if (outlinePass) outlinePass.setSize(w, h);
    camera.aspect = w / h;
    camera.updateProjectionMatrix();
}

export function loadEnvironmentMap() {
    const pmremGenerator = new THREE.PMREMGenerator(rendererHead);
    pmremGenerator.compileEquirectangularShader();
    new RGBELoader().load(
        'https://assets.pmnd.rs/hdris/venice_sunset_1k.hdr',
        (hdrTex) => {
            envMapTexture = pmremGenerator.fromEquirectangular(hdrTex).texture;
            hdrTex.dispose();
            pmremGenerator.dispose();
        },
        undefined,
        (err) => {
            console.warn('HDRI failed to load, continuing without environment.', err);
        }
    );
    return envMapTexture;
}

