     8â†’export let cameraBaseZ = 0;
     9â†’export let cameraBaseY = 0;
    10â†’export let cameraTargetY = 0;
    11â†’let projectionLines = null;
    12â†’let projectionGeom = null;
    13â†’let projectionSamples = [];
    14â†’let floorPointsGeom = null;
    15â†’let floorPoints = null;
    16â†’
    17â†’function initProjectionLines(root) {
    18â†’    projectionSamples = [];
    19â†’    root.traverse((obj) => {
    20â†’        if (!obj.isMesh || !obj.geometry || !obj.geometry.attributes || !obj.geometry.attributes.position) return;
    21â†’        const posAttr = obj.geometry.attributes.position;
    22â†’        const count = posAttr.count;
    23â†’        const stride = Math.max(10, Math.floor(count / 500));
    24â†’        for (let i = 0; i < count; i += stride) {
    25â†’            const lx = posAttr.getX(i);
    26â†’            const ly = posAttr.getY(i);
    27â†’            const lz = posAttr.getZ(i);
    28â†’            projectionSamples.push({ mesh: obj, local: new THREE.Vector3(lx, ly, lz) });
    29â†’        }
    30â†’    });
    31â†’    const maxLines = projectionSamples.length;
    32â†’    projectionGeom = new THREE.BufferGeometry();
    33â†’    projectionGeom.setAttribute('position', new THREE.Float32BufferAttribute(new Float32Array(maxLines * 2 * 3), 3));
    34â†’    const material = new THREE.LineBasicMaterial({
    35â†’        color: 0xffffff,
    36â†’        transparent: true,
